	eval( fncName + "()");
}


function grd_list_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex) {

	var arg = "ztplno  = "  + quote(ds_list.getcolumn(ds_list.row, "ZTPLNO" ));
	    arg = arg +  " ztplgbn = "  + quote(ds_list.getcolumn(ds_list.row, "ZTPLGBN"));
	    arg = arg +  " zprdgbn = "  + quote(ds_list.getcolumn(ds_list.row, "ZPRDGBN"));
	    arg = arg +  " ztplnm  = "  + quote(ds_list.getcolumn(ds_list.row, "ZTPLNM" ));
	    arg = arg +  " brndcd  = "  + quote(brndcd);
	    arg = arg +  " brndseq = "  + quote(brndseq);
	    arg = arg +  " qtnum   = "  + quote(qtnum);
    gfn_openPopUpUrlHdel("SES::Ses0583.xml", true, arg);
}

