function after_CopySes0583(ds_temp_copy) {
	// 제거로직 수행
	// 브랜드, 기종, 용도, 인승, 용량, 열림, 속도, 층수, 정지층수 복사 금지
	var notCodeArray;
	var QnotCodeArray = ["EL_ABRAND", "EL_ATYP", "EL_AUSE", "EL_AMAN", "EL_ACAPA", "EL_AOPEN", "EL_ASPD", "EL_AFQ", "EL_ASTQ"];
	var PnotCodeArray = ["EL_ABRAND", "EL_ATYP"];
	var searchRow = 0;

    ds_temp.ClearData();
	if(mode=='Q') {
	    // 견적서 작성 복사 제외
	    notCodeArray = QnotCodeArray;
	} else {
	    // 계약변경 복사 제외
	    notCodeArray = PnotCodeArray;
	}

	for(var i=0; i < Length(notCodeArray); i++) {
		searchRow = ds_temp_copy.FindRow("PRH", notCodeArray[i]);
		if(searchRow > -1) {
			ds_temp_copy.DeleteRow(searchRow);
		}
	}

    // 공통사양은 대상에서 제외
    // 2020 브랜드
    // 복사대상 영업사양 특성 존재하고 출력여부, 수정여부 가능시 복사 대상 추가
	var ds_List = [parent.ds_template1, parent.ds_template2, parent.ds_template3, parent.ds_template4, parent.ds_template5, parent.ds_template6];
    var disptp = "";
	var moditp = "";
	var prh    = "";
	var prd    = "";
	var newRow = "";
    for (var iObj = 0; iObj < Length(ds_List); iObj++) {
        for ( var i = 0 ; i < ds_List[iObj].GetRowCount(); i++ ) {
            prh = ds_List[iObj].GetColumn(i, "PRH");
            disptp = ds_List[iObj].GetColumn(i, "DISPTP");
            moditp = ds_List[iObj].GetColumn(i, "MODITP");
            searchRow = ds_temp_copy.FindRow("PRH", prh);
            if(searchRow >-1) {
                if(disptp == "X" && moditp == "X") {
                    prd = ds_temp_copy.GetColumn(searchRow, "PRD");
			        newRow = ds_temp.addRow();
			        ds_temp.CopyRow(newRow, ds_List[iObj], i);
                    ds_temp.SetColumn(newRow, "PRD", prd);
                    //trace("qtseq:"+ds_List[iObj].GetColumnNF(i, "QTSEQ")+", prh:"+prh+", searchRow:"+searchRow+", disptp:"+disptp+", moditp:"+moditp+", prd:"+prd);
                }
            }
        }
    }

	//ds_temp.copy(ds_temp_copy);
	fn_afterQuery("queryTemp");
}
