/********************************************************************************
* 기      능   : 조회 1
********************************************************************************/
function fn_query() {

// Message 영역 초기화
	global.FRM_BOTTOM.img_msgType.imageId = "";
	global.FRM_BOTTOM.st_msg.value = "";

	// 필수 입력 항목 체크
	if ( gfn_isNull(cal_qtdat_fm, true) ) {
		gfn_showMsg("CW00001","견적등록기간");
		return;
	}
	if ( gfn_isNull(cal_qtdat_to, true) ) {
		gfn_showMsg("CW00001","견적등록기간");
		return;
	}


	if ( !gfn_checkDate( cal_qtdat_fm.value, cal_qtdat_to.value, true, cal_qtdat_to) ) {
		return;
	}

	tit_clearActionInfo();
	tit_addSearchActionInfo("mm05:MM0507001A_S1");
	ds_cond.setColumn(0, "QTDAT_FM", cal_qtdat_fm.Value);
	ds_cond.setColumn(0, "QTDAT_TO", cal_qtdat_to.Value);
	ds_cond.setColumn(0, "QTNUM", ed_qtnum.Value);

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , true);


}

