/********************************************************************************
* 기      능   : 조회 Callback
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {

   	if ( ds_list.rowCount() > 0 ) {
		gfn_showMsg("CI00002", ds_list.rowCount() + "");

	} else {
		gfn_showMsg("CW00010");
	}

	gfn_copyDs(ds_list, ds_list2);
}

/********************************************************************************
* 기      능   : 저장
********************************************************************************/
function fn_save() {
	for (var i = 0; i < ds_list.rowCount(); i++) {
		if (ds_list.GetColumn(i, "CHECK") == 1) {
			//trace('org : ' + ds_list.GetColumn(i, "POSID_ORG"));

			ds_list.SetColumn(i, "POSID", ds_list.GetColumn(i, "POSID_ORG"));

		}
	}

	ds_list2.ClearData();
	trace('row count before clear : ' + ds_list2.rowCount());
	ds_list.Filter("CHECK==1");
	ds_list2.copyF(ds_list);
	ds_list.unFilter();

	trace('row count after copy : ' + ds_list2.rowCount());

	tit_clearActionInfo();
	tit_addMultiActionInfo("mm05:MM0505001B_U1");
	tit_callService(
		""
		, ""
		, "ds_list3=ds_list2"
		, ""
		, ""
		, "fn_afterSave"
		, true);
}

/********************************************************************************
* 기      능   : 저장 Callback
********************************************************************************/
function fn_afterSave(errCode, errMsg) {	

	gfn_showMsg("CI00003");

	fn_query();
}

/********************************************************************************
* 기      능   : 삭제 Callback
********************************************************************************/
function fn_afterDelete(errCode, errMsg) {

	if ( errCode != 0 )
	{
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");
	}

	ds_list.fireevent = true;

	fn_query();
}

