				+ " MESGTITL="  + quote( mesgtitl )
				+ " MESGBODY="  + quote( mesgbody )
			, ""
			, false
			, true);
	}

	// 구매운영팀장 메일 발송 
	for (var i = 0; i < ds_mail.rowCount(); i++) {	
		mesgtitl2 = "고철 견적 요청서 - " + ds_mail.getColumn(i, "GSNAM") ;
		mesgbody2 = " - 제목 : 고철 견적 요청서 - " + ds_mail.getColumn(i, "GSNAM") + "\r\n" + " - 요청자 : " + 
			G_USER_NAME+" / "+G_VEND_NAME+ "\r\n" + " - 기안일 : "+ gfn_formatDate(getDate(), '-');

		//rece_user2 = "youngsuchoi@hdel.co.kr";
		rece_user2 = "junghwan.park@hdel.co.kr"; //박정환 구매운영 팀장

		tit_callService(
			"COMM_ACT"
