/********************************************************************************
* 기      능   : 고철처리 저장 Callback
********************************************************************************/
function fn_MeltSave(errCode, errMsg) {		

	//보내는 사람
	send_user = 'helco' + G_USER_ID +  '@hdel.co.kr';	

	// 최초 등록자에게 메일 발송	
	for (var i = 0; i < ds_mail.rowCount(); i++) {

		mesgtitl = "리모델링 현장 고철 처리 유형";		

		var metGb;
		if(ds_mail.getColumn(i, "METGUBUN") == "1")
			metGb = "1안";
		else if(ds_mail.getColumn(i, "METGUBUN") == "2")
			metGb = "2안";
		else if(ds_mail.getColumn(i, "METGUBUN") == "3")
			metGb = "3안";		
		else
			metGb = "기타";		

		mesgbody = "- "+ds_mail.getColumn(i, "GSNAM") + " 현장 고철 처리 유형 : "+metGb+ "\r\n" ;			
		mesgbody += "\r\n" ;
		mesgbody += "<고철처리방식 유형>" + "\r\n" + " - 1안 : 고객이 직접 처리"+ "\r\n" + " - 2안 : 당사가 처리하고 계약금액에서 상계"+ "\r\n" +	" - 3안 : 당사가 처리하고 감액 계약"+ "\r\n" ;

		rece_user = ds_mail.getColumn(i, "REGMAIL");

		tit_callService(
			"COMM_ACT"
			, "sendmail"
			, ""
			, ""
			," SEND_USER=" + quote( send_user )
				+ " RECE_USER=" + quote( rece_user )
				+ " MESGTITL="  + quote( mesgtitl )
				+ " MESGBODY="  + quote( mesgbody )
			, ""
			, false
			, true);
	}

	pop_meg = '최초 등록자께 메일이 발송되었습니다';		
	gfn_showInform("CW10025", pop_meg);		
	gfn_showMsg("CI00003");
	fn_query();
}


