var col = "";
function grd_list_OnExpandEdit(obj,nRow,nCell,strVal,nPivotIndex)
{	
	if(nCell = 16 || nCell = 17)	{
			var rect = obj.GetCellRect(nRow, ncell);
			cal_temp.top = rect[1] +60 ;
			cal_temp.Left = rect[0];
			cal_temp.Visible = true;

			switch( nCell )
			{
				case 16:
					cal_temp.Value = ds_list.GetColumn(nRow, "METDELDT");
					col = "METDELDT";
					break;
				case 17:
					cal_temp.Value = ds_list.GetColumn(nRow, "TAXDT");
					col = "TAXDT";
					break;
			}

		cal_temp.SetFocus();

	}	else if(nCell = 7)	{
		// 협력업체조회 팝업화면 OPEN
		var arg = "fa_lifnr= " 	+ quote("")   // 거래코드
				 + " fa_name1 = " + quote("")   // 협력업체명
				 ;
		var arr = gfn_openPopUpUrlHdel("MM12::MM1201004B.xml", true, arg );	

		if (arr != null && arr != 0)	{
			//trace("값은 "+arr[1]+"/"+arr[0]);
			//obj.value = arr[2];	// 리턴된 협력업체코드로 검색조건 SET

			ds_list.setColumn(nRow,'BIDCOP',arr[0]);	
			ds_list.setColumn(nRow,'BIDCOP_NM',arr[1]);				
		}
	}
}

function cal_temp_OnDayClick(obj,strText)
{
	gfn_clearMsgbar();

	ds_list.setColumn(ds_list.row, col, strText);

	obj.Visible = false;
}

function cal_temp_OnKillFocus(obj)
{
	obj.Visible = false;
}


function ds_list_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if ( strColumnID <> "CHECK"){

		ds_list.fireevent = false;
		var colid = strColumnID;

		if ( Trim(obj.GetColumn(nRow,colid)) != Trim(ds_list2.GetColumn(nRow,colid)))
		{
			obj.SetColumn(nRow,"CHECK","1");

		} else {
			obj.SetColumn(nRow,"CHECK","0");

		}

	ds_list.fireevent = true;

	}

}



function ufn_openPjt(obj)
{
	var arg = "fa_qtnum   = "+quote(ed_qtnum.Text);		//프로젝트번호
	trace(arg);
	//프로젝트명			+ " fa_zzpjt_nm  = "+quote(ed_qtso_nm.Text); 	
	var arr = gfn_openPopUpUrlHdel("Com::Com0250.xml", true, arg);			
	if (arr != null && arr != 0) {
		ed_qtnum.Text = arr[0];
	}	
}

