// 최초등록 버튼 클릭 이벤트
function btn_FirstSave_OnClick(obj)
{
	if(ds_list.RowCount() == 0)
	{
		gfn_showInform("CW00054", "");	
		return;
	}

	var j = 0;
	for(var i=0; i < ds_list.GetRowCount() ; i++) {				
		// 체크된 항목이 존재하는지 여부 체크
		if( grd_list.GetCellValue(i, 0) == "1")	{
			j++;
		}
	}	
	if( j == 0)	{
		gfn_showInform("CW00013", "");	
		j = 0;
		return;
	}


	ds_mail.ClearData()	;

	ds_list2.ClearData();

	ds_list.Filter("CHECK==1");
	ds_list2.copyF(ds_list);

	var j = 0;	
	for (var i = 0; i < ds_list2.rowCount(); i++) { 
		//trace('org : ' + ds_list.GetColumn(i, "POSID_ORG"));			
		ds_mail.AddRow();
		ds_mail.SetColumn(j, "SEQNO", j+1);
		ds_mail.SetColumn(j, "GSNAM", ds_list2.GetColumn(i, "GSNAM"));
		j++;		
	}





	ds_list.unFilter();

	tit_clearActionInfo();
	/*
	tit_addMultiActionInfo("mm05:MM0507001A_I1");
	tit_callService(
		""
		, ""
		, "ds_list3=ds_list2"
		, ""
		, ""
		, "fn_FirstSave"
		, true);
	*/		
	//fn_mail();


	// 저장 처리 



//   call 서비스에서 commit실행하여 commit 필요없음.

	// 저장 처리 
	tit_clearActionInfo();
	tit_addSelectExActionInfo(
		"mm05:MM0507001A_S2"
	   ,"mm05:MM0507001A_I1"
	   ,"mm05:MM0507001A_U5");

//   call 서비스에서 commit실행하여 commit 필요없음.

	tit_callService(
        ""
        , ""
        , "ds_list3=ds_list2"
        , ""
        , ""
        , "fn_FirstSave");


}

