// 낙찰정보 저장 버튼 클릭 이벤트
function btn_QuotSave_OnClick(obj)
{	
	if(ds_list.RowCount() == 0)
	{
		gfn_showInform("CW00054", "");	
		return;
	}	

	var j = 0;
	for(var i=0; i < ds_list.GetRowCount() ; i++) {				
		// 체크된 항목이 존재하는지 여부 체크
		if( grd_list.GetCellValue(i, 0) == "1")	{
			j++;
		}
	}	
	if( j == 0)	{
		gfn_showInform("CW00013", "");	
		j = 0;
		return;
	}

	for(var i=0; i < ds_list.GetRowCount() ; i++) {		

		// 체크된 항목중에서 접수일자가 존재하는지 체크
		if( grd_list.GetCellValue(i, 0) == "1" && length(trim(ds_list.GetColumn(i,3))) == 0) {										
			gfn_showAlert("CW00022","( " + (i+1) + " ) 행은 최초등록" );
			ds_list.unFilter();	
			return;
		}

		// 체크된 항목중에서 고철낙찰업체가 입력되어 있는지 여부 확인 
		if( grd_list.GetCellValue(i, 0) == "1" && length(trim(ds_list.GetColumn(i,"BIDCOP"))) == 0) {	
			gfn_showAlert("CW00022","( " + (i+1) + " ) 행은 고철낙찰업체" );
			ds_list.unFilter();	
			return;
		}

		// 체크된 항목중에서 낙찰가가 입력되어 있는지 여부 확인 
		if( grd_list.GetCellValue(i, 0) == "1" && ((length(trim(ds_list.GetColumn(i,"BIDPRC")))) == 0 ||  trim(ds_list.GetColumn(i,"BIDPRC")) = "0" ) ) {	
			gfn_showAlert("CW00022","( " + (i+1) + " ) 행은 낙찰가" );
			ds_list.unFilter();	
			return;
		}
	}



	ds_list2.ClearData();

	ds_list.Filter("CHECK==1");



	ds_list2.copyF(ds_list);
	ds_list.unFilter();	

	tit_clearActionInfo();
	tit_addMultiActionInfo("mm05:MM0507001A_U1");
	tit_callService(
		""
		, ""
		, "ds_list3=ds_list2"
		, ""
		, ""
		, "fn_afterSave"
		, true);
}

// 고철처리 저장 버튼 클릭 이벤트
