		gfn_showInform("CW00013", "");	
		j = 0;
		return;
	}

	for(var i=0; i < ds_list.GetRowCount() ; i++) {		

		// 체크된 항목중에서 접수일자가 존재하는지 체크
		if( grd_list.GetCellValue(i, 0) == "1" && length(trim(ds_list.GetColumn(i,3))) == 0) {										
			gfn_showAlert("CW00022","( " + (i+1) + " ) 행은 최초등록" );
			ds_list.unFilter();	
			return;
		}

		// 체크된 항목중에서 고철낙찰업체 입력되어 있는지 여부 확인 
		if( grd_list.GetCellValue(i, 0) == "1" && length(trim(ds_list.GetColumn(i,"BIDCOP"))) == 0) {	
			gfn_showAlert("CW00022","( " + (i+1) + " ) 행은 고철낙찰업체" );
			ds_list.unFilter();	
			return;
		}

		// 체크된 항목중에서 낙찰가가 입력되어 있는지 여부 확인 
		if( grd_list.GetCellValue(i, 0) == "1" && ((length(trim(ds_list.GetColumn(i,"BIDPRC")))) == 0 ||  trim(ds_list.GetColumn(i,"BIDPRC")) = "0" ) ) {	
			gfn_showAlert("CW00022","( " + (i+1) + " ) 행은 낙찰가" );
			ds_list.unFilter();	
			return;
		}

		// 체크된 항목중에서 고철처리유형이  입력되어 있는지 여부 확인 
		if( grd_list.GetCellValue(i, 0) == "1" && length(trim(ds_list.GetColumn(i,"METGUBUN"))) == 0  ) {	
			gfn_showAlert("CW00022","( " + (i+1) + " ) 행은 고철처리유형" );
			ds_list.unFilter();	
			return;
		}
	}





	ds_mail.ClearData()	;

	ds_list2.ClearData();

	ds_list.Filter("CHECK==1");
	ds_list2.copyF(ds_list);
	ds_list.unFilter();	

	var j = 0;	
	for (var i = 0; i < ds_list2.rowCount(); i++) { 
		//trace('org : ' + ds_list.GetColumn(i, "POSID_ORG"));			
		ds_mail.AddRow();
		ds_mail.SetColumn(j, "SEQNO", j+1);
		ds_mail.SetColumn(j, "GSNAM", ds_list2.GetColumn(i, "GSNAM"));
		ds_mail.SetColumn(j, "METGUBUN", ds_list2.GetColumn(i, "METGUBUN"));
		ds_mail.SetColumn(j, "REGMAIL", ds_list2.GetColumn(i, "REGMAIL"));		
		j++;		
	}








	tit_clearActionInfo();
	tit_addMultiActionInfo("mm05:MM0507001A_U2");
	tit_callService(
		""
		, ""
		, "ds_list3=ds_list2"
		, ""
		, ""
		, "fn_MeltSave"
		, true);
}

// 설치정보 저장 버튼 클릭 이벤트
