//=========================================================================================
// Form Loading 
//=========================================================================================
function fn_OnLoadCompleted(obj) 
{
	// 리사이징
	fn_FormResize(true);	

	// FromEditSize 	--> From text 
	// ToEditSize 		--> To text 
	div_planYn.fn_reSize('100','80');
	// 수주년월 콤보 데이타 Load 및 기본값 SETTING 
	var curYear = substr(gfn_getCurrDate(),0,4); 
	div_planYn.fn_makeYear(curYear,-5,5,0);
	div_planYn.cb_monthf.index = parseInt(substr(gfn_getCurrDate(),4,2))-1;

	// 차수 ( componentId, comboGubun, DatasetId, 전체여부 )
	fn_SetListCode(cb_chaSu, "chasu", "ds_chasu", "N");
	// 조회구분 ( componentId, comboGubun, DatasetId, 전체여부 )
	fn_SetListCode(cb_where, "whereGubun", "ds_where", "N");	

	// 제품 ( componentId, comboGubun, DatasetId, 전체여부, 그리드/콤보여부 )
	fn_SetListCode( 11, "spart", "ds_spart", "N", "grid");
	// 자재번호 ( componentId, comboGubun, DatasetId, 전체여부, 그리드/콤보여부 )
	fn_SetListCode( 13, "matnr", "ds_matnr", "N", "grid");
	// 기종 ( componentId, comboGubun, DatasetId, 전체여부, 그리드/콤보여부 )
	fn_SetListCode( 14, "gtype", "ds_gtype", "N", "grid");
	// 국가( componentId, comboGubun, DatasetId, 전체여부, 그리드/콤보여부 )
	fn_SetListCode( 21, "nation", "ds_nation", "N", "grid");
	// 설치지역 ( componentId, comboGubun, DatasetId, 전체여부, 그리드/콤보여부 )
	fn_SetListCode( 22, "region", "ds_region", "N", "grid");
	// 단납기 ( componentId, comboGubun, DatasetId, 전체여부, 그리드/콤보여부 )
	fn_SetListCode( 23, "zdeli", "ds_zdeli", "N", "grid");
	// 국내상해 ( componentId, comboGubun, DatasetId, 전체여부, 그리드/콤보여부 )
	fn_SetListCode( 24, "shangh", "ds_shangh", "N", "grid");

	// 수주가능성 ( componentId, comboGubun, DatasetId, 전체여부, 그리드/콤보여부 )
	fn_SetListCode( 31, "soable", "ds_soable", "N", "grid");
	// 수주결과  ( componentId, comboGubun, filter1, filter2, filter3, DatasetId, 전체여부, 그리드/콤보여부 )
	fn_SetListCodeZLCODE( 32, "zlcode", G_LANG, "SD111", "", "ds_sorlt", "N", "grid");

}


