//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) {

	gfn_initForm(true);   

	// DIVISION 사이즈 조정
	//FromEditSize, ToStaticStartWidth, ToStaticNm, ToStaticSize, ToEditStartWidth, ToEditSize, flag
	div_buyr.fn_reSize	(65, "1", "~", 15, "1", 65, ""	);  // 거래선 사이즈 조정 (FROM, TO 모두 처리)
	div_prjt.fn_reSize	(84, "1", "~", 15, "1", 84, ""	);  // 프로젝트사이즈 조정	(FROM, TO 모두 처리)

	// 오더유형 콤보목록 조회
	fn_query_auart();  

	// 폼 초기화
	fn_init_form();

}

/********************************************************************************
* 기      능   : 공통코드가 조회된 후의 Callback 함수 (common.js에서 호출됨)
********************************************************************************/
function fn_completeInit(sCode, sDsObj, sCodeType) {
	gfn_showMsg("CI00007"); 
	cb_auart.Index = 0;
}

/********************************************************************************
* 기      능   : form UnloadCompleted 
********************************************************************************/
function fn_OnUnloadCompleted(obj) {
	return gfn_isFormUnload(obj);
}

//=========================================================================================
// [ PART 3 ]
// Main Process Event 정의(조회,입력,삭제,저장,인쇄,form닫기)
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 1
********************************************************************************/
var fn_query_chk = false; //중복조회 방지용 flag

function fn_query() { 

	//alert("fn_query()호출 start");

	gfn_clearMsgBar(); 

	// sort mark 삭제 
	gfn_clearGridSortMark(grd_list_itab); 
	gfn_clearGridSortMark(grd_list_itab2); 

	// 조회조건 항목오류검증
	if (fn_valid_chk("query") == false)  return;

	// 조회조건 설정
	ds_cond.ClearData();
	ds_cond_prjt_list.ClearData();
	ds_cond_buyr_list.ClearData();
	if (fn_param_set("query") == false)  return;

	// 업무구분별 그리드 Set
	fn_jobgbn_set(); 		// 그리드 visable 제어
	fn_grid_header_set();	// 그리드 header 제어

	// 데이타셋 이벤트 비활성화
	ds_list_itab.Fireevent = false;

	// 기준년월 초기화
	F_YEARM 		= "";	 // 기준년월

	// 최종 변경된 셀정보 초기화
	F_CHG_COLNUM	= "";	 // 변경된 컬럼번호
	F_CHG_ROWNUM	= "";	 // 변경된 행번호

	if(!fn_query_chk) {
		fn_query_chk = true;
	} else {
		gfn_showAlert("CW10025", "조회중입니다..");
		return;
	}

	// 서비스 호출 
	fn_createWait("",this);     // 처리중입니다. 메시지 처리
	ds_list_itab.ClearData();  
	ds_list_itab2.ClearData();  
	ds_error.ClearData();   
	nsfRequest("query"									// srvId
				, "ssa0020/find"						// url
				, "ds_cond=ds_cond" + " ds_cond_prjt_list=ds_cond_prjt_list"
				                   	+ " ds_cond_buyr_list=ds_cond_buyr_list" 	// inDsList
				, "ds_list_itab=ds_list_itab ds_list_itab2=ds_list_itab2 ds_error=ds_error"	// outDsList
				, ""									// strParam
				, "fn_afterQuery"						// CallBackFunc
				); 

}


// INPUT PARAM SET
// pProcGb : 처리구분 (query:조회)
function fn_param_set(pProcGb)
{  

	var pldat = "";  // 회계기준년월

	switch(pProcGb)
	{
		case "zclose" :  // 마감일자 조회

			ds_cond_zclose.AddRow();
			ds_cond_zclose.SetColumn(ds_cond_zclose.row, "ZYEAR", substr(cal_yearm.value,0,4));  // 기준년도
			ds_cond_zclose.SetColumn(ds_cond_zclose.row, "ZMONTH", substr(cal_yearm.value,4,2)); // 기준월도

			break;

		case "query" :  // 조회 시

			ds_cond.ClearData();
			ds_cond.AddRow();  

			ds_cond.SetColumn(0, "YEARM"	, replace(trim(cal_yearm.text),".","")	); 	// 기준년월  
			ds_cond.SetColumn(0, "FR_PAYER"	, ""									);  // 거래선_FR
			ds_cond.SetColumn(0, "TO_PAYER"	, ""									);  // 거래선_TO
			ds_cond.SetColumn(0, "FR_VB"	, trim(div_vbvg.cb_frvb.Value)			);  // 부서코드_FR
			ds_cond.SetColumn(0, "TO_VB"	, trim(div_vbvg.cb_tovb.Value)			);  // 부서코드_TO
			ds_cond.SetColumn(0, "FR_VG"	, trim(div_vbvg.cb_frvg.Value)			);  // 팀코드_FR  
			ds_cond.SetColumn(0, "TO_VG"	, trim(div_vbvg.cb_tovg.Value)			);  // 팀코드_TO  
			ds_cond.SetColumn(0, "FR_EMPL"	, trim(div_sman_fr.ed_sman_cd.text)		);  // 담당자_FR 
			ds_cond.SetColumn(0, "TO_EMPL"	, trim(div_sman_to.ed_sman_cd.text)		);  // 담당자_TO  
			ds_cond.SetColumn(0, "FR_PJT"	, ""									);  // 프로젝트_FR  
			ds_cond.SetColumn(0, "TO_PJT"	, ""									);  // 프로젝트_TO
			ds_cond.SetColumn(0, "FR_AUART"	, trim(cb_auart_fr.value)				);  // 오더유형_FR  
			ds_cond.SetColumn(0, "TO_AUART"	, trim(cb_auart_to.value)				);  // 오더유형_TO
			ds_cond.SetColumn(0, "JOB_GBN"	, rd_job_gbn.Value						);  // 업무구분
			ds_cond.SetColumn(0, "PRT_GBN"	, rd_prt_gbn.Value						);  // 리스트양식

			// SubForm 데이타셋을 Copy해옴  : 빈행제외
			gfn_copyDs(div_buyr.Ds_rtn, ds_cond_buyr_list, GC_BUYR_FILTER_COND	); 	// 거래선 범위조건
			gfn_copyDs(div_prjt.Ds_rtn, ds_cond_prjt_list, GC_PRJT_FILTER_COND	); 	// 프로젝트 범위조건


			break;  

		case "update_plan" :  // 수금계획 저장 시

			// 기준년월 SET 
			ds_update_yearm.AddRow();  
			ds_update_yearm.SetColumn(0, "YEARM" , F_YEARM); 	// 기준년월 = 조회된 자료의 기준년월로 저장

			// 수금계획 SET 
			for (var i=0; i<ds_list_itab.RowCount(); i++)
			{ 
				if (ds_list_itab.getcolumn(i, "CHK") == "1" &&
					ds_list_itab.getcolumn(i, "SAVE_YN") == "Y")  // 저장대상 행에 한해서만 저장수행
				{
					ds_list_update_plan.AddRow();     

					pldat = gfn_formatDate(ds_list_itab.GetColumn(i, "PLDAT"),"-"); // 회계기준일자

					ds_list_update_plan.SetColumn(ds_list_update_plan.row, "BUKRS" 		, F_BUKRS								);  // 회사코드(1000)
					ds_list_update_plan.SetColumn(ds_list_update_plan.row, "KUNNR" 		, ds_list_itab.GetColumn(i, "KUNNR")	);  // 고객 번호 1		    
					ds_list_update_plan.SetColumn(ds_list_update_plan.row, "KIDNO" 		, ds_list_itab.GetColumn(i, "KIDNO")	);  // 프로젝트		      
					ds_list_update_plan.SetColumn(ds_list_update_plan.row, "BELNR" 		, ds_list_itab.GetColumn(i, "BELNR")	);  // 회계 전표 번호		
					ds_list_update_plan.SetColumn(ds_list_update_plan.row, "PLDAT" 		, pldat									);  // 회수계획일자		  
					ds_list_update_plan.SetColumn(ds_list_update_plan.row, "WAERS" 		, ds_list_itab.GetColumn(i, "WAERS")	);  // 통화 키 		      
					ds_list_update_plan.SetColumn(ds_list_update_plan.row, "PLAN_A01" 	, ds_list_itab.GetColumn(i, "PLAN_A01")	);  // 기준월 수금계획	
					ds_list_update_plan.SetColumn(ds_list_update_plan.row, "PLAN_A02" 	, ds_list_itab.GetColumn(i, "PLAN_A02")	);  // M+1월 수금계획		
					ds_list_update_plan.SetColumn(ds_list_update_plan.row, "PLAN_A03" 	, ds_list_itab.GetColumn(i, "PLAN_A03")	);  // M+2월 수금계획		
					ds_list_update_plan.SetColumn(ds_list_update_plan.row, "PLAN_A04" 	, ds_list_itab.GetColumn(i, "PLAN_A04")	);  // M+3월 수금계획		
					ds_list_update_plan.SetColumn(ds_list_update_plan.row, "PLAN_A05" 	, ds_list_itab.GetColumn(i, "PLAN_A05")	);  // M+4월 수금계획		
					ds_list_update_plan.SetColumn(ds_list_update_plan.row, "PLAN_A06" 	, ds_list_itab.GetColumn(i, "PLAN_A06")	);  // M+5월 수금계획		
					ds_list_update_plan.SetColumn(ds_list_update_plan.row, "PLAN_A07" 	, ds_list_itab.GetColumn(i, "PLAN_A07")	);  // M+6월 수금계획		
					ds_list_update_plan.SetColumn(ds_list_update_plan.row, "PLAN_A08" 	, ds_list_itab.GetColumn(i, "PLAN_A08")	);  // M+7월 수금계획		
					ds_list_update_plan.SetColumn(ds_list_update_plan.row, "PLAN_A09" 	, ds_list_itab.GetColumn(i, "PLAN_A09")	);  // M+8월 수금계획		
					ds_list_update_plan.SetColumn(ds_list_update_plan.row, "PLAN_A10" 	, ds_list_itab.GetColumn(i, "PLAN_A10")	);  // M+9월 수금계획		
					ds_list_update_plan.SetColumn(ds_list_update_plan.row, "PLAN_A11" 	, ds_list_itab.GetColumn(i, "PLAN_A11")	);  // M+10월 수금계획	
					ds_list_update_plan.SetColumn(ds_list_update_plan.row, "PLAN_A12" 	, ds_list_itab.GetColumn(i, "PLAN_A12")	);  // M+11월 수금계획	
					ds_list_update_plan.SetColumn(ds_list_update_plan.row, "PLAN_A13" 	, ds_list_itab.GetColumn(i, "PLAN_A13")	);  // M+12월 수금계획	
				}
			}  

			// trace("ds_list_update_plan.RowCount() : " + ds_list_update_plan.RowCount());

			break;   

		case "update_reason" :  // 채권사유 저장 시

			var sysdatetime = "";  	// 현재일시

			// 기준년월 SET 
			ds_update_yearm.AddRow();  
			ds_update_yearm.SetColumn(0, "YEARM" , F_YEARM); 	// 기준년월 = 조회된 자료의 기준년월로 저장

			// 채권사유 SET 
			for (var i=0; i<ds_list_itab.RowCount(); i++)
			{ 
				if (ds_list_itab.getcolumn(i, "CHK") == "1" &&
					ds_list_itab.getcolumn(i, "SAVE_YN") == "Y")  // 저장대상 행에 한해서만 저장수행
				{
					ds_list_update_reason.AddRow();     

					sysdatetime = gfn_formatDate(GetDate(),"-");  // 현재일시  
					// trace("sysdatetime : " + sysdatetime);

					ds_list_update_reason.SetColumn(ds_list_update_reason.row, "MANDT" 		, G_MANDT								);  // 클라이언트
					ds_list_update_reason.SetColumn(ds_list_update_reason.row, "PSPID" 		, ds_list_itab.GetColumn(i, "KIDNO")	);  // 프로젝트
					ds_list_update_reason.SetColumn(ds_list_update_reason.row, "REASON" 	, ds_list_itab.GetColumn(i, "REASON")	);  // 채권사유
					ds_list_update_reason.SetColumn(ds_list_update_reason.row, "ERDAT" 		, substr(sysdatetime,0,10)				);  // 등록일자		
					ds_list_update_reason.SetColumn(ds_list_update_reason.row, "ERZZT" 		, substr(sysdatetime,11,8) 				);  // 등록시간
					ds_list_update_reason.SetColumn(ds_list_update_reason.row, "ERNAM" 		, G_USER_NAME							);  // 등록자이름
					ds_list_update_reason.SetColumn(ds_list_update_reason.row, "AEDAT" 		, substr(sysdatetime,0,10) 				);  // 수정일자
					ds_list_update_reason.SetColumn(ds_list_update_reason.row, "AEZZT" 		, substr(sysdatetime,11,8)				);  // 수정시간
					ds_list_update_reason.SetColumn(ds_list_update_reason.row, "AENAM" 		, G_USER_NAME							);  // 수정자이름
				}
			}  

			// trace("ds_list_update_reason.RowCount() : " + ds_list_update_reason.RowCount());

			break;   

		default: 
			break; 
	}   

	return true;

}
/********************************************************************************
* 기      능   : 닫기  
********************************************************************************/
function fn_close() { 

	if (fn_valid_chk("close") == false) return false;

	close();
}


/********************************************************************************
* 기      능   : 복원 
********************************************************************************/
function fn_reset() 
{
	ds_list_itab.Reset();
	ds_list_itab2.Reset();
}

/********************************************************************************
* 기      능   : 액셀  
********************************************************************************/
function fn_excel() {

	// trace("fn_excel()호출 start");

	gfn_clearMsgBar(); 

	// 조회조건 항목오류검증
	if (fn_valid_chk("excel") == false)  return; 

	// Excel Export 
	if (rd_job_gbn.Value == "1" || rd_job_gbn.Value == "2") // 미수, 부도
	{
		gfn_saveToExcel(grd_list_itab, true);
	}
	else if (rd_job_gbn.Value == "3")						// 수금계획 리포트
	{
		gfn_saveToExcel(grd_list_itab2, true);
	}

}


//=========================================================================================
// [ PART 3 ]
// Main Process Event 정의(조회,입력,삭제,저장,인쇄,form닫기)
//=========================================================================================
/********************************************************************************
* 기      능   : 저장 : 미수, 부도의 수금계획 저장
********************************************************************************/
function fn_update_plan() { 

	// trace("fn_update_plan() start");

	gfn_clearMsgBar();   

	// 저장항목 오류검증
	if (fn_valid_chk("update_plan") == false)  return false;

	if (!gfn_showConfirm("CI00014", rd_job_gbn.Text + " 수금계획 변경사항")) 
	{
		return false;
	}

	// 저장항목 값설정
	ds_update_yearm.ClearData();
	ds_list_update_plan.ClearData(); 
	if (fn_param_set("update_plan") == false)  return false;   
	// trace(ds_update_yearm.SaveXML());
	// trace(ds_list_update_plan.SaveXML());	

	// 서비스 호출
	fn_createWait("",this);     // 처리중입니다. 메시지 처리
	ds_error.ClearData();
	nsfRequest("update"										// srvId
				, "ssa0020/update_plan"						// url
				, "ds_update_yearm=ds_update_yearm ds_list_update_plan=ds_list_update_plan"	// inDsList
				, "ds_error=ds_error"						// outDsList
				, ""										// strParam
				, "fn_afterSave_update_plan"				// CallBackFunc
				);

}

/********************************************************************************
* 기      능   : 저장 : 미수, 부도의 채권사유 저장
********************************************************************************/
function fn_update_reason() { 

	// trace("fn_update_reason() start");

	gfn_clearMsgBar();   

	// 저장항목 오류검증
	if (fn_valid_chk("update_reason") == false)  return false;

	if(!Confirm("저장하시겠습니까?")){
	   return false;
	}

	// 저장항목 값설정
	ds_update_yearm.ClearData();
	ds_list_update_reason.ClearData(); 
	if (fn_param_set("update_reason") == false)  return false;    
	// trace(ds_update_yearm.SaveXML());
	// trace(ds_list_update_reason.SaveXML());	

	// 서비스 호출
	fn_createWait("",this);     // 처리중입니다. 메시지 처리
	ds_error.ClearData();
	nsfRequest("update"										// srvId
				, "ssa0020/update_reason"					// url
				, "ds_update_yearm=ds_update_yearm ds_list_update_reason=ds_list_update_reason"	// inDsList
				, "ds_error=ds_error"						// outDsList
				, ""										// strParam
				, "fn_afterSave_update_reason"				// CallBackFunc
				);

}

//=========================================================================================
// [ PART 4]
// Callback 함수 정의 : callback 함수명은 메인 함수의 fn_xxxx() 에서 xxxx 부분은 fn_afterXXXX()
// 형태가 되도록 작성 
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {

	fn_query_chk = false; //중복조회 방지용 flag
	fn_destroyWait();

	// 결과값 trace
	// trace("fn_afterQuery 호출 start"); 
	// trace(ds_error.SaveXML());
	// trace(ds_list_itab.SaveXML());
	// trace(ds_list_itab2.SaveXML());

	// 데이타셋 이벤트 활성화
	ds_list_itab.Fireevent = true;

	if(ds_error.RowCount() > 0) 	// 조회실패
	{ 
		gfn_showMsgBar(ds_error.GetColumn(0,"ERRMSG")); 
	} 
	else 
	{ 
		// 업무구분이 미수(1), 부도(2) 인 경우
		if (rd_job_gbn.Value == "1" || rd_job_gbn.Value == "2")
		{
			// 조회된 자료가 있을 경우 그리드 첫번째 행에 포커스 위치시킨다.
			if (ds_list_itab.RowCount() > 0) 
			{ 
				grd_list_itab.SetFocus();

				// 폼변수 기준년월 SET (저장용)
				F_YEARM = substr(replace(cal_yearm.value, ".",""),0,6);	

				// 작업구분 컬럼 추가
				// ds_list_itab.AddColumn("CHK" , "STRING", 1);		// 선택여부
				// ds_list_itab.AddColumn("FLAG", "STRING", 1);		// 변경여부  
				// ds_list_itab.ApplyChange();  

				// CI00002 : "조회를 완료했습니다.조회건수 [${}건]"
				gfn_showMsg("CI00002", ds_list_itab.RowCount() + ""); 

			} else {			 
				// trace("fn_Query SUCCESS!!! 건수없음");
				// CW00010 : "데이터가 없습니다."
				gfn_showMsg("CW00010"); 
			}
		}

		// 업무구분이 수금계획리포트(3)인 경우
		else
		{
			// 조회된 자료가 있을 경우 그리드 첫번째 행에 포커스 위치시킨다.
			if (ds_list_itab2.RowCount() > 0) 
			{
				grd_list_itab2.SetFocus();
				// trace("fn_Query SUCCESS!!! [건수 : " + ds_list_itab2.RowCount() + "]");
				gfn_showMsg("CE00001");   
			} else {			
				// trace("fn_Query SUCCESS!!! 건수없음");
				gfn_showMsg("CI00002", ds_list_itab2.RowCount() + "");
			}
		} 
	} 

	// trace("fn_afterQuery 호출 end");
} 

/********************************************************************************
* 기      능   : 저장 Callback  : 미수, 부도의 회수계획
********************************************************************************/
function fn_afterSave_update_plan(errCode, errMsg) { 

	fn_destroyWait(); 

	// trace("fn_afterSave_update_plan 호출 start");  

	// trace(ds_error.SaveXML());

	if(ds_error.RowCount() > 0) 	// 저장실패
	{ 
		gfn_showMsgBar(ds_error.GetColumn(0,"ERRMSG")); 
	} 
	else 
	{  
		gfn_showAlert("CI00003");  // CI00003: "저장을 완료했습니다

		ds_list_itab.ApplyChange();

		// 재조회
		fn_query(); 
	}

	// trace("fn_afterSave_update_plan 호출 end");
}


/********************************************************************************
* 기      능   : 저장 Callback  : 미수, 부도의 채권사유
********************************************************************************/
function fn_afterSave_update_reason(errCode, errMsg) { 

	fn_destroyWait(); 

	// trace("fn_afterSave_update_reason 호출 start");  

	// trace(ds_error.SaveXML()); 

	if(ds_error.RowCount() > 0) 	// 저장실패
	{ 
		gfn_showMsgBar(ds_error.GetColumn(0,"ERRMSG")); 
	} 
	else 
	{  
		gfn_showAlert("CI00003");  // CI00003: "저장을 완료했습니다

		ds_list_itab.ApplyChange();

		// 재조회
		fn_query();
	}

	// trace("fn_afterSave_update_reason 호출 end");
}

// 임시코딩
function fn_OnClick_btn(obj)
{ 
	//alert("fn_OnClick_btn");

	var fncName = "fn_" + substr(obj.id, 4); 
	eval( fncName + "()");
}

// 항목 오류 검증
// pProcGb : 처리구분 (query:조회,excel:엑셀)
function fn_valid_chk(pProcGb)
{   
	var arr   = "";
	var ChgYn = "N";
	var SaveCount = 0;

 	switch(pProcGb)
	{
		case "zclose" :	// 마감일자 조회 시 
			if (length(cal_yearm.Value) == 0)
			{
				// CW00208 : "[${}]은/는 필수입력 입니다.확인바랍니다.")
				gfn_showAlert("CW00208","기준년월");
				gfn_SetFocus(cal_yearm,"Y"); 
				return false;
			} 
			break;

		case "query" :   // 조회 시

			if (length(cal_yearm.Value) == 0)
			{
				// CW00208 : "[${}]은/는 필수입력 입니다.확인바랍니다.")
				gfn_showAlert("CW00208","기준년월");
				gfn_SetFocus(cal_yearm,"Y"); 
				return false;
			}  

			//미수(1), 부도(2)인 경우 마감일자 없으면 오류처리
			if (rd_job_gbn.Value == "1" || rd_job_gbn.Value == "2")
			{
				if (F_ZCLOSE_YN == "N")
				{
					// "CW10004", "${}은(는) ${}되지 않아 ${} 할 수 없습니다."
					arr = [substr(cal_yearm.Value,0,4)+"년 "+substr(cal_yearm.Value,4,2)+"월", "마감", "조회"]; 
					gfn_showAlert("CW10004", arr);
					gfn_SetFocus(cal_yearm,"Y"); 
					return false;
				}
			}

			if (ds_list_itab.GetUpdate()==true)
			{ 
				arr = ["조회","계속"];
				// "CI10001", "변경된 자료가 존재합니다. \n${} 할 경우 기존에 변경된 정보가 모두 소실됩니다.\n\n{} 하시겠습니까?"); 			 
				if (gfn_showConfirm("CI10001", arr) == false)   
				{ 
					// 조회하지 않기 
					gfn_SetFocusGrid(grd_list_itab, F_CHG_ROWNUM, F_CHG_COLNUM);  // 변경된 행에 포커싱처리
					return false;
				}   
			}

			break;  

		case "excel" :   // 엑셀

			if (rd_job_gbn.Value == "1" || rd_job_gbn.Value == "2")	// 미수/부도
			{
				if (ds_list_itab.RowCount() <=0)
				{
					// CW00010 : "데이터가 없습니다."
					gfn_showAlert("CW00010");
					return false;
				} 

				// 변경된 내역이 존재할 경우 엑셀출력 불가처리
				if(gfn_isChangeDs(ds_list_itab, false, "FLAG", rd_job_gbn.text)== true) 
				{  
					//"CW10003", "변경된 ${}이 존재하여 ${}이 불가능합니다. 변경된 정보를 저장후 다시 실행하여 주십시오.");  // 2012.06.13 : GRY : 추가
					arr = [rd_job_gbn.text, "엑셀출력"];
					gfn_showAlert("CW10003", arr);
				}  


			}
			else if (rd_job_gbn.Value == "3")  // 수금계획
			{
				if (ds_list_itab2.RowCount() <=0)
				{
					// CW00010 : "데이터가 없습니다."
					gfn_showAlert("CW00010");
					return false;
				}
			}
			break; 

		case "update_plan":   	// 수금계획저장

			// 변경된 내역 존재여부 체크  
			if(gfn_isChangeDs(ds_list_itab, true, "FLAG", rd_job_gbn.Text + " 수금계획")== false) 
			{ 
				return false; 
			}    

			// 선택된 행 존재여부 체크
			if (ds_list_itab.FindRow("CHK",1) < 0)
			{ 
				// CW00013 : "처리할 자료를 먼저 선택해 주십시오."
				gfn_showAlert("CW00013"); 
				return false; 
			}  

			F_CHG_COLNUM = F_COLNUM_PLAN_13;	// 최종 변경된 컬럼번호(임의값:13개월차회수계획금액컬럼)

			for (var i=0; i<=ds_list_itab.RowCount(); i++)
			{  

				// 선택된 행에 대하여
				if (ds_list_itab.GetColumn(i, "CHK") == 1)
				{ 

					// arr에 선언된 컬럼에 대해서만 변경여부 체크 (Y) 
					ChgYn = gfn_GetDatasetChgYN(grd_list_itab, i, F_SAVE_PLAN_ARR, "Y"); 
					if (ChgYn == "Y")  			// 변경된 자료가 있으면
					{ 

						gfn_SetColumn(ds_list_itab, i, "SAVE_YN", "Y");  // 저장대상=Y로 SET
						SaveCount++;								// 저장대상건수 누적

						F_CHG_ROWNUM = i;		// 최종 변경된 헹번호 

						// 회수계획일 < SYSDATE || 회수계획일 > SYSDTAE + 1 이면  오류처리 
						if (fn_chk_pldat(i, F_COLNUM_PLDAT)==false) return false;  

						// sum(회수계획금액) > 장기미수금액이면 오류처리 
						if (fn_chk_plan_amt(i, F_COLNUM_PLAN_13)==false)  return false;  
					}
					else
					{
						gfn_SetColumn(ds_list_itab, i, "SAVE_YN", "N");  // 저장대상=N로 원복 
					}
				}
			}

			// trace("fn_valid_chk.SaveCount : " + SaveCount);
			if (SaveCount == 0)
			{ 
				// CW00006=${}에 변경된 정보가 없습니다.
				gfn_showAlert("CW00006", rd_job_gbn.Text + " 수금계획");
				return false; 
			}

			break;

		case "update_reason" :  // 채권사유저장

			// 변경된 내역 존재여부 체크 
			if(gfn_isChangeDs(ds_list_itab, true, "FLAG", rd_job_gbn.Text + " 채권사유")== false) 
			{ 
				return false; 
			}   
			// 선택된 행 존재여부 체크
			if (ds_list_itab.FindRow("CHK",1) < 0)
			{	
				// CW00013 : "처리할 자료를 먼저 선택해 주십시오."
				gfn_showAlert("CW00013"); 
				return false; 
			}

			F_CHG_COLNUM = F_COLNUM_PLAN_13;	// 최종 변경된 컬럼번호(임의값:13개월차회수계획금액컬럼)
			for (var i=0; i<=ds_list_itab.RowCount(); i++)
			{ 
				// 선택된 행에 대하여
				if (ds_list_itab.GetColumn(i, "CHK") == 1)
				{ 

					// arr에 선언된 컬럼에 대해서만 변경여부 체크 (Y) 
					ChgYn = gfn_GetDatasetChgYN(grd_list_itab, i, F_SAVE_LAW_ARR, "Y"); 
					if (ChgYn == "Y")  			// 변경된 자료가 있으면
					{ 

						gfn_SetColumn(ds_list_itab, i, "SAVE_YN", "Y");  // 저장대상=Y로 SET
						SaveCount++;								// 저장대상건수 누적

						F_CHG_ROWNUM = i;		// 최종 변경된 헹번호 
					}
					else
					{
						gfn_SetColumn(ds_list_itab, i, "SAVE_YN", "N");  // 저장대상=N로 원복 
					}
				}
			} 

			// trace("fn_valid_chk.SaveCount : " + SaveCount);
			if (SaveCount == 0)
			{ 
				// CW00006=${}에 변경된 정보가 없습니다.
				gfn_showAlert("CW00006", rd_job_gbn.Text + " 채권사유");
				return false; 
			}

			break;

		default: 
			break; 
	}   

	return true;
}

/********************************************************************************
* 기      능   : 새로고침
********************************************************************************/
function fn_refresh() {

	// 폼초기화
	//fn_init_form();

}  

// 폼 초기화 
function fn_init_form()
{ 
	// 임시코딩 
	if (length(G_SESSION_ID)==0)
	{
		btn_query.Visible = true;   
		btn_excel.Visible = true;
		btn_close.Visible = true;
	}
	else
	{ 
		btn_query.Visible = false;  
		btn_excel.Visible = false;
		btn_close.Visible = false;
	}

 	// 메세지바 초기화
	gfn_clearMsgBar();  

	// 검색조건 div의 border 삭제
	div_vbvg.Border 	= "none";  	// 부서 FROM ~ TO div 
	div_sman_fr.Border 	= "none";	// 담당자_FR div
	div_sman_to.Border 	= "none";	// 담당자_TO div
	div_buyr.Border 	= "none";	// 거래선 FROM ~ TO div 
	div_prjt.Border 	= "none";	// 프로젝트 FROM ~ TO div 

	// 코딩 시 필요한 그리드 컬럼 인덱스	 
	F_COLNUM_CHK		= gfn_getColIdx(grd_list_itab,"CHK")     ;  // 체크여부     
	F_COLNUM_STATUS		= gfn_getColIdx(grd_list_itab,"STATUS")  ;  // 상태    
	F_COLNUM_LANDX		= gfn_getColIdx(grd_list_itab,"LANDX")   ;  // 국가
	F_COLNUM_BDATE		= gfn_getColIdx(grd_list_itab,"BDATE")   ;  // 부도일자
	F_COLNUM_PLDAT 		= gfn_getColIdx(grd_list_itab,"PLDAT")   ;  // 회수계획일         
	F_COLNUM_PLAN_01 	= gfn_getColIdx(grd_list_itab,"PLAN_A01");  // 기준년월+0 회수금액  
	F_COLNUM_PLAN_02 	= gfn_getColIdx(grd_list_itab,"PLAN_A02");  // 기준년월+1 회수금액  
	F_COLNUM_PLAN_03 	= gfn_getColIdx(grd_list_itab,"PLAN_A03");  // 기준년월+2 회수금액  
	F_COLNUM_PLAN_04 	= gfn_getColIdx(grd_list_itab,"PLAN_A04");  // 기준년월+3 회수금액  
	F_COLNUM_PLAN_05 	= gfn_getColIdx(grd_list_itab,"PLAN_A05");  // 기준년월+4 회수금액  
	F_COLNUM_PLAN_06 	= gfn_getColIdx(grd_list_itab,"PLAN_A06");  // 기준년월+5 회수금액  
	F_COLNUM_PLAN_07 	= gfn_getColIdx(grd_list_itab,"PLAN_A07");  // 기준년월+6 회수금액  
	F_COLNUM_PLAN_08 	= gfn_getColIdx(grd_list_itab,"PLAN_A08");  // 기준년월+7 회수금액  
	F_COLNUM_PLAN_09 	= gfn_getColIdx(grd_list_itab,"PLAN_A09");  // 기준년월+8 회수금액  
	F_COLNUM_PLAN_10 	= gfn_getColIdx(grd_list_itab,"PLAN_A10");  // 기준년월+9 회수금액  
	F_COLNUM_PLAN_11 	= gfn_getColIdx(grd_list_itab,"PLAN_A11");  // 기준년월+10 회수금액 
	F_COLNUM_PLAN_12 	= gfn_getColIdx(grd_list_itab,"PLAN_A12");  // 기준년월+11 회수금액 
	F_COLNUM_PLAN_13 	= gfn_getColIdx(grd_list_itab,"PLAN_A13");  // 기준년월+12 회수금액 
	F_COLNUM_LONG_AMT 	= gfn_getColIdx(grd_list_itab,"LONG_AMT");  // 장기미수금액
	F_COLNUM_REASON 	= gfn_getColIdx(grd_list_itab,"REASON")	 ;  // 채권사유  
	F_COLNUM_REASONT 	= gfn_getColIdx(grd_list_itab,"REASONT") ;  // 채권사유 내역
	F_COLNUM_KIDNO_T    = gfn_getColIdx(grd_list_itab2,"KIDNO_T");  // 거래선명 

	// 폼변수 초기화
	F_ZCLOSE_YN 	= "N";  // 기준년월자료 마감여부
	F_YEARM     	= "";	// 목록 조회가 완료된 기준년월 
	F_CHG_COLNUM	= "";	 // 변경된 컬럼번호
	F_CHG_ROWNUM	= "";	 // 변경된 행번호
	F_SYSDATE   	= substr(gfn_getCurrDate(),0,8); // 현재년월일(8자리)

	// 기준년월 기본값 SETTING
	cal_yearm.Value = substr(gfn_getCurrDate(),0,8); // 기준년월 = 현재년월  

	ds_cond.ClearData();  		// 검색조건 데이타셋 초기화
	ds_list_itab.ClearData(); 	// 검색결과 그리드데이타셋 초기화
	ds_list_itab2.ClearData(); 	// 검색결과 그리드데이타셋 초기화

	// 업무구분별 초기화	
	grd_list_itab.top 		= 110;		// 미수, 부도 그리드
	grd_list_itab.left 		= 10;		// 미수, 부도 그리드
	grd_list_itab.Height 	= 443;		// 미수, 부도 그리드
	grd_list_itab2.top 		= 110;		// 수금계획리포트 그리드
	grd_list_itab2.left 	= 10;		// 수금계획리포트 그리드
	grd_list_itab2.Height 	= 443;		// 수금계획리포트 그리드
	fn_jobgbn_set();  					// 업무구분옵션 visable set 
	//fn_grid_header_set();  			// 그리드(미수,부도)의 헤더에 기준년월 대비 계획년월 SET
	ed_zclose.Text = "마감년월 : ";
	grd_list_itab.Format	= "Default";
	grd_list_itab2.Format 	= "Default";

	// 그 외 항목 초기화
	F_YEARM = "";	 // 기준년월 

	// 그리드 FIX
	gfn_grid_fix(grd_list_itab	, F_COLNUM_STATUS);		// 상태에서 fix
	gfn_grid_fix(grd_list_itab2	, F_COLNUM_KIDNO_T);	// 거래선명에서 fix 

	// 옵션버튼별 초기화
	rd_job_gbn_OnClick(); 

	// 버튼 제어
	ufn_enableBtn("input"	, false);  // 입력버튼 비활성화
	ufn_enableBtn("save"	, false);  // 저장버튼 비활성화
	ufn_enableBtn("delete"	, false);  // 사제버튼 비활성화

} 

