// 업무구분 별 그리드 설정
function fn_jobgbn_set()
{  
	switch (rd_job_gbn.Value)
	{
		case "1" : // 미수
			grd_list_itab.Visible 		= true;		// 미수, 부도 그리드 보이게
			grd_list_itab2.Visible 		= false;	// 수금계획 그리드 안보이게
			//ed_zclose.Visible 		= true;     // 마감년월 보이게
			btn_update_plan.Enable		= true;   	// 수금계획반영 버튼 사용하게
			btn_update_reason.Enable	= true; 	// 채권사유반영 버튼 사용하게
			btn_progress_mng.Enable		= true;   	// 진행사항관리 버튼 사용하게
			btn_legal_action_mng.Enable	= true; 	// 법조치관리 버튼 사용하게
			break;
		case "2" :	// 부도
			grd_list_itab.Visible 		= true;		// 미수, 부도 그리드 보이게
			grd_list_itab2.Visible 		= false;	// 수금계획 그리드 안보이게
			//ed_zclose.Visible 		= true;     // 마감년월 보이게
			btn_update_plan.Enable		= true;   	// 수금계획반영 버튼 사용하게
			btn_update_reason.Enable	= true; 	// 채권사유반영 버튼 사용하게
			btn_progress_mng.Enable		= true;   	// 진행사항관리 버튼 사용하게
			btn_legal_action_mng.Enable	= true; 	// 법조치관리 버튼 사용하게
			break;
		case "3" : // 수금계획
			grd_list_itab.Visible 		= false;	// 미수, 부도 그리드 안보이게
			grd_list_itab2.Visible 		= true;		// 수금계획 그리드 보이게
			//ed_zclose.Visible 		= false;    // 마감년월 안보이게
			btn_update_plan.Enable		= false;   	// 수금계획반영 버튼 사용못하게
			btn_update_reason.Enable	= false; 	// 채권사유반영 버튼 사용못하게
			btn_progress_mng.Enable		= false;   	// 진행사항관리 버튼 사용못하게
			btn_legal_action_mng.Enable	= false; 	// 법조치관리 버튼 사용못하게
			break;
	  default : 
			break;
	}
}

// 기준년월에 따른 그리드 헤더(계획년월) 재설정
// YYYY.MM월 계획
function fn_grid_header_set()
{
	var bef_month = substr(replace(cal_yearm.Value, ".",""),0,6);
	var aft_month = "";     

	// 기준년월 + 
	for (var i=0; i<=12; i++)
	{
		aft_month = gfn_getAfterMonth (bef_month, i);  // 한달 씩 더한다.
		grd_list_itab.SetCellProp("Head"
									, F_COLNUM_PLAN_01 + i	//F_COLNUM_PLAN_01 컬럼에 기준년월계획이 위치함
									, "Text"
									, substr(aft_month,0,4)+"."+substr(aft_month,4,2)+"월 계획"  // YYYY.MM월 계획
									);
	}
}


// 오더유형코드목록 조회 CALL BACK함수
function fn_afterQuery_auart(errCode, errMsg) 
{   
	gfn_InsertRowCombo(ds_auart_cd, 0, "Y", "N", "Y"); // 오더유형에 전체 행 추가
	cb_auart_fr.index = 0;
	cb_auart_to.index = 0;
} 


function grd_list_itab_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if ( nCell == 0 ) 
	{
		gfn_selectGridCol(obj); 
	} 
	else if (nCell == 1) { 						// 선택 CELL 인 경우
		gfn_selectAllGrid2(obj,1,1,true);	// 전체 선택  
	} else { 								// 그 외 CELL 인 경우
		gfn_sortGrid(obj,nCell);			// 그리드 정렬 처리 
	} 
}

// 그리드 셀에서 엔터키 누르면 다음 행으로 이동
function grd_list_itab_OnKeyDown(obj,nChar,bShift,bCtrl,bAlt,nLLParam,nHLParam)
{

	// trace("grd_list_OnKeyDown.nChar : " + nChar); 

	var ds = object(obj.BindDataset);  

	// Enter 키이면
	if (nChar==13)
	{ 
		ds.row = ds.row + 1;  // 다음 행으로 이동
	} 
	// Esc 키이면 변경전 자료로 되돌린다.
	else if (nChar==27)
	{  
		var nCol 	= obj.GetCellPos();

		switch (nCol)				// 변경된 셀 인덱스
		{
			case F_COLNUM_PLDAT:	// 회수계획일 
			case F_COLNUM_PLAN_01:	// 기준년월+0 회수금액
			case F_COLNUM_PLAN_02:	// 기준년월+1 회수금액
			case F_COLNUM_PLAN_03:	// 기준년월+2 회수금액
			case F_COLNUM_PLAN_04:	// 기준년월+3 회수금액
			case F_COLNUM_PLAN_05:	// 기준년월+4 회수금액
			case F_COLNUM_PLAN_06:	// 기준년월+5 회수금액
			case F_COLNUM_PLAN_07:	// 기준년월+6 회수금액
			case F_COLNUM_PLAN_08:	// 기준년월+7 회수금액
			case F_COLNUM_PLAN_09:	// 기준년월+8 회수금액
			case F_COLNUM_PLAN_10:	// 기준년월+9 회수금액
			case F_COLNUM_PLAN_11:	// 기준년월+10 회수금액
			case F_COLNUM_PLAN_12:	// 기준년월+11 회수금액
			case F_COLNUM_PLAN_13:	// 기준년월+12 회수금액    
				gfn_SetOrgColumn(obj,  ds.row, nCol); 		 
				break;

			case F_COLNUM_REASON:			// 채권사유 
			case F_COLNUM_REASONT:			// 채권사유내역
				gfn_SetOrgColumn(obj,  ds.row, F_COLNUM_REASON); 		 
				gfn_SetOrgColumn(obj,  ds.row, F_COLNUM_REASONT); 	 
		}  
	} 
	else 
	{
		gfn_GrdCellCopyPasteReset(obj,  ds, bCtrl, nChar);

		// P 키이면 그리드 셋팅을 다시 한다.
		if ( nChar == 80 )   
		{
			// 그리드 FIX
			gfn_grid_fix(grd_list_itab	, F_COLNUM_STATUS);		// 상태에서 fix
			gfn_grid_fix(grd_list_itab2	, F_COLNUM_KIDNO_T);	// 거래선명에서 fix  

			// 국가, 부도일 Width 재설정
			rd_job_gbn_OnClick(rd_job_gbn);

			// 그리드의 헤더 재설정
			fn_grid_header_set();
		}
	} 

}

// 컬럼 내용이 변경되면 변경여부컬럼의 값을 "Y" 로 변경한다.
function ds_list_itab_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{ 

	// 아래 컬럼에 대해서만 변경여부 체크함
	switch (strColumnID)
	{
		case "CHK"		:    // 체크여부

			if (varNewValue==1)
			{ 
				// 변경된 자료가 없으면 체크하지 않는다.
				if (F_CHG_ROWNUM < 0 || F_CHG_COLNUM < 0) return; 

				// 변경된 행의 보수==X 이면 체크하지 않는다.
				if (ds_list_itab.GetColumn(F_CHG_ROWNUM, "BOSU") == "X") return;

				// 변경된 행의 FLAG = "U" 가 아니면 체크하지 않는다.
				if (ds_list_itab.GetColumn(F_CHG_ROWNUM, "FLAG") <> "U") return;  

				// 컬럼ID에 해당하는 그리드 셀번호 GET
				var nCol = gfn_getColIdx(grd_list_itab, strColumnID) ; 

				// 회수계획일 < 현재년월 || 회수계획일 > 현재년월 + 1 이면  오류처리
				if (fn_chk_pldat(nRow, nCol)==false) return;  

				// SUM(회수계획금액) > 장기미수금액  이면 오류처리
				if (fn_chk_plan_amt(nRow, nCol)==false) return;  
			}

			break; 

		case "PLDAT" 	:	// 회수계획일 
		case "PLAN_A01"	:	// 기준년월+0 회수금액
		case "PLAN_A02"	:	// 기준년월+1 회수금액
		case "PLAN_A03"	:	// 기준년월+2 회수금액
		case "PLAN_A04"	:	// 기준년월+3 회수금액
		case "PLAN_A05"	:	// 기준년월+4 회수금액
		case "PLAN_A06"	:	// 기준년월+5 회수금액
		case "PLAN_A07"	:	// 기준년월+6 회수금액
		case "PLAN_A08"	:	// 기준년월+7 회수금액
		case "PLAN_A09"	:	// 기준년월+8 회수금액
		case "PLAN_A10"	:	// 기준년월+9 회수금액
		case "PLAN_A11"	:	// 기준년월+10 회수금액
		case "PLAN_A12"	:	// 기준년월+11 회수금액
		case "PLAN_A13"	:	// 기준년월+12 회수금액
		case "REASON"   :  	// 사유   

			// 값이 변경되었을 경우 변경구분 컬럼 자동 SET 
			if (varNewValue != ds_list_itab.GetOrgColumn(nRow, strColumnID))  
			{ 
				obj.Fireevent = false;
				obj.SetColumn(obj.row, "CHK" , "1");	// 선택여부 
				obj.Fireevent = true; 

				// FLAG 변경 : U
				gfn_changeFlag( obj, strColumnID, nRow, "FLAG");  

				// 변경된 현재 행번호,컬럼번호
				F_CHG_COLNUM = grd_list_itab.GetCellPos(); 	// 현재 컬럼번호
				F_CHG_ROWNUM = nRow;		  				// 현재 행번호
			} 

			break;

		default :
			break;
	} 

} 

// 그리드 셀 위치변경시 sum(회수계획금액) > 장기미수금액이면 오류처리
function grd_list_itab_OnCellPosChanged(obj,nRow,nCell,nOldRow,nOldCell,nPivotIndex,nOldPivotIndex)
{  

	// trace("grd_list_itab_OnCellPosChanged"); 

	// 변경된 자료가 없으면 체크하지 않는다.
	if (F_CHG_ROWNUM < 0 || F_CHG_COLNUM < 0) return;

	// CHK 셀이면 체크하지 않는다.
	if (nCell == F_COLNUM_CHK) return;

	// 변경된 행의 보수==X 이면 체크하지 않는다.
	if (ds_list_itab.GetColumn(F_CHG_ROWNUM, "BOSU") == "X") return;
	// trace("BOSU : " + ds_list_itab.GetColumn(F_CHG_ROWNUM, "BOSU")); 

	// 변경된 행의 CHK <> 1 이 아니면 체크하지 않는다.
	if (ds_list_itab.GetColumn(F_CHG_ROWNUM, "CHK") <> 1) return; 
	// trace("CHK : " + ds_list_itab.GetColumn(F_CHG_ROWNUM, "CHK"));  

	// 변경된 행의 FLAG = "U" 가 아니면 체크하지 않는다.
	if (ds_list_itab.GetColumn(F_CHG_ROWNUM, "FLAG") <> "U") return;  
	// trace("FLAG : " + ds_list_itab.GetColumn(F_CHG_ROWNUM, "FLAG"));  

	switch (F_CHG_COLNUM)		// 변경된 셀 인덱스
	{
		case F_COLNUM_PLDAT:	// 회수계획일

			// 회수계획일 < SYSDATE || 회수계획일 > SYSDTAE + 1 이면  오류처리
			if (fn_chk_pldat(nOldRow, nOldCell)==false) return; 

			break;

		case F_COLNUM_PLAN_01:	// 기준년월+0 회수금액
		case F_COLNUM_PLAN_02:	// 기준년월+1 회수금액
		case F_COLNUM_PLAN_03:	// 기준년월+2 회수금액
		case F_COLNUM_PLAN_04:	// 기준년월+3 회수금액
		case F_COLNUM_PLAN_05:	// 기준년월+4 회수금액
		case F_COLNUM_PLAN_06:	// 기준년월+5 회수금액
		case F_COLNUM_PLAN_07:	// 기준년월+6 회수금액
		case F_COLNUM_PLAN_08:	// 기준년월+7 회수금액
		case F_COLNUM_PLAN_09:	// 기준년월+8 회수금액
		case F_COLNUM_PLAN_10:	// 기준년월+9 회수금액
		case F_COLNUM_PLAN_11:	// 기준년월+10 회수금액
		case F_COLNUM_PLAN_12:	// 기준년월+11 회수금액
		case F_COLNUM_PLAN_13:	// 기준년월+12 회수금액  

			// sum(회수계획금액) > 장기미수금액이면 오류처리
			if (fn_chk_plan_amt(nOldRow, nOldCell)==false) return; 

			break;
	}  

}

// 회수계획일 < 현재년월 || 회수계획일 > 현재년월 + 1 이면  오류처리
function fn_chk_pldat(pRow, pCol)
{ 
	// trace("F_CHG_ROWNUM : " + F_CHG_ROWNUM);
	// trace("F_CHG_COLNUM : " + F_CHG_COLNUM);

	// 값이 변경된 컬럼일 경우에만 오류 체크하기 위함
	if (F_CHG_ROWNUM < 0 || F_CHG_COLNUM < 0) 
	{
		return true;
	}  

	var sys_yearm 		= substr(F_SYSDATE, 0,6); 						// 현재년월
	var sys_yearm_after = gfn_getAfterMonth(sys_yearm, 1); 				// 현재년월 + 1월
	var pldat 			= replace(substr(replace(ds_list_itab.GetColumn(pRow,"PLDAT"),"-",""), 0, 6),"000000","");		// 회수계획일

	// trace("fn_chk_pldat.pRow 			: " + pRow);
	// trace("fn_chk_pldat.pCol 			: " + pCol);
	// trace("fn_chk_pldat.sys_yearm 		: " + sys_yearm);
	// trace("fn_chk_pldat.sys_yearm_after : " + sys_yearm_after);
	// trace("fn_chk_pldat.pldat 			: " + pldat);

	// 날짜오류검증
	if (gfn_chkDate(pldat, "회수계획일", true)==-1)
	{ 
		gfn_SetFocusGrid(grd_list_itab, pRow,  pCol);
		return false;  
	}

	if (pldat <> "" && (pldat < sys_yearm || pldat > sys_yearm_after))
	{ 

		// "CW10005", "[${}] 회수계획일자는 현재월 ~ 익월 범위로 입력 가능합니다."
		gfn_showAlert("CW10005", "No:"+toNumber(pRow+1));     
		gfn_SetFocusGrid(grd_list_itab, pRow, pCol); 

		return false; 
	}
	return true;
}

// SUM(회수계획금액) > 장기미수금액  이면 오류처리
function fn_chk_plan_amt(pRow, pCol)
{  
	// trace("F_CHG_ROWNUM " + F_CHG_ROWNUM);
	// trace("F_CHG_COLNUM " + F_CHG_COLNUM);
	// trace("fn_chk_plan_amt " + pRow + "," + pCol); 

	// 값이 변경된 컬럼일 경우에만 오류 체크하기 위함
	// if (F_CHG_ROWNUM < 0 || F_CHG_COLNUM < 0) 
	// {
		// return true;
	// }  
	if (F_CHG_ROWNUM >= 0 && F_CHG_COLNUM >= 0 && F_CHG_COLNUM <> pCol) 
	{
		return false;
	}  

	// 장기미수금액
	var long_amt	= ds_list_itab.GetColumn(pRow, "LONG_AMT");

	// 수금계획합계금액
	var plan_amt    = ds_list_itab.GetColumn(pRow, "PLAN_A01")
					+ ds_list_itab.GetColumn(pRow, "PLAN_A02")
					+ ds_list_itab.GetColumn(pRow, "PLAN_A03")
					+ ds_list_itab.GetColumn(pRow, "PLAN_A04")
					+ ds_list_itab.GetColumn(pRow, "PLAN_A05")
					+ ds_list_itab.GetColumn(pRow, "PLAN_A06")
					+ ds_list_itab.GetColumn(pRow, "PLAN_A07")
					+ ds_list_itab.GetColumn(pRow, "PLAN_A08")
					+ ds_list_itab.GetColumn(pRow, "PLAN_A09")
					+ ds_list_itab.GetColumn(pRow, "PLAN_A10")
					+ ds_list_itab.GetColumn(pRow, "PLAN_A11")
					+ ds_list_itab.GetColumn(pRow, "PLAN_A12")
					+ ds_list_itab.GetColumn(pRow, "PLAN_A13")
					;

	// 회수계획 > 장기미수금액 인 경우 오류처리
	if (plan_amt > long_amt)
	{
		// "CW10006", "[${}] 미수금액을 초과해서 회수계획을 수립할 수 없습니다."
		gfn_showAlert("CW10006", "No:"+toNumber(pRow+1));   
		gfn_SetFocusGrid(grd_list_itab, pRow, F_CHG_COLNUM);

		return false; 
	} 

	return true;
}

// 업무구분 옵션 변경 시 기준년월의 마감일자 조회
function rd_job_gbn_OnClick(obj)
{  
	// 업무구분 검색조건이 부도인 경우
	if (rd_job_gbn.Value == "2")  
	{ 
		grd_list_itab.SetColProp(F_COLNUM_LANDX, "Width", 0 );	// 국가 안보이게
		grd_list_itab.SetColProp(F_COLNUM_BDATE, "Width", 80);	// 부도일자 보이게 
	}
	else
	{
		grd_list_itab.SetColProp(F_COLNUM_LANDX, "Width", 51);	// 국가 보이게
		grd_list_itab.SetColProp(F_COLNUM_BDATE, "Width", 0 );	// 부도일자 안보이게	
	}

	// 기준년월의 마감일자 조회
	fn_query_zclose();				
}

// 기준년월의 마감일자 조회 
function fn_query_zclose()
{
	// trace("fn_query_zclose start");

	F_ZCLOSE_YN = "N";  // 마감여부 초기화	

	// 조회조건 항목오류검증
	if (fn_valid_chk("zclose") == false)  return;

	// 조회조건 설정
	ds_cond_zclose.ClearData();
	if (fn_param_set("zclose") == false)  return;  
	// trace(ds_cond_zclose.SaveXML()); 

	// 서비스 호출 (기준년월의 마감일자 조회)
	fn_createWait("",this);     // 처리중입니다. 메시지 처리
	ds_list_zclose.ClearData();	
	ds_error.ClearData();	
	nsfRequest("query"									// srvId
				, "ssa0020/find_zclose"					// url
				, "ds_cond_zclose=ds_cond_zclose ds_list_zclose=ds_list_zclose"	// inDsList
				, "ds_list_zclose=ds_list_zclose ds_error=ds_error"				// outDsList
				, ""									// strParam
				, "fn_afterQuery_zclose"				// CallBackFunc
				);
}

// 기준년월의 마감일자 조회 CALL BACK
function fn_afterQuery_zclose(errCode, errMsg) {   

	fn_destroyWait();

	// trace("fn_afterQuery_zclose 호출 start"); 

	// trace(ds_error.SaveXML());
	// trace(ds_list_zclose.SaveXML()); 

	if(ds_error.RowCount() > 0) 	// 조회실패
	{ 
		gfn_showMsgBar(ds_error.GetColumn(0,"ERRMSG")); 
	} 
	else 
	{ 

		// 조회된 자료가 있을 경우 그리드 첫번째 행에 포커스 위치시킨다.
		if (ds_list_zclose.RowCount() > 0) 
		{
			// trace("fn_afterQuery_zclose SUCCESS!!! [건수 : " + ds_list_zclose.RowCount() + "]");

			F_ZCLOSE_YN = "Y";
			ed_zclose.Text = "마감일자 : " + gfn_formatDate(ds_list_zclose.GetColumn(ds_list_zclose.row, "ZCLOSE"));

		} else {	

			// trace("fn_afterQuery_zclose SUCCESS!!! 건수없음"); 

			ed_zclose.Text = "마감일자 : " + "없음";		

			// 미수(1), 부도(2)인 경우 마감여부 체크
			if (rd_job_gbn.Value == "1" || rd_job_gbn.Value == "2" )  
			{		
				F_ZCLOSE_YN = "N";	// 마감여부 
			}
		}
	}

	// trace("fn_afterQuery_zclose 호출 end");
} 

