// 채권사유 클릭 시
function grd_list_itab_OnExpandEdit(obj,nRow,nCell,strVal,nPivotIndex)
{
	// trace("grd_list_itab_OnExpandEdit start ");
	// trace("grd_list_itab_OnExpandEdit start nCell " + nCell);

	switch (nCell)
	{
		case F_COLNUM_REASON:  // 채권사유

			// 채권사유선택 팝업화면 OPEN
			var arg = ""
					+ " fa_frm_title= " + quote("채권사유선택") 		// 팝업화면 제목
					+ " fa_cd_title= " 	+ quote("사유") 				// 팝업화면 그리드 코드 제목
					+ " fa_nm_title= " 	+ quote("사유내역") 			// 팝업화면 그리드 코드명 제목
					+ " fa_domname= " 	+ quote(GC_DOMNAME_ZFID_REASON) // GC_DOMNAME_ZFID_REASON : ZFID_REASON
					; 

			var arr = gfn_openPopUpUrlHdel("Com::Com0050.xml", true, arg );	 

			// trace("arr : " + arr); 

			if (arr != null && arr != 0)
			{
				ds_list_itab.SetColumn(ds_list_itab.row, "REASON" , arr[0]);	// 리턴된 채권사유코드로 SET
				ds_list_itab.SetColumn(ds_list_itab.row, "REASONT", arr[1]);	// 리턴된 채권사유내역으로 SET
			}

			break;

		default :
			break;

	} 


}

