function grd_list_itab2_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if ( nCell == 0 ) 
	{
		gfn_selectGridCol(obj);  
	} else { 								// 그 외 CELL 인 경우
		gfn_sortGrid(obj,nCell);			// 그리드 정렬 처리 
	} 
}

// 진행사항관리 버튼 클릭 시 (진행사항 조회,등록,수정,삭제)
function btn_mng_progress_OnClick(obj)
{  
	if (ds_list_itab.RowCount() == 0)
	{
		// CW00010 : "데이터가 없습니다."
		gfn_showAlert("CW00010");
		return false;
	}

	if (ds_list_itab.row < 0)
	{
		// CW00013 : "처리할 자료를 먼저 선택해 주십시오."
		gfn_showAlert("CW00013"); 
		return false; 
	}   

	// 진행사항관리 팝업화면 OPEN
	var arg = ""
			+ " fa_frm_title= " + quote("진행사항관리") // 팝업화면 제목
			+ " fa_kunnr	= "	+ quote(ds_list_itab.getcolumn(ds_list_itab.row, "KUNNR"	))	// 거래선번호
			+ " fa_kidno	= "	+ quote(ds_list_itab.getcolumn(ds_list_itab.row, "KIDNO"	))	// 공사번호  (프로젝트번호)
			+ " fa_belnr	= "	+ quote(ds_list_itab.getcolumn(ds_list_itab.row, "BELNR"	))	// 청구번호
			+ " fa_kidno_t	= "	+ quote(ds_list_itab.getcolumn(ds_list_itab.row, "KIDNO_T"	))	// 현장명    (프로젝트명 )
			+ " fa_name1	= "	+ quote(ds_list_itab.getcolumn(ds_list_itab.row, "NAME1"	))	// 거래선명
			+ " fa_ingtext	= "	+ quote(ds_list_itab.getcolumn(ds_list_itab.row, "INGTEXT"	))	// 진행사항문자열
			; 

	var arr = gfn_openPopUpUrlHdel("Ssa::Ssa0021.xml", true, arg );	 

	// trace("arr : " + arr); 

	if (arr != null && arr != 0)
	{
		// trace("arr != null && arr != 0"); 
		ds_list_itab.Fireevent = false;
		ds_list_itab.SetColumn(ds_list_itab.row, "INGTEXT" , arr[0]);	// 리턴된 진행사항 SET 
		ds_list_itab.Fireevent = true;
	} 

}

// 법조치관리 버튼 클릭 시 (법조치 조회,등록,수정,삭제)
function btn_mng_legal_action_OnClick(obj)
{
	var row = ds_list_itab.row;

	if (ds_list_itab.RowCount() == 0)
	{
		// CW00010 : "데이터가 없습니다."
		gfn_showAlert("CW00010");
		return false;
	}

	if (row < 0)
	{
		// CW00013 : "처리할 자료를 먼저 선택해 주십시오."
		gfn_showAlert("CW00013"); 
		return false; 
	}   

	// 진행사항관리 팝업화면 OPEN
	var arg = ""
			+ " fa_frm_title= " + quote("법조치관리") // 팝업화면 제목
			+ " fa_kidno	= "	+ quote(ds_list_itab.getcolumn(row, "KIDNO"	))	// 공사번호  (프로젝트번호)
			+ " fa_kidno_t	= "	+ quote(ds_list_itab.getcolumn(row, "KIDNO_T"	))	// 현장명    (프로젝트명 )
			; 

	var arr = gfn_openPopUpUrlHdel("Ssa::Ssa0022.xml", true, arg );	 
	// trace("arr : " + arr);  
	// trace(ds_last_law.SaveXML());  

	// 법조치결과를 그리드에 적용한다.
	if (ds_last_law.RowCount() > 0)
	{
		ds_list_itab.SetColumn(row, "MEDAT01"	, ds_last_law.GetColumn(0, "MEDAT01")	);	// 지불각서-일자	    
		ds_list_itab.SetColumn(row, "MEDAT02"	, ds_last_law.GetColumn(0, "MEDAT02")	);  // 가압류-일자	      
		ds_list_itab.SetColumn(row, "MEDAT03"	, ds_last_law.GetColumn(0, "MEDAT03")	);  // 지급명령-일자	    
		ds_list_itab.SetColumn(row, "MEDAT04"	, ds_last_law.GetColumn(0, "MEDAT04")	);  // 소송-일자	        
		ds_list_itab.SetColumn(row, "MEDAT05"	, ds_last_law.GetColumn(0, "MEDAT05")	);  // 재산명시-일자	    
		ds_list_itab.SetColumn(row, "MEDAT06"	, ds_last_law.GetColumn(0, "MEDAT06")	);  // 압류-일자	        
		ds_list_itab.SetColumn(row, "MEDAT07"	, ds_last_law.GetColumn(0, "MEDAT07")	);  // 채무不-일자	      
		ds_list_itab.SetColumn(row, "MEDAT08"	, ds_last_law.GetColumn(0, "MEDAT08")	);  // 주민말소-일자	    
		ds_list_itab.SetColumn(row, "DEBTRT01"	, ds_last_law.GetColumn(0, "DEBTRT01")	);  // 지불각서-채무자 	
		ds_list_itab.SetColumn(row, "DEBTRT02"	, ds_last_law.GetColumn(0, "DEBTRT02")	);  // 가압류-채무자 	  
		ds_list_itab.SetColumn(row, "DEBTRT03"	, ds_last_law.GetColumn(0, "DEBTRT03")	);  // 지급명령-채무자 	
		ds_list_itab.SetColumn(row, "DEBTRT04"	, ds_last_law.GetColumn(0, "DEBTRT04")	);  // 소송-채무자 	    
		ds_list_itab.SetColumn(row, "DEBTRT05"	, ds_last_law.GetColumn(0, "DEBTRT05")	);  // 재산명시-채무자 	
		ds_list_itab.SetColumn(row, "DEBTRT06"	, ds_last_law.GetColumn(0, "DEBTRT06")	);  // 압류-채무자 	    
		ds_list_itab.SetColumn(row, "DEBTRT07"	, ds_last_law.GetColumn(0, "DEBTRT07")	);  // 채무不-채무자 	  
		ds_list_itab.SetColumn(row, "DEBTRT08"	, ds_last_law.GetColumn(0, "DEBTRT08")	);  // 주민말소-채무자 	
		ds_list_itab.SetColumn(row, "QQ_CNT01"	, ds_last_law.GetColumn(0, "QQ_CNT01")	);  // 지불각서-건수	    
		ds_list_itab.SetColumn(row, "QQ_CNT02"	, ds_last_law.GetColumn(0, "QQ_CNT02")	);  // 가압류-건수	      
		ds_list_itab.SetColumn(row, "QQ_CNT03"	, ds_last_law.GetColumn(0, "QQ_CNT03")	);  // 지급명령-건수	    
		ds_list_itab.SetColumn(row, "QQ_CNT04"	, ds_last_law.GetColumn(0, "QQ_CNT04")	);  // 소송-건수	        
		ds_list_itab.SetColumn(row, "QQ_CNT05"	, ds_last_law.GetColumn(0, "QQ_CNT05")	);  // 재산명시-건수	    
		ds_list_itab.SetColumn(row, "QQ_CNT06"	, ds_last_law.GetColumn(0, "QQ_CNT06")	);  // 압류-건수	        
		ds_list_itab.SetColumn(row, "QQ_CNT07"	, ds_last_law.GetColumn(0, "QQ_CNT07")	);  // 채무不-건수	      
		ds_list_itab.SetColumn(row, "QQ_CNT08"	, ds_last_law.GetColumn(0, "QQ_CNT08")	);  // 주민말소-건수	    
	}

} 

// 데이타셋 행 자료가 변경되었을 경우
function ds_list_itab_OnRowPosChanged(obj,nOldRow,nRow)
{ 
	// 변경된 행에 대해서만 체크함	
	if (obj.GetColumn(nOldRow, "FLAG") <> "U") return;   

	// F_CHG_ARR에 선언된 컬럼에 대해서만 변경여부 체크 (Y) 
	var ChgYn = gfn_GetDatasetChgYN(grd_list_itab, nOldRow, F_CHG_ARR, "Y");

	// 변경된 자료가 없으면 선택여부,변경구분 초기화
	if (ChgYn == "N")  
	{
		gfn_SetDatasetChgFlag(obj, nOldRow); 
	}

}
