/********************************************************************************
* form loadCompleted 
********************************************************************************/
function fn_OnLoadCompleted(obj) 
{
	gfn_initForm(true);	// Form 로드 이벤트 맨 첫줄에 기술
	fn_init_form();			// 폼 초기화
}

/********************************************************************************
* form UnloadCompleted 
********************************************************************************/
function fn_OnUnloadCompleted(obj)
{
	return gfn_isFormUnload(obj); // Tab에서 해당 화면 삭제
}

/********************************************************************************
* 기      능   : 폼 초기화
********************************************************************************/
function fn_init_form()
{
	cal_datefr.Value = addDate(gfn_getCurrDate(), -10);	// 현재일자 10일전의 일자부터
	cal_dateto.Value = gfn_getCurrDate();

	//코드콤보 설정
	var codeArr    = ["VKBUR",   "VKGRP"];
	var dsObjArr   = [ds_vkbur, ds_vkgrp];
	var addTypeArr = [G_CODE_ALL];
	gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);

	// 사무소, PM 콤보 목록 가져오기
	tit_clearActionInfo();
	tit_addSearchActionInfo("ps02:PS0201003A_S41");
	tit_addSearchActionInfo("ps02:PS0201003A_S42");
	tit_callService(
		""
		, ""
		, "ds_cond=ds_cond"
		, "ds_zzactss_cd=ds_zzactss_cd ds_zzpmnum_cd=ds_zzpmnum_cd"
		, ""
		, "fn_afterQuery40"
		, true,true);

	// 공통코드 콤보목록 조회
 	fn_query_DD07T("ZDLYBIGO", ds_zdlybigo); // 수금지연사유
}


//=========================================================================================
// [ PART 3 ]
// Main Process Event 정의(조회,입력,삭제,저장,인쇄,form닫기)
//=========================================================================================
// 공통코드 조회
function fn_query_DD07T(pDomainId, pDs)
{  
	// 데이타셋 초기화
	ds_cond_dd07t.ClearData();	
	pDs.ClearData();

	// 데이타셋 설정
	ds_cond_dd07t.AddRow();
	ds_cond_dd07t.SetColumn(ds_cond_dd07t.row, "DOMNAME"	, pDomainId);  
	ds_cond_dd07t.SetColumn(ds_cond_dd07t.row, "CONCAT_YN"	, "N");  

	// 서비스 호출
	nsfRequest(  "query"
			   , "combo/comboDD07T"
			   , "ds_cond_dd07t=ds_cond_dd07t " + pDs.ID+"="+pDs.ID
			   , pDs.ID+"="+pDs.ID +" ds_error=ds_error"
			   , ""
			   , "fn_afterQuery_DD07T_"+pDomainId);
}

/********************************************************************************
* 기      능   : 조회
********************************************************************************/
var fn_query_chk = false; //중복조회 방지용 flag

function fn_query()
{
	var datefr = cal_datefr.Value;
	var dateto = cal_dateto.Value;

	if ( datefr == null || datefr == "" ) { gfn_showAlert("CW00208", "조회기간");     return false; } // [${}]은/는 필수입력 입니다. 확인바랍니다.
	if ( dateto == null || dateto == "" ) { gfn_showAlert("CW00208", "조회기간");     return false; } // [${}]은/는 필수입력 입니다. 확인바랍니다.

	var arr = ['조회기간', '12'];
	if(gfn_getDayCnt(ds_days, cal_datefr.Value, cal_dateto.Value) > 365) {
		gfn_showAlert("CW10087", arr);
		cal_datefr.SetFocus();
		return false;
	}

	if(!fn_query_chk) {
		fn_query_chk = true;
	} else {
		gfn_showAlert("CW10025", "조회중입니다..");
		return;
	}	

	ds_cond.ClearData();
	ds_cond.AddRow();
	ds_cond.SetColumn(0, "MANDT"	, G_MANDT);
	ds_cond.SetColumn(0, "DATEGBN"	, rd_dategbn.Value);
	ds_cond.SetColumn(0, "DATEFR"	, cal_datefr.Value);
	ds_cond.SetColumn(0, "DATETO"	, cal_dateto.Value);
	ds_cond.SetColumn(0, "PSPID"	, ed_pspid.Text);
	ds_cond.SetColumn(0, "ZZACTSS"	, cb_zzactss.Value);
	ds_cond.SetColumn(0, "ZZPMNUM"	, cb_zzpmnum.Value);
	ds_cond.SetColumn(0, "SAID"		, div_manager.ed_sman_cd.text);
	ds_cond.SetColumn(0, "ASUGM"	, chk_a.Value);
	ds_cond.SetColumn(0, "BSUGM"	, chk_b.Value);
	ds_cond.SetColumn(0, "CSUGM"	, chk_c.Value);

	// sort mark 삭제
	gfn_clearGridSortMark(grd_list);

	// 출력데이타셋 초기화
	ds_list.ClearData();
	ds_error.ClearData();

	fn_createWait("",this); // 처리중입니다. 메시지 처리

	nsfRequest("query"													// svcId
				, "ssa0070/find"										// url
				, "ds_cond=ds_cond ds_list=ds_list"						// biz에서 받는 명, mip에서 보내는 명
				, "ds_list=ds_list ds_error=ds_error"					// mip에서 받는 명, biz에서 보내는 명
				, ""													// strParam
				, "fn_afterQuery"										// CallbackFunc
	);
}

/********************************************************************************
* 기      능   : 저장
********************************************************************************/
function fn_save()
{
	// 변경된 정보 있는지 체크
	if ( ! ds_list.GetUpdate() ) {
		gfn_showAlert("변경된 정보가 없습니다.");
		return;
	}

	if ( !gfn_showConfirm("CI00024", "[저장]") ) { // [저장]을 하시겠습니까?
		return;
	}

	// sort mark 삭제
	gfn_clearGridSortMark(grd_list);

	// 출력데이타셋 초기화
	ds_error.ClearData();

	fn_createWait("",this); // 처리중입니다. 메시지 처리

	// 저장을 위한 임시 데이터셋 생성
	destroy("ds_save_list");
	create("dataset", "ds_save_list");
	ds_save_list.copy(ds_list);
	ds_save_list.clearData();

	// 변경된 자료만 추출
	grd_list.Redraw = false;
	ds_list.Filter("STATUS=='U'");
	ds_save_list.copyF(ds_list);
	ds_list.UnFilter();
	grd_list.Redraw = true;

	nsfRequest("save"									// svcId
				, "ssa0070/save"						// url
				, "ds_list=ds_save_list"				// biz에서 받는 명, mip에서 보내는 명
				, "ds_error=ds_error"					// mip에서 받는 명, biz에서 보내는 명
				, ""									// strParam
				, "fn_afterQuery"						// CallbackFunc
	);
}

