// 공통코드 조회 CALL BACK
function fn_afterQuery_DD07T_ZDLYBIGO(errCode, errMsg) 
{  
	gfn_InsertRowCombo(ds_zdlybigo, 0, "Y", "Y"); // 선택 행 추가
}

/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery40(errCode, errMsg) {
	//코드콤보 설정 : 사무소 전체표시
	gfn_insertEmptyRow( ds_zzactss_cd, G_CODE_ALL );
	cb_zzactss.value = "";
	gfn_insertEmptyRow( ds_zzpmnum_cd, G_CODE_ALL );
	cb_zzpmnum.value = "";
}

/********************************************************************************
* 기      능   : 조회/저장 Callback
********************************************************************************/
function fn_afterQuery(svcId, errCode, errMsg)
{
	fn_query_chk = false; //중복조회 방지용 flag
	fn_destroyWait();

	if ( ds_error.GetRowCount() > 0 ) {
		gfn_showAlert("CE00001");
		return;
	}

	if ( svcId == "query" ) {
		if ( ds_list.GetRowCount() > 0 ) {
			gfn_showMsg("CI00002", ds_list.RowCount()+""); // CI00002 : "조회를 완료했습니다.조회건수 [${}건]"
		} else {
			gfn_showMsg("CW00010"); // CW00010 : "데이터가 없습니다."
		}
	} else if ( svcId == "save" ) {
		gfn_showAlert("CI00006", "[저장]"); // ${} 처리를 완료했습니다.

		fn_query();
	}
}

