/********************************************************************************
* 기      능   : 데이터셋 변경시 처리
********************************************************************************/
function ds_list_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if ( strColumnID != "STATUS" ) {
		obj.SetColumn(nRow, "STATUS", decode(obj.GetRowType(nRow), "update", "U", ""));
	}
}

/********************************************************************************
* 기      능   : 그리드 헤더 클릭시 정렬 처리 
********************************************************************************/
function grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex) 
{
	if ( nCell == 0 ) {
		gfn_selectGridCol(obj); // 컬럼 display 여부
	} else {
		gfn_sortGrid(obj, nCell); // 그리드 정렬 처리
	}
}

/********************************************************************************
* 기      능   : 그리드 영역 클릭시
********************************************************************************/
function grd_list_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	grd_list.redraw = false;
	obj.setCellProp("body",obj.getBindCellIndex("body", "ASUGM_DT"),"expandshow",false);
	obj.setCellProp("body",obj.getBindCellIndex("body", "BSUGM_DT"),"expandshow",false);
	obj.setCellProp("body",obj.getBindCellIndex("body", "CSUGM_DT"),"expandshow",false);
	if (   nCell == obj.getBindCellIndex("body", "ASUGM_DT")
		|| nCell == obj.getBindCellIndex("body", "BSUGM_DT")
		|| nCell == obj.getBindCellIndex("body", "CSUGM_DT")
	) {
		obj.setCellProp("body",nCell,"expandshow",true);
		obj.setCellProp("body",nCell,"expandshow",true);
		obj.setCellProp("body",nCell,"expandshow",true);
	}
	grd_list.redraw = true;
}

// 달력 처리 부분 
/********************************************************************************
* 기      능   : 그리드에서 날짜 입력시
********************************************************************************/
function grd_list_OnExpandEdit(obj,nRow,nCell,strVal,nPivotIndex)
{
	F_SELECTED_COLID = obj.GetCellProp("body", nCell, "ColId");

	var rect = obj.GetCellRect(nRow, ncell);
	cal_temp.top = obj.Top + rect[1] + 20;
	cal_temp.Left = rect[0] - 50; 
	cal_temp.Visible = true;
	cal_temp.value = ds_list.GetColumn(nRow, F_SELECTED_COLID);
	cal_temp.SetFocus();
}

/********************************************************************************
* 기      능   : 달력에서 일자 선택했을 경우
********************************************************************************/
function cal_temp_OnDayClick(obj,strText)
{
	ds_list.SetColumn(ds_list.row, F_SELECTED_COLID, strText);
	obj.Visible = false;
}

/********************************************************************************
* 기      능   : 달력에서 포커스 잃었을 경우
********************************************************************************/
function cal_temp_OnKillFocus(obj)
{
	obj.Visible = false;
}

function cb_zzactss_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	cb_zzpmnum.value = "";
	if ( strCode == "") {
		ds_zzpmnum_cd.filter("ZZACTSS ==''");
	} else {
		ds_zzpmnum_cd.filter("ZZACTSS == " + quote( strCode) );
	}
}

function fn_delay(sDate) {
trace(sDate);
	var nDate = toNumber(sDate);
trace(nDate);
	return iif(toNumber(ASUGM_DT)>19000101,toNumber(today())-toNumber(ASUGM_DT),"");
}
