/********************************************************************************
* 기      능   : 조회 1
********************************************************************************/
var fn_query_chk = false; //중복조회 방지용 flag

function fn_query() {

	var arr = ['수주년월', '12'];
	if(gfn_getDayCnt(ds_days, cal_frymd.Value, cal_toymd.Value) > 365) {
		gfn_showAlert("CW10087", arr);
		cal_frymd.SetFocus();
		return false;
	}

	// 수주년월
	if ( cal_frymd.Value == '' or cal_frymd.Value == null)
	{
		if ( cal_toymd.Value == '' or cal_toymd.Value == null)	{
			gfn_ShowAlert("CW00208", "수주년월");
			gfn_SetFocus(cal_toymd, "Y");
			return;
		}
	}

	// 부서정보 필수 확인
	/*if ( div_vbvg.cb_frvb.Value == '' or div_vbvg.cb_frvb.Value == null)
	{
		if ( div_vbvg.cb_tovb.Value == '' or div_vbvg.cb_tovb.Value == null)	{
			gfn_ShowAlert("CW00208", "부서");
			gfn_SetFocus(div_vbvg.cb_frvb, "Y");
			return;
		}
	}*/

	if(!fn_query_chk) {
		fn_query_chk = true;
	} else {
		gfn_showAlert("CW10025", "조회중입니다..");
		return;
	}

	F_VBELN = "";

	ds_cond.ClearData();
	ds_cond.AddRow();

	ds_cond.SetColumn(0, "FR_SO",  ed_frso.Value);
	ds_cond.SetColumn(0, "FR_VB",  div_vbvg.cb_frvb.Value);
	ds_cond.SetColumn(0, "FR_VG",  div_vbvg.cb_frvg.Value);
	ds_cond.SetColumn(0, "FR_YMD", cal_frymd.Value);
	ds_cond.SetColumn(0, "SMAN",   div_sman.ed_sman_cd.Value);

	ds_cond.SetColumn(0, "TO_SO",  ed_toso.Value);	
	ds_cond.SetColumn(0, "TO_VB",  div_vbvg.cb_tovb.Value);
	ds_cond.SetColumn(0, "TO_VG" , div_vbvg.cb_tovg.Value);
	ds_cond.SetColumn(0, "TO_YMD", cal_toymd.Value);

	// 서비스 호출
	nsfRequest("query"									// srvId
				, "sso0030/find"						// url
				, "ds_cond=ds_cond"						// inDsList
				, "ds_list=ds_list ds_error=ds_error"	// outDsList
				, ""									// strParam
				, "fn_afterQuery"						// CallBackFunc
				);

    fn_createWait("",this);     // 처리중입니다. 메시지 처리
}

