/********************************************************************************
* 기      능   :  기안 수주통보서 호출
********************************************************************************/
function btn_drft_OnClick(obj)
{
    var demand_da = ds_list.GetColumn(ds_list.row, "DEMAND_DA");
    var recad_da = ds_list.GetColumn(ds_list.row, "RECAD_DA"); 
    var chade_da = ds_list.GetColumn(ds_list.row, "CHADE_DA"); 
    var chaad_da = ds_list.GetColumn(ds_list.row, "CHAAD_DA"); 

    if ( ( demand_da > "0000-00-00" && ( recad_da = "    -  -  " || recad_da = "0000-00-00" ) ) ||
       ( chade_da  > "0000-00-00" && ( chaad_da = "    -  -  " || chaad_da = "0000-00-00" ) ) ){

		alert("이미 기안한 상태입니다");
		return;
    }       

	// 선택된 정보 확인 후 처리
	if (F_VBELN == "") {
		gfn_showAlert("CW00116", "기안 (수주통보서) 대상을");
		return;
	}

	var varUrl = F_URL + "&ino=" + F_VBELN +"&param=order";
	// var varUrl = F_URL + "&param=order&ino=" + F_VBELN;		//021.05.21 그룹웨어 리뉴얼
	ExecBrowser(varUrl);

	// RFC 호출을 방식을 브러우져 호출 방식으로변경 2012.10.17
	//F_CMD_FLAG = "DRFT";
	//fn_process(F_CMD_FLAG);
}


