/********************************************************************************
* 기      능   :  수주통보서(인쇄) call 
********************************************************************************/
function fn_print() {

	if (F_VBELN == "") {
		gfn_showAlert("CW00116", "수주통보서 인쇄 대상을");
		return;
	}

    fn_createWait("",this);     // 처리중입니다. 메시지 처리

	ds_cond.ClearData();
	ds_cond.AddRow();

	ds_cond.SetColumn(0, "VBELN",  F_VBELN);

	dsBILLCON.ClearData();
	dsBOTTINFO.ClearData();
	dsCONCON.ClearData();
	dsCOSTINFO.ClearData();
	dsCOSTINFO2.ClearData();
	dsHEAD.ClearData();
	dsO_TAB.ClearData();
	dsSOINFO.ClearData();
	dsSOINFO1.ClearData();
	dsSUBTO.ClearData();
	dsSUBTO1.ClearData();
	dsTEXT.ClearData();
	dsTOTAL.ClearData();
	dsT_ADD_DATA.ClearData();
	dsSPDRFTINFO.ClearData();

	// 서비스 호출
	nsfRequest("print"									// srvId
				, "sso0030/print"						// url
				, "ds_cond=ds_cond dsBILLCON=dsBILLCON dsBOTTINFO=dsBOTTINFO dsCONCON=dsCONCON dsCOSTINFO=dsCOSTINFO dsCOSTINFO2=dsCOSTINFO2 dsHEAD=dsHEAD dsO_TAB=dsO_TAB dsSOINFO=dsSOINFO dsSOINFO1=dsSOINFO1 dsSUBTO=dsSUBTO dsSUBTO1=dsSUBTO1 dsTEXT=dsTEXT dsTOTAL=dsTOTAL dsT_ADD_DATA = dsT_ADD_DATA dsSPDRFTINFO = dsSPDRFTINFO"		// inDsList
				, "dsBILLCON=dsBILLCON dsBOTTINFO=dsBOTTINFO dsCONCON=dsCONCON dsCOSTINFO=dsCOSTINFO dsCOSTINFO2=dsCOSTINFO2 dsHEAD=dsHEAD dsO_TAB=dsO_TAB dsSOINFO=dsSOINFO dsSOINFO1=dsSOINFO1 dsSUBTO=dsSUBTO dsSUBTO1=dsSUBTO1 dsTEXT=dsTEXT dsTOTAL=dsTOTAL dsT_ADD_DATA = dsT_ADD_DATA dsSPDRFTINFO = dsSPDRFTINFO"	// outDsList
				, ""									// strParam
				, "fn_afterPrint"						// CallBackFunc
				);
	// 헤더 정보(행수고정)			R3HEAD			HEAD
	// 계약조건 정보(행수고정)		R3CONCON		CONCON
	// 수금조건 (행수고정)			R3BILLCON		BILLCON
	// 바닥영역 정보 (행수고정)		R3BOTTINFO		SOINFO
	// 특기사항 정보 (행수가변)		R3TEXT			COSTINFO
	// 예상원가 세부사항(행수가변)	R3COSTINFO		TEXT
	// 수주내역(행수가변)-일식외주	R3SOINFO1		BOTTINFO
	// 수주내역(행수가변)-일반기종	R3SOINFO		SOINFO1

}

