// Callback 함수 정의 : callback 함수명은 메인 함수의 fn_xxxx() 에서 xxxx 부분은 fn_afterXXXX()
// 형태가 되도록 작성 
//=========================================================================================
/********************************************************************************
* 기      능   :  Callback  
********************************************************************************/
function fn_afterQuery(svcId, errCode, errMsg) {

	fn_query_chk = false; //중복조회 방지용 flag
	fn_destroyWait();

	// 2012.10.16 변경
	if(svcId == "query") {
		if(ds_error.rowcount() > 0) {
			if (ds_error.GetColumn(0, "ERRCODE") == "NO" )	{
				gfn_showMsg("CW00010");		// 데이터가 없습니다.
				F_SELECT_ROW = 0;
			} else	{
				gfn_showMsg("CE00001");
			}
			return;
		} else {
			gfn_showMsg("CI00002", ds_list.RowCount() + "");
			// 저장 시 선택된 위치 이동
			if(F_SELECT_ROW != "") {
				ds_list.row =  F_SELECT_ROW;
			}
			F_VBELN = ds_list.GetColumn(ds_list.row, "VBELN");
		}	
	}	
}

