/********************************************************************************
* 기      능   : 리포트 Callback  
********************************************************************************/
function fn_afterPrint(svcId, errCode, errMsg) {

	fn_destroyWait();

	// 수주내역
	for ( var i = 0 ; i < dsSOINFO.GetRowCount() ; i++ ) {
		dsSOINFO.SetColumn(i, "TPCOST", replace(dsSOINFO.GetColumn(i, "KWERTT"), ",", ""));
		dsSOINFO.SetColumn(i, "MONEY" , replace(dsSOINFO.GetColumn(i, "NETWRT"), ",", ""));
	}
	// 일식외주
	for ( var i = 0 ; i < dsSOINFO1.GetRowCount() ; i++ ) {
		dsSOINFO1.SetColumn(i, "TPCOST", replace(dsSOINFO1.GetColumn(i, "KWERTT"), ",", ""));
		dsSOINFO1.SetColumn(i, "MONEY" , replace(dsSOINFO1.GetColumn(i, "NETWRT"), ",", ""));
		dsSOINFO1.SetColumn(i, "PCOST" , replace(dsSOINFO1.GetColumn(i, "PWERTT"), ",", ""));
	}
	// 계약조건
	for ( var i = 0 ; i < dsCONCON.GetRowCount() ; i++ ) {
		dsCONCON.SetColumn(i, "KWERT", replace(dsCONCON.GetColumn(i, "KWERTT"), ",", ""));
		dsCONCON.SetColumn(i, "NETWR", replace(dsCONCON.GetColumn(i, "NETWRT"), ",", ""));
	}

	var arg = "";

	dsSUMMARY1.DeleteAll();
	dsSUMMARY1.AddRow();
	dsSUMMARY1.SetColumn(0,"SUMMARY1",grdSOINFO.GetCellText("summary",-1,1));
	dsSUMMARY1.SetColumn(0,"SUMMARY2",grdSOINFO.GetCellText("summary",-1,2));
	dsSUMMARY1.SetColumn(0,"SUMMARY3",grdSOINFO.GetCellText("summary",-1,3));

	dsSUMMARY2.DeleteAll();
	dsSUMMARY2.AddRow();
	dsSUMMARY2.SetColumn(0,"SUMMARY1",grdSOINFO1.GetCellText("summary",-1,1));
	dsSUMMARY2.SetColumn(0,"SUMMARY2",grdSOINFO1.GetCellText("summary",-1,2));
	dsSUMMARY2.SetColumn(0,"SUMMARY3",grdSOINFO1.GetCellText("summary",-1,3));
	dsSUMMARY2.SetColumn(0,"SUMMARY4",grdSOINFO1.GetCellText("summary",-1,4));
	dsSUMMARY2.SetColumn(0,"SUMMARY5",grdSOINFO1.GetCellText("summary",-1,5));

	var sum1 = 0;
	var sum2 = 0;
	var sum3 = 0;
	var sum4 = 0;
	var sum5 = 0;
	sum1 = dsSUMMARY1.GetColumn(0,"SUMMARY1") + dsSUMMARY2.GetColumn(0,"SUMMARY1");
	sum2 = dsSUMMARY1.GetColumn(0,"SUMMARY2") + dsSUMMARY2.GetColumn(0,"SUMMARY2");
	sum3 = sum1 / sum2 * 100;
	sum4 = dsSUMMARY2.GetColumn(0,"SUMMARY4");
	sum5 = sum4 / sum2 * 100;

	dsSUMMARY3.DeleteAll();
	dsSUMMARY3.AddRow();
	dsSUMMARY3.SetColumn(0,"SUMMARY1",sum1);
	dsSUMMARY3.SetColumn(0,"SUMMARY2",sum2);
	dsSUMMARY3.SetColumn(0,"SUMMARY3",sum3);
	dsSUMMARY3.SetColumn(0,"SUMMARY4",sum4);
	dsSUMMARY3.SetColumn(0,"SUMMARY5",sum5);

	dsCONCON.AddColumn("ACHNO","STRING", 255);
	dsCONCON.AddColumn("ACHTP","STRING", 255);
	dsCONCON.AddColumn("ACHDT","STRING", 255);
	dsCONCON.AddColumn("VIPTP","STRING", 255);
	dsCONCON.AddColumn("LIFNRCHKT","STRING", 255);

	dsCONCON.SetColumn(0, "ACHNO", dsHEAD.GetColumn(0,"ACHNO"));	
	dsCONCON.SetColumn(0, "ACHTP", dsHEAD.GetColumn(0,"ACHTP"));	
	dsCONCON.SetColumn(0, "ACHDT", dsHEAD.GetColumn(0,"ACHDT"));	
	dsCONCON.SetColumn(0, "VIPTP", dsHEAD.GetColumn(0,"VIPTP"));	
	dsCONCON.SetColumn(0, "LIFNRCHKT", dsHEAD.GetColumn(0,"LIFNRCHKT"));	

	Dialog("SSO::Sso0031.xml", arg, -1, -1, "Resize=true");
}

