}

/********************************************************************************
* 기      능   : 수주번호(SO번호)가 존재하는 경우 수금관리대장 상세 호출
********************************************************************************/
function grd_list_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if (ds_list.row < 0) return;

	//var soNo = ds_list.getcolumn(ds_list.row, "VBELN");
	var soNo = obj.GetCellValue(nRow, 1);

	// soNo는 원 데이터는 6자리 4번째의 위치가 숫자의 경우 앞에 '0000'으로 채워지며
	// 영문자의 경우는 6자리 고정 임.
	if ( IsDigit( Mid(soNo, 4, 1) ) )
		soNo = Right(soNo, 6);		// 숫자의 경우 '0000'을 제외하고 전달

	var arg = "fa_zzpjt_id = " + quote( soNo );  //  SO번호

	if (substr(soNo, 0, 1) == 'E' or substr(soNo, 0, 1) == 'C' or substr(soNo, 0, 1) == 'T')
	{
	  return gfn_openPopUpUrlHdel("Scl::Scl0043.xml", true, arg );
	}
	else
	{
	  return gfn_openPopUpUrlHdel("Scl::Scl0041.xml", true, arg );
	}
}

/********************************************************************************
* 기      능   :  일자 Data 변환
********************************************************************************/
function fn_dateValueChange(obj, nRow, orgStr, toStr)
{
	obj.SetColumn(nRow, "CONTR_DA",  Trim(Replace( obj.GetColumn(nRow, "CONTR_DA"), orgStr, toStr ) ) );
	obj.SetColumn(nRow, "VDATU",     Trim(Replace( obj.GetColumn(nRow, "VDATU"), orgStr, toStr ) ) );
	obj.SetColumn(nRow, "BSTDK",     Trim(Replace( obj.GetColumn(nRow, "BSTDK"), orgStr, toStr ) ) );
	obj.SetColumn(nRow, "DEMAND_DA", Trim(Replace( obj.GetColumn(nRow, "DEMAND_DA"), orgStr, toStr ) ) );
	obj.SetColumn(nRow, "RECAD_DA",  Trim(Replace( obj.GetColumn(nRow, "RECAD_DA"), orgStr, toStr ) ) );
	obj.SetColumn(nRow, "CHADE_DA",  Trim(Replace( obj.GetColumn(nRow, "CHADE_DA"), orgStr, toStr ) ) );
	obj.SetColumn(nRow, "CHAAD_DA",  Trim(Replace( obj.GetColumn(nRow, "CHAAD_DA"), orgStr, toStr ) ) );
	obj.SetColumn(nRow, "AEDAT",     Trim(Replace( obj.GetColumn(nRow, "AEDAT"), orgStr, toStr ) ) );
	obj.SetColumn(nRow, "STADA",     Trim(Replace( obj.GetColumn(nRow, "STADA"), orgStr, toStr ) ) );
}






