//  선택 항목 오류 검증
function fn_valid_chk(pProcGb)
{   
	var arr = "";

 	switch(pProcGb)
	{
		case "query" :   
			if (length(cal_yearm.Value) == 0)
			{
				// CW00208 : "[${}]은/는 필수입력 입니다.확인바랍니다.")
				gfn_showAlert("CW00208","기준년월");
				gfn_SetFocus(cal_yearm,"Y"); 
				return false;
			}  

			//미수(1), 부도(2)인 경우 마감일자 없으면 오류처리
			if (rd_job_gbn.Value == "1" || rd_job_gbn.Value == "2")
			{
				if (F_ZCLOSE_YN == "N")
				{
					// "CW10004", "${}은(는) ${}되지 않아 ${} 할 수 없습니다."
					arr = [(substr(cal_yearm.Value,0,4)+"년 "+substr(cal_yearm.Value,4,2)+"월", "마감", "조회"]; 
					gfn_showAlert("CW10004", arr);
					gfn_SetFocus(cal_yearm,"Y"); 
					return false;
				}
			}
			break;  

		case "excel" :   // 엑셀

			if (rd_job_gbn.Value == "1" || rd_job_gbn.Value == "2")	// 미수/부도
			{
				if (ds_list.RowCount() <=0)
				{
					// CW00010 : "데이터가 없습니다."
					gfn_showAlert("CW00010");
					return false;
				} 

				// 변경된 내역이 존재할 경우 엑셀출력 불가처리
				if(gfn_isChangeDs(ds_list, false, "FLAG", rd_job_gbn.text)== true) 
				{  
					//"CW10003", "변경된 ${}이 존재하여 ${}이 불가능합니다. 변경된 정보를 저장후 다시 실행하여 주십시오.");  // 2012.06.13 : GRY : 추가
					arr = [rd_job_gbn.text, "엑셀출력"];
					gfn_showAlert("CW10003", arr);
				}  


			}
			else if (rd_job_gbn.Value == "3")  // 수금계획
			{
				if (ds_list.RowCount() <=0)
				{
					// CW00010 : "데이터가 없습니다."
					gfn_showAlert("CW00010");
					return false;
				}
			}
			break; 

		case "update_plan":   	// 기안 (수주통보서)

			// 선택된 행 존재여부 체크
			if (ds_list.FindRow("CHK",1) < 0)
			{ 
				// CW00013 : "처리할 자료를 먼저 선택해 주십시오."
				gfn_showAlert("CW00013"); 
				return false; 
			} 

			// 변경된 내역 존재여부 체크  
			if(gfn_isChangeDs(ds_list, true, "FLAG", "기안 (수주통보서)")== false) 
			{ 
				return false; 
			}    

		    ds_list_param.ClearData();

			for (var i=0; i<=ds_list.RowCount(); i++)
			{ 
			    if ( ds_list.getColumn(i, "CHK") == 1) { 

					var nRow =  ds_list_param.addRow(); 
					ds_list_param.CopyRow(nRow, ds_list_param, i); 
				}
			}

			break;

		case "update_reason" :  // 채권사유저장

			// 선택된 행 존재여부 체크
			if (ds_list.FindRow("CHK",1) < 0)
			{	
				// CW00013 : "처리할 자료를 먼저 선택해 주십시오."
				gfn_showAlert("CW00013"); 
				return false; 
			}

			// 변경된 내역 존재여부 체크 
			if(gfn_isChangeDs(ds_list, true, "FLAG", rd_job_gbn.Text + "채권사유")== false) 
			{ 
				return false; 
			}   

			break;

		default: 
			break; 
	}   

	return true;
}




