//=========================================================================================
// Form Loading 
//=========================================================================================
function fn_OnLoadCompleted(obj) 
{
	// Button 정보 loading 시키기 
	div_btn.fn_loadUseBtn('', ParentTitle+'품목사양값 일괄복사[sso0051]', '', '');
	// focus
	tabCopy.tabHogi.ed_pspHogi.SetFocus();

	// 그리드 설정
	fn_GridSet();	

	// [복사]버튼 활성여부
	btn_copy.Enable = false;
}

/********************************************************************************
* form UnloadCompleted 
********************************************************************************/
function fn_OnUnloadCompleted(obj) {

	// 부모창의 [호기]탭에서 호출했을경우는 호기탭으로
	// [사양]탭에서 호출했을경우에는 사양탭으로
	if(parent.Tab00.TabIndex == '1' && length(parent.Tab00.tab2.ed_posnr.text) != 0) {
		parent.Tab00.TabIndex = 2;
	} else {
		parent.Tab00.TabIndex = 1;
	}

	// Tab에서 해당 화면 삭제
	return gfn_isFormUnload(obj);
}


/********************************************************************************
* 조회 
********************************************************************************/
function fn_queryP() { 
	// 원본대상 그리드 초기화
	tabCopy.tabHogi.Grid0.BindDataset = "";
	tabCopy.tabMo.grdTempList.BindDataset = "";

	ds_result.ClearData();

	var hogiPsp = tabCopy.tabHogi.ed_pspHogi.text;
	var param;

	ds_output_ZSDS0016.ClearData(); // 2013.03.21 이전 조회결과 초기화

	var output = "ds_output_ZSDS0016=ds_output_ZSDS0016 ds_output_ZSDS0017=ds_output_ZSDS0017";

	if(tabCopy.TabIndex == 0) {
		// 필수 체크 - [프로젝트]
		if (( length(hogiPsp) == 0)) {
			//"[${}] 을/를 확인하십시요."
			gfn_showAlert("CW00022", "호기별프로젝트번호");
			tabCopy.tabHogi.ed_pspHogi.SetFocus();
			return;
		}

		param = "pspHogi="+quote(hogiPsp)+" fa_seq="+quote(fa_seq)+" tab_index="+quote(tabCopy.TabIndex);
	} else if(tabCopy.TabIndex == 1) {
		var qt = trim(tabCopy.tabMo.ed_qtnum.text) + trim(tabCopy.tabMo.ed_qtser.text) + trim(tabCopy.tabMo.ed_qtseq.text);

		// 필수 체크 - [견적번호]
		if(length(qt) == 0 ) {
			//"[${}] 을/를 확인하십시요."
			gfn_showAlert("CW00022", "견적변호");
			tabCopy.tabMo.ed_qtnum.SetFocus();
			return;
		}

		param = "pspHogi="+quote(qt)+" fa_seq=''"+" tab_index="+quote(tabCopy.TabIndex);
	}	

	// Transaction
	callServiceN("query"			// srvId
				, "sso0051n/findQ"	// url
				, output			// biz에서 받는 명, mip에서 보내는 명
				, output			// mip에서 받는 명, biz에서 보내는 명
				, param				// strParam
				, ""				// CallBackFunc
				);
}


/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(strSvcID, nErrorCode, strErrorMag) { 
	if(nErrorCode < 0) {
		//  CE00001 : "처리에 실패했습니다.\n확인 후 다시 처리해 주십시오.");
		gfn_showMsg("CE00001");
	} else {
		if(strSvcID == "query") {
			tabCopy.tabHogi.ed_tempHogi.Text = "";
			tabCopy.tabMo.ed_tempMo.Text = "";

			if(tabCopy.TabIndex == 0) {
				if(ds_output_ZSDS0016.GetRowCount() > 0) {
					// CI00002 : "조회를 완료했습니다.조회건수 [${}건]"
					gfn_showMsg("CI00002", ds_output_ZSDS0016.RowCount() + ""); 

					// 그리드 설정
					fn_sayangGridSet();
					tabCopy.tabHogi.Checkbox0.Enable = true;
					tabCopy.tabHogi.Checkbox1.Enable = true;
					tabCopy.tabHogi.Checkbox2.Enable = true;
					tabCopy.tabHogi.Checkbox3.Enable = true;
					tabCopy.tabHogi.Checkbox4.Enable = true;
					tabCopy.tabHogi.Checkbox5.Enable = true;
					tabCopy.tabHogi.Checkbox6.Enable = true;

					// [복사]버튼 활성여부
					btn_copy.Enable = true;
				} else {
					// CW00010 : "데이터가 없습니다."
					//gfn_showMsg("CW00010"); 
					gfn_showAlert("해당차수가 저장되지 않았습니다. 저장후 실행해주십시오");
					// 그리드에 표시
					tabCopy.tabHogi.Grid0.NoDataText = gridNoDataMsg;
					tabCopy.tabHogi.Grid0.BindDataset = "";
					tabCopy.tabHogi.Checkbox0.Enable = false;
					tabCopy.tabHogi.Checkbox1.Enable = false;
					tabCopy.tabHogi.Checkbox2.Enable = false;
					tabCopy.tabHogi.Checkbox3.Enable = false;
					tabCopy.tabHogi.Checkbox4.Enable = false;
					tabCopy.tabHogi.Checkbox5.Enable = false;
					tabCopy.tabHogi.Checkbox6.Enable = false;

					// [복사]버튼 활성여부
					btn_copy.Enable = false;
				} 
			} else if(tabCopy.TabIndex == 1) {
				if(ds_output_ZSDS0016.GetRowCount() > 0) {
					// CI00002 : "조회를 완료했습니다.조회건수 [${}건]"
					gfn_showMsg("CI00002", ds_output_ZSDS0016.RowCount() + ""); 

					// 그리드 설정
					fn_sayangGridSet();
					tabCopy.tabMo.Checkbox0.Enable = true;
					tabCopy.tabMo.Checkbox1.Enable = true;
					tabCopy.tabMo.chk_basic.Enable = true;
					tabCopy.tabMo.chk_inside.Enable = true;
					tabCopy.tabMo.chk_outside.Enable = true;
					tabCopy.tabMo.chk_option.Enable = true;
					tabCopy.tabMo.chk_layout.Enable = true;
					tabCopy.tabMo.chk_common.Enable = true;

					// [복사]버튼 활성여부
					btn_copy.Enable = true;
				} else {
					gfn_showAlert("해당차수가 저장되지 않았습니다. 저장후 실행해주십시오");
					// 그리드에 표시
					tabCopy.tabMo.grdTempList.NoDataText = gridNoDataMsg;
					tabCopy.tabMo.grdTempList.BindDataset = "";
					tabCopy.tabMo.Checkbox0.Enable = false;
					tabCopy.tabMo.Checkbox1.Enable = false;
					tabCopy.tabMo.chk_basic.Enable = false;
					tabCopy.tabMo.chk_inside.Enable = false;
					tabCopy.tabMo.chk_outside.Enable = false;
					tabCopy.tabMo.chk_option.Enable = false;
					tabCopy.tabMo.chk_layout.Enable = false;
					tabCopy.tabMo.chk_common.Enable = false;

					// [복사]버튼 활성여부
					btn_copy.Enable = false;
				}
			}

		} else if(strSvcID == "copy") {	
			// 상태바 비활성
			fn_createWait("",this);
			//fn_progressBarWait('copy', 'false');

			var rsMsg = split(ds_result.getColumn(0, "RESULT"), ",", "webstyle");
			// 총건수:[${}]건 중 반영된건수:[${}] \n 실패한 특성:[${}]
			var arr = [rsMsg[0],rsMsg[1],rsMsg[2]];
			gfn_showAlert("CI10004", arr);

			// 2013.03.04 사양복사 완료시 공통사양 재설정
			parent.fn_aspc (); // 시방서
			parent.fn_bclcd(); // LCD 총수량
			parent.fn_bcpi (); // LED 총 수량
			parent.fn_deph (); // 비상통화장치 DI-PBX 적용대수
			parent.fn_ddvr (); // DVR 적용대수
			parent.fn_dsv1 (); // 감시반1
			parent.fn_dsv2 (); // 감시반2

			// 2013.04.15 사양복사시 복사된 호기에도 변경 FLAG 체크
			var oSpecDs = parent.ds_output_ZSDT0094_change;
			var oHogiDs = parent.ds_change_ZSDT0091;
			var sPosnr, sHogiChck, nSearchRow;

			for(var i = 0; i < oHogiDs.getRowCount(); i++) {
				sPosnr = oHogiDs.getColumn(i, "POSNR");
				sHogiChck = oHogiDs.getColumn(i, "CHCK");
				if(sHogiChck != "X") {
					nSearchRow = oSpecDs.searchRow("POSNR=="+quote(sPosnr)+" && (STATUS=='insert'||STATUS=='update')");
					if(nSearchRow > -1) {
						oHogiDs.setColumn(i, "CHCK", "X");
					}
				}
			}

			parent.ds_output_ZSDT0094_change_OnLoadCompleted();
			fn_destroyWait();
		}
	}
}


/******************************************************
* 그리드 헤더 클릭시 정렬 처리 
*******************************************************/
function Grid_OnHeadClick(obj,nCell,nX,nY,nPivotIndex) {
	if (nCell == 0) {
		// 컬럼 display 여부
		gfn_selectGridCol(obj); 
	} else  {
		// 그리드 정렬 처리 
		gfn_sortGrid(obj, nCell);
	}

}


/******************************************************
// 호기 - 그리드 설정
******************************************************/
function fn_GridSet() {
	// 호기
	//http.Sync = true;
	// 자재 ( componentId, comboGubun, DatasetId, 전체여부, 그리드/콤보여부 )
	//fn_SetListCode( 5, "matnr", "ds_matnr", "N", "grid");
	//http.Sync = false;
	//trace(parent.ds_matnrClass.saveXML());
	ds_matnrClass.copy(parent.ds_matnrClass);

	// chk컬럼 추가하여 사용하기 위한 ds생성
	destroy("ds_copy_ZSDT0091");
	create("dataset", "ds_copy_ZSDT0091");
	object("ds_copy_ZSDT0091").copy(ds_change_ZSDT0091);
	object("ds_copy_ZSDT0091").addColumn("CHK", "STRING", 256);

	// chk컬럼 추가
	for(var i = 0 ; i < ds_copy_ZSDT0091.getRowCount() ; i++) {
		ds_copy_ZSDT0091.setColumn(i, "CHK", 0);
	}

	ds_copy_ZSDT0091.filter("SPEC_STAT!='X'"); 	// 수정불가능여부(x : true)
	tabCopy.tabHogi.grdHogiList.BindDataset = "ds_copy_ZSDT0091";		// 그리드 바인딩
	tabCopy.tabMo.grdMoList.BindDataset = "ds_copy_ZSDT0091";		// 그리드 바인딩
}

/******************************************************
// 호기 - 그리드 전체선택/해제
******************************************************/
function btn_HogiListChk_OnClick(obj) {
	var chk = 0;
	if(ds_copy_ZSDT0091.getColumn(0, "CHK") == 1) {
		chk = 0;
	} else {
		chk = 1;
	}

	for(var i = 0 ; i < ds_copy_ZSDT0091.getRowCount() ; i++) {
		ds_copy_ZSDT0091.setColumn(i, "CHK", chk);
	}
}

/******************************************************
// 사양 - 그리드 설정
******************************************************/
function fn_sayangGridSet() {
	var grpCd;
	// chk컬럼, grp컬럼 추가하여 사용하기 위한 ds생성
	destroy("ds_copy_ZSDS0016");
	create("dataset", "ds_copy_ZSDS0016");
	object("ds_copy_ZSDS0016").copy(ds_output_ZSDS0016);
	object("ds_copy_ZSDS0016").addColumn("CHK", "STRING", 256);
	object("ds_copy_ZSDS0016").addColumn("GRP", "STRING", 256);

	// chk컬럼 추가, grp컬럼 추가
	for(var i = 0 ; i < ds_copy_ZSDS0016.getRowCount() ; i++) {
		ds_copy_ZSDS0016.setColumn(i, "CHK", 0);
		if(substr(ds_copy_ZSDS0016.getColumn(i, "NAM_CHAR"),0,3) == 'CO_') {
			ds_copy_ZSDS0016.DeleteRow(i);
			i--;
			continue;
		}

		grpCd = substr(ds_copy_ZSDS0016.getColumn(i, "NAM_CHAR"),3,1);
		switch(grpCd){
			case 'F':
			case 'Y':
				grpCd = 'D';
				break;
			default:
		}
		ds_copy_ZSDS0016.setColumn(i, "GRP", grpCd);
	}

	if(tabCopy.TabIndex == 0) {
		ds_copy_ZSDS0016.Sort("GRP,ATBEZ"); // 그룹별로 sorting	
		tabCopy.tabHogi.Grid0.BindDataset = "ds_copy_ZSDS0016"; // 그리드 바인딩
	} else if(tabCopy.TabIndex == 1) {
		tabCopy.tabMo.grdTempList.BindDataset = "ds_copy_ZSDS0016"; // 그리드 바인딩
	}	
}

/******************************************************
// 사양 - 그리드 전체선택/해제
******************************************************/
function btn_SayangListChk_OnClick(obj) {
	var chk = 0;
	if(ds_copy_ZSDS0016.getColumn(0, "CHK") == 1) chk = 0;
	else chk = 1;

	for(var i = 0 ; i < ds_copy_ZSDS0016.getRowCount() ; i++) {
		ds_copy_ZSDS0016.setColumn(i, "CHK", chk);
	}
}

/******************************************************
// 사양선택옵션 체크시
******************************************************/
function Checkbox_OnClick(obj,strValue) {
	var grpGubun = "";

	if(tabCopy.TabIndex == 0) {
		if(obj.id == 'Checkbox0') grpGubun = "A1";	// 기본사양
		if(obj.id == 'Checkbox1') grpGubun = "A2";	// 영업사양
		if(obj.id == 'Checkbox3') grpGubun = "A";	// A그룹
		if(obj.id == 'Checkbox4') grpGubun = "B";	// B그룹
		if(obj.id == 'Checkbox5') grpGubun = "C";	// C그룹
		if(obj.id == 'Checkbox6') grpGubun = "D";	// D그룹
		if(obj.id == 'Checkbox2') grpGubun = "E";	// L/O 사양

		for(var i = 0 ; i < ds_copy_ZSDS0016.getRowCount() ; i++) {
			if(grpGubun == 'A1') {
				if(ds_copy_ZSDS0016.getColumn(i, "NAM_CHAR") == 'EL_ASPD' 
				|| ds_copy_ZSDS0016.getColumn(i, "NAM_CHAR") == 'EL_ASTQ'  
				|| ds_copy_ZSDS0016.getColumn(i, "NAM_CHAR") == 'EL_ATYP'  
				|| ds_copy_ZSDS0016.getColumn(i, "NAM_CHAR") == 'EL_AFQ'  
				|| ds_copy_ZSDS0016.getColumn(i, "NAM_CHAR") == 'EL_AMAN'  
				|| ds_copy_ZSDS0016.getColumn(i, "NAM_CHAR") == 'EL_ACAPA'  
				|| ds_copy_ZSDS0016.getColumn(i, "NAM_CHAR") == 'EL_ATR'  
				|| ds_copy_ZSDS0016.getColumn(i, "NAM_CHAR") == 'EL_AOPEN'  
				|| ds_copy_ZSDS0016.getColumn(i, "NAM_CHAR") == 'EL_AUSE') {	
					if(strValue == 0) {
						ds_copy_ZSDS0016.setColumn(i, "CHK", 0);
					} else {
						ds_copy_ZSDS0016.setColumn(i, "CHK", 1);
					}
				}
			}
			// 영업사양
			if(grpGubun == 'A2') {
				if(ds_copy_ZSDS0016.getColumn(i, "GRP") == 'A'
				|| ds_copy_ZSDS0016.getColumn(i, "GRP") == 'B' 
				|| ds_copy_ZSDS0016.getColumn(i, "GRP") == 'C' 
				|| ds_copy_ZSDS0016.getColumn(i, "GRP") == 'D') {
					if(strValue == 0) {
						ds_copy_ZSDS0016.setColumn(i, "CHK", 0);
						tabCopy.tabHogi.Checkbox3.Value = false;
						tabCopy.tabHogi.Checkbox4.Value = false;
						tabCopy.tabHogi.Checkbox5.Value = false;
						tabCopy.tabHogi.Checkbox6.Value = false;
					} else {
						ds_copy_ZSDS0016.setColumn(i, "CHK", 1);
						tabCopy.tabHogi.Checkbox3.Value = true;
						tabCopy.tabHogi.Checkbox4.Value = true;
						tabCopy.tabHogi.Checkbox5.Value = true;
						tabCopy.tabHogi.Checkbox6.Value = true;
					}
				}
			}

			if(grpGubun == 'A'
			|| grpGubun == 'B'
			|| grpGubun == 'C'
			|| grpGubun == 'D'
			|| grpGubun == 'E') {
				if(ds_copy_ZSDS0016.getColumn(i, "GRP") == grpGubun) {
					if(strValue == 0) {
						ds_copy_ZSDS0016.setColumn(i, "CHK", 0);
					} else {
						ds_copy_ZSDS0016.setColumn(i, "CHK", 1);
					}
				}
			}
		}
	} else if(tabCopy.TabIndex == 1) {
		tabCopy.tabMo.grdTempList.Redraw = false;

		if(obj.id == 'Checkbox0') grpGubun = "A1";			// 기본사양
		if(obj.id == 'Checkbox1') grpGubun = "A2";			// 영업사양
		if(obj.id == 'chk_basic') grpGubun = "BASIC";		// basic
		if(obj.id == 'chk_inside') grpGubun = "INSIDE";		// inside
		if(obj.id == 'chk_outside') grpGubun = "OUTSIDE";	// outside
		if(obj.id == 'chk_option') grpGubun = "OPTION";		// option
		if(obj.id == 'chk_layout') grpGubun = "LAYOUT";		// layout
		if(obj.id == 'chk_common') grpGubun = "COMMON";		// common

		for(var i = 0; i < ds_copy_ZSDS0016.getRowCount(); i++) {
			if(grpGubun == 'A1') { // 기본사양
				if(ds_copy_ZSDS0016.getColumn(i, "NAM_CHAR") == 'EL_ASPD' 
				|| ds_copy_ZSDS0016.getColumn(i, "NAM_CHAR") == 'EL_ASTQ'  
				|| ds_copy_ZSDS0016.getColumn(i, "NAM_CHAR") == 'EL_ATYP'  
				|| ds_copy_ZSDS0016.getColumn(i, "NAM_CHAR") == 'EL_AFQ'  
				|| ds_copy_ZSDS0016.getColumn(i, "NAM_CHAR") == 'EL_AMAN'  
				|| ds_copy_ZSDS0016.getColumn(i, "NAM_CHAR") == 'EL_ACAPA'  
				|| ds_copy_ZSDS0016.getColumn(i, "NAM_CHAR") == 'EL_ATR'  
				|| ds_copy_ZSDS0016.getColumn(i, "NAM_CHAR") == 'EL_AOPEN'  
				|| ds_copy_ZSDS0016.getColumn(i, "NAM_CHAR") == 'EL_AUSE') {	
					if ( strValue == 0 ) {
						ds_copy_ZSDS0016.setColumn(i, "CHK", 0);
					} else {
						ds_copy_ZSDS0016.setColumn(i, "CHK", 1);
					}
				}
			}
			// 영업사양
			if ( grpGubun == 'A2' ) {
				if(substr(ds_copy_ZSDS0016.getColumn(i, "NAM_CHAR"),0,3) == 'CO_')
					continue;
				if ( substr(ds_copy_ZSDS0016.getColumn(i, "NAM_CHAR"),3,1) == 'A'
				 || substr(ds_copy_ZSDS0016.getColumn(i, "NAM_CHAR"),3,1) == 'B' 
				 || substr(ds_copy_ZSDS0016.getColumn(i, "NAM_CHAR"),3,1) == 'C' 
				 || substr(ds_copy_ZSDS0016.getColumn(i, "NAM_CHAR"),3,1) == 'D') {
					if ( strValue == 0 ) {
						ds_copy_ZSDS0016.setColumn(i, "CHK", 0);
					} else {
						ds_copy_ZSDS0016.setColumn(i, "CHK", 1);
					}
				}
			}

			if(grpGubun == 'BASIC' || grpGubun == 'INSIDE' || grpGubun == 'OUTSIDE' || 
			   grpGubun == 'OPTION' || grpGubun == 'LAYOUT' || grpGubun == 'COMMON') {
				if(IndexOf(ds_copy_ZSDS0016.getColumn(i, "ATWTB1"), grpGubun) > -1) {
					if(strValue == 0 ) ds_copy_ZSDS0016.setColumn(i, "CHK", 0);
					else ds_copy_ZSDS0016.setColumn(i, "CHK", 1);
				}
			}
		}

		tabCopy.tabMo.grdTempList.Redraw = true;
	}
}

