/******************************************************
// 복사
******************************************************/
function fn_sayangCopy() {
	// 사양그리드 redraw 설정
	parent.Tab00.tab3.Grid.Redraw = false;
	parent.Tab00.tab3.Grid0.Redraw = false;
	parent.Tab00.tab3.Grid1.Redraw = false;
	parent.Tab00.tab3.Grid2.Redraw = false;
	parent.Tab00.tab3.Grid3.Redraw = false;
	parent.Tab00.tab3.Grid4.Redraw = false;

	// 복사 구분
	var copyGubun;

	if(tabCopy.TabIndex == 0) copyGubun = tabCopy.tabHogi.rd_copyGubun.Value;
	else if(tabCopy.TabIndex == 1) copyGubun = tabCopy.tabMo.rd_copyGubun.Value;

	if(copyGubun == 1) {
		if(ds_copy_ZSDT0091.CaseCount("CHK=='1'") < 1 || ds_copy_ZSDS0016.CaseCount("CHK=='1'") < 1) {
			// [${}]과 [${}]이 존재하는지 확인바랍니다.
			var arr = ["복사할 대상","복사할 원본"];
			gfn_showAlert("CW11001", arr);
			return false;
		}
	} else if(copyGubun == 2) {
		if(ds_copy_ZSDS0016.CaseCount("CHK=='1'") < 1) {
			// [${}]과 [${}]이 존재하는지 확인바랍니다.
			var arr = ["복사할 대상","복사할 원본"];
			gfn_showAlert("CW11001", arr);
			return false;
		}
	}

	var inputDs = "ds_ZSDS0016_input=ds_copy_ZSDS0016";
		inputDs += " ds_ZSDT0091_input=ds_copy_ZSDT0091";
		inputDs += " ds_ZSDT0094_input=ds_output_ZSDT0094_change";
	var outputDs = "ds_output_ZSDT0094_change=ds_ZSDT0094_input ds_result=ds_result";
	var strParam = "flag=" + quote(copyGubun) + " tabIndex=" + quote(tabCopy.TabIndex);

	// Transaction
	callServiceN("copy"				// srvId
				, "sso0051n/copy"	// url
				, inputDs			// biz에서 받는 명, mip에서 보내는 명
				, outputDs			// mip에서 받는 명, biz에서 보내는 명
				, strParam			// strParam
				, ""				// CallBackFunc
				);
}

/******************************************************
// 엔터키 입력 시 조회되도록 함
******************************************************/
function ed_pspHogi_OnChar(obj,strPreText,nChar,strPostText,LLParam,HLParam) {
	if(nChar == 13) fn_queryP();
}

/******************************************************
// 탭 이동시
******************************************************/
function tab_OnChanged(obj,nOldIndex,nNewindex) {
	if(tabCopy.TabIndex == 0) tabCopy.tabHogi.ed_pspHogi.SetFocus();
	else if(tabCopy.TabIndex == 1) tabCopy.tabMo.ed_qtnum.SetFocus();
}

function ed_temp_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam) {
	if(nChar == "13") {
		if(obj.id == "ed_tempMo") {
			if(length(obj.text) < 1) {
				tabCopy.tabMo.grdTempList.Redraw = false;
				tabCopy.tabMo.grdTempList.SetCellProp("body", 1, "bkColor", "");
				tabCopy.tabMo.grdTempList.SetCellProp("body", 1, "bkColor2", "User Color 22");
				tabCopy.tabMo.grdTempList.Redraw = true;
			} else {
				tabCopy.tabMo.grdTempList.Redraw = false;
				tabCopy.tabMo.grdTempList.SetCellProp("body", 1, "bkColor", "expr:iif(IndexOf(ATBEZ, tabCopy.tabMo.ed_tempMo.Text) < 0, '', 'red')");
				tabCopy.tabMo.grdTempList.SetCellProp("body", 1, "bkColor2", "expr:iif(IndexOf(ATBEZ, tabCopy.tabMo.ed_tempMo.Text) < 0, 'User Color 22', 'red')");
				tabCopy.tabMo.grdTempList.Redraw = true;
			}
		} else if(obj.id == "ed_tempHogi") {
			if(length(obj.text) < 1) {
				tabCopy.tabHogi.Grid0.Redraw = false;
				tabCopy.tabHogi.Grid0.SetCellProp("body", 1, "bkColor", "");
				tabCopy.tabHogi.Grid0.SetCellProp("body", 1, "bkColor2", "User Color 22");
				tabCopy.tabHogi.Grid0.Redraw = true;
			} else {
				tabCopy.tabHogi.Grid0.Redraw = false;
				tabCopy.tabHogi.Grid0.SetCellProp("body", 1, "bkColor", "expr:iif(IndexOf(ATBEZ, tabCopy.tabHogi.ed_tempHogi.Text) < 0, '', 'red')");
				tabCopy.tabHogi.Grid0.SetCellProp("body", 1, "bkColor2", "expr:iif(IndexOf(ATBEZ, tabCopy.tabHogi.ed_tempHogi.Text) < 0, 'User Color 22', 'red')");
				tabCopy.tabHogi.Grid0.Redraw = true;
			}		
		}
	}		
}
