function fn_OnUnloadCompleted(obj) {
	return gfn_isFormUnload(obj);
}

/********************************************************************************
* 기      능   : form 초기화 
********************************************************************************/
function fn_init_form()
{
	F_QTNUM			= "";				// 조회된 견적번호
	F_QTSER			= "";				// 조회된 견적차수
	F_VBELN			= "";				// 조회된 프로젝트번호
	F_RECAD_DA		= "";				// 조회된 수주승인일

	// 데이타셋 초기화
	ds_dtl.ClearData(); 	
	ds_list_item.ClearData();
	ds_list_txt.ClearData(); 

	// 견적상태 초기화
	ed_status.Text = "";

	// 코딩 시 필요한 그리드 컬럼 인덱스	  
	//F_COLNUM_MEKIND	= gfn_getColIdx(grd_list,"MEKIND"	);  // 법조치종류

	// 그리드, 디비젼, 탭 항목 위치 재조정 
	comm_shape.Height 	= 524;
	//grid_partner.top 	= 90;
	grid_partner.top 	= 66;

	//grid_partner.Height 	= 121; 
	grid_partner.Height 	= 152; 

	grid_partner.Width 	= 442; 
	tab_hdel.Height 	= 248;
	tab_hdel.Width  	= 962; 
	tab_hdel.item.grid_item.Height 	= 159;
	tab_hdel.item.grid_item.Width   = 953;
	tab_hdel.txt.grid_txt.Height 	= 177;   
	grd_bill.Height = 101;

	tab_hdel.TabIndex = 0;  // 헤더 탭에 오도록 

	// 공동수급1, 공동수급2, 공동수급3 디비젼 초기화 
	tab_hdel.head.div_lifnr1.fn_rsetSize(272, 81, 170);  	 // 사이즈조정
	tab_hdel.head.div_lifnr2.fn_rsetSize(272, 81, 170);  	 // 사이즈조정
	tab_hdel.head.div_lifnr3.fn_rsetSize(272, 81, 170); 		 // 사이즈조정

	tab_hdel.head.div_lifnr1.top = 47;
	tab_hdel.head.div_lifnr2.top = 69;
	tab_hdel.head.div_lifnr3.top = 91;

	tab_hdel.head.div_lifnr1.ed_lifnr.Style = "sty_ipt_form"; 				// 스타일적용(옵션 입력항목)
	tab_hdel.head.div_lifnr2.ed_lifnr.Style = "sty_ipt_form"; 				// 스타일적용(옵션 입력항목)
	tab_hdel.head.div_lifnr3.ed_lifnr.Style = "sty_ipt_form"; 				// 스타일적용(옵션 입력항목)

	gfn_BindColumn(tab_hdel.head.div_lifnr1.ed_lifnr, "ds_dtl", "LIFNR");	// 데이타셋 바인딩
	gfn_BindColumn(tab_hdel.head.div_lifnr2.ed_lifnr, "ds_dtl", "LIFNR2");	// 데이타셋 바인딩
	gfn_BindColumn(tab_hdel.head.div_lifnr3.ed_lifnr, "ds_dtl", "LIFNR3");	// 데이타셋 바인딩

	tab_hdel.head.div_lifnr1.ed_name1.Visible = true;						// 협력업체명 보이도록 수정
	tab_hdel.head.div_lifnr2.ed_name1.Visible = true;						// 협력업체명 보이도록 수정
	tab_hdel.head.div_lifnr3.ed_name1.Visible = true; 						// 협력업체명 보이도록 수정

	tab_hdel.head.div_lifnr1.ed_lifnr.Enable = false;
	tab_hdel.head.div_lifnr2.ed_lifnr.Enable = false;
	tab_hdel.head.div_lifnr3.ed_lifnr.Enable = false;
	tab_hdel.head.div_lifnr1.btn_popLifnr.Enable = false;
	tab_hdel.head.div_lifnr2.btn_popLifnr.Enable = false;
	tab_hdel.head.div_lifnr3.btn_popLifnr.Enable = false;
	tab_hdel.head.div_lifnr1.ed_name1.Enable     = false;						// 협력업체명 수정불가하도록 수정
	tab_hdel.head.div_lifnr2.ed_name1.Enable     = false;						// 협력업체명 수정불가하도록 수정
	tab_hdel.head.div_lifnr3.ed_name1.Enable     = false; 						// 협력업체명 수정불가하도록 수정

	// 빌딩 버튼
	btn_input_bill.Enable  = false;	// 빌링-행삽입버튼
	btn_append_bill.Enable = false;	// 빌링-행추가버튼
	btn_delete_bill.Enable = false;	// 빌링-행삭제버튼

	// 그리드 FIX
	gfn_grid_fix(tab_hdel.item.grid_item	, 5);	// 품목그리드는 SPEC에서 fix

	// 일괄복사 버튼
	tab_hdel.item.btn_head_copy.Enable = false;	// 일괄복사 버튼
	tab_hdel.item.btn_sta_copy.Enable = false;  //착공(골조)예정일 일괄복사 버튼

	// TEXT
	tab_hdel.txt.btn_input_txt.Enable  = false;
	tab_hdel.txt.btn_append_txt.Enable = false;
	tab_hdel.txt.btn_delete_txt.Enable = false;

	// 빌링필터구분 초기화
	cb_bill_gbn.Value = "";

	cb_bill_gbn.Enable = false;	// 빌딩필터조건
}     

/********************************************************************************
* 기      능   : 오더유형 조회
********************************************************************************/
function fn_query_auart()
{
	// 오더유형 콤보목록 조회
	nsfRequest("query", "combo/comboAuartNm"  , "ds_auart_cd=ds_auart_cd"
											  , "ds_auart_cd=ds_auart_cd ds_error=ds_error"
											  , ""
											  , "fn_afterQuery_auart");
}

/********************************************************************************
* 기      능   : 건물용도 코드목록 조회
********************************************************************************/
function fn_query_bdtyp() 
{
	nsfRequest("query"									// srvId
				, "com0160/find"						// url
				, "ds_cond_build=ds_cond_build ds_list_build=ds_list_build"		// inDsList
				, "ds_list_build=ds_list_build ds_error=ds_error"	// outDsList
				, ""									// strParam
				, "fn_afterQuery_bdtyp"						// CallBackFunc
				);  
}

//=========================================================================================
// [ PART 3 ]
// Main Process Event 정의(조회,입력,삭제,저장,인쇄,form닫기)
//=========================================================================================
/********************************************************************************
* 기      능   : 조회
********************************************************************************/
var fn_query_chk = false; //중복조회 방지용 flag

function fn_query()
{
	// sort mark 삭제 
	gfn_clearGridSortMark(grd_bill); 

	// 조회조건 항목오류검증
	if (fn_valid_chk("query") == false)  return; 

	// 조회조건 설정
	ds_cond.ClearData();

	if (fn_param_set("query") == false)  return;

	if(!fn_query_chk) {
		fn_query_chk = true;
	} else {
		gfn_showAlert("CW10025", "조회중입니다..");
		return;
	}

	fn_createWait("",this);     // 처리중입니다. 메시지 처리

	// 조회항목 초기화
	F_QTNUM				= "";	// 조회된 견적번호
	F_QTSER				= "";	// 조회된 견적차수
	F_VBELN				= "";	// 조회된 프로젝트번호
	F_RECAD_DA 			= "";	// 조회된 수주승인일

	// DataSet과 Bind 되지 않은 변수
	ed_status.Text 		= "";	// 견적상태
	ed_guresun.Text		= "";	// 거래선
	med_netwr.Text 		= "";	// 수주금액
	med_wavwr.Text 		= "";	// 원가
	med_rate.Text 		= "";	// 시행율
	ed_auart_nm.text 	= "";  	// 문서유형명
	ed_spart_nm.text 	= "";	// 제품군명 

	tab_hdel.TabIndex 	= 0;	// 헤더 탭으로 이동

	med_fakwr_totsum.Value = "";
	cb_bill_gbn.Value      = "";
	med_fakwr_sum.Value    = "";

	// 데이타셋 이벤트 비활성화
	ds_dtl.Fireevent = false; 
	ds_list_partner.Fireevent = false; 
	ds_list_item.Fireevent = false; 
	ds_list_bill.Fireevent = false; 
	ds_list_txt.Fireevent = false;  

	ds_dtl.ClearData();		
	ds_list_partner.ClearData();
	ds_list_item.ClearData();
	ds_list_bill.ClearData();
	ds_list_txt.ClearData();
	ds_dtl_save.ClearData();		 
	ds_list_item_save.ClearData();
	ds_list_bill_save.ClearData();
	ds_list_txt_save.ClearData();
	ds_error.ClearData();   	// 결과값 

	// 06. 기능개선 추가
    ds_list_bill_delete.ClearData();
	ds_list_txt_delete.ClearData();

	var in_ds_list =  " ds_cond=ds_cond" 
					+ " ds_dtl=ds_dtl"
					+ " ds_list_partner=ds_list_partner"
					+ " ds_list_item=ds_list_item"
					+ " ds_list_bill=ds_list_bill"
					+ " ds_list_txt=ds_list_txt"
					+ " ds_manager=ds_manager"
					;
	var out_ds_list = " ds_dtl=ds_dtl"
					+ " ds_list_partner=ds_list_partner"
					+ " ds_list_item=ds_list_item"
					+ " ds_list_bill=ds_list_bill"
					+ " ds_list_txt=ds_list_txt"
					+ " F_TMPSEARCH=F_TMPSEARCH"
					+ " ds_error=ds_error"
					+ " ds_manager=ds_manager"
					;

    nsfRequest("query"                         // srvId
               , "sso0060N/findEstimateCreate" // url
               , in_ds_list                    // inDsList
               , out_ds_list                   // outDsList
               , ""                            // strParam
               , "fn_afterQuery"               // CallBackFunc
               );

}


//=========================================================================================
//  함수기능  : 수주생성 저장
//  기능ID    : UF015
//  개선내역  : 2016.02.12 임시저장 기능을 추가함
//=========================================================================================
function fn_save()
{
    //trace(ds_dtl.SaveXML()); 
	if ( F_PROCESSING == true ) { // 2013.03.21 처리중 플래그 설정중이면 저장하지 못함
		gfn_showConfirm("CW10025", "이미 처리중입니다. 처리가 완료될 때까지 잠시만 기다려주세요.");
		return;
	}

	gfn_clearMsgBar();   

	// 저장항목 오류검증
	if (fn_valid_chk("save") == false)  return false;

	// 저장여부 확인    (프로젝트번호가 없을 경우에는 신규 수주생성)
	var save_gbn = decode(length(F_VBELN), 0 , "입력된 견적정보로 신규 수주가 생성됩니다. 견적정보" 
											 , "기 생성된 수주생성자료가 변경됩니다. 수주정보"); 
	if (!gfn_showConfirm("CI00014", save_gbn))  return false;  

    // 저장항목 값설정   
	if (fn_param_set("save") == false)  return false;

	fn_createWait("",this);     // 처리중입니다. 메시지 처리 

	// 서비스 호출 후 사업계획번호 GET
	ds_error.ClearData();   	// 결과값 
	var in_ds_list =  " ds_cond=ds_cond" 
					+ " ds_sto=ds_sto" 
					+ " ds_dtl_save=ds_dtl_save" 
					+ " ds_list_item_save=ds_list_item_save"
					+ " ds_list_txt_save=ds_list_txt_save"
					+ " ds_list_bill_save=ds_list_bill_save"
					+ " ds_error=ds_error";

	var out_ds_list = " ds_error=ds_error";

    // 테스트용으로 타임 설정 2012.11.21
	http.TimeOut = 1800;


    //trace(ds_dtl.savexML()); 
    //trace(ds_cond.SaveXML()); 

	F_PROCESSING = true; // 2013.03.21 처리중 플래그 설정

	nsfRequest("update"						    // srvId
				, "sso0060N/saveXmlEstiMateCreate"	// url
				, in_ds_list				    // inDsList
				, out_ds_list				    // outDsList
				, ""						    // strParam
				, "fn_afterSave"			    // CallBackFunc
				);	

}

//=========================================================================================
//  함수기능  : 수주생성 임시저장
//  기능ID    : UF015
//  개선내역  : 2016.02.12 임시저장 기능을 추가함
//=========================================================================================
function fn_saveTemp()
{
    if (ds_dtl.RowCount() > 0) {
		if ( F_PROCESSING == true ) { // 2013.03.21 처리중 플래그 설정중이면 저장하지 못함
			gfn_showConfirm("CW10025", "이미 처리중입니다. 처리가 완료될 때까지 잠시만 기다려주세요.");
			return;
		}

		gfn_clearMsgBar();   

		// 저장항목 오류검증
		// 임시 저장시에는 입력값 체크가 필요없음(고객 확인 필요)
		// if (fn_valid_chk("save") == false)  return false;

		// 저장여부 확인    (프로젝트번호가 없을 경우에는 신규 수주생성)
		var save_gbn = decode(length(F_VBELN), 0 , "입력된 신규 수주생성 정보를 임시 저장합니다. 수주정보"); 
		if (!gfn_showConfirm("CI00014", save_gbn))  return false;  

		// 저장항목 값설정   
		if (fn_param_set("tmpsave") == false)  return false;

		fn_createWait("",this);     // 처리중입니다. 메시지 처리 

		// 서비스 호출 후 사업계획번호 GET
		ds_error.ClearData();   	// 결과값 

		var in_ds_list =  " ds_cond=ds_cond" 
						+ " ds_dtl_save=ds_dtl_save" 
						+ " ds_list_item_save=ds_list_item_save"
						+ " ds_list_txt_save=ds_list_txt_save"
						+ " ds_list_bill_save=ds_list_bill_save"
						+ " ds_error=ds_error";

		var out_ds_list = " ds_error=ds_error";

		F_PROCESSING = true; // 2013.03.21 처리중 플래그 설정

		nsfRequest("update"						        // srvId
					, "sso0060N/tmpSaveEstiMateCreate"	// url
					, in_ds_list				        // inDsList
					, out_ds_list				        // outDsList
					, ""						        // strParam
					, "fn_afterSave"			        // CallBackFunc
					);
    }
}

//=========================================================================================
//  함수기능  : 수주생성 임시저장 삭제 
//  기능ID    : UF015
//  개선내역  : 2017.05.10 임시저장 삭제 기능을 추가함
//=========================================================================================
function fn_deleteTemp(obj)
{
    if (ds_dtl.RowCount() > 0) {
		if ( F_PROCESSING == true ) { // 2013.03.21 처리중 플래그 설정중이면 저장하지 못함
			gfn_showConfirm("CW10025", "이미 처리중입니다. 처리가 완료될 때까지 잠시만 기다려주세요.");
			return;
		}

		gfn_clearMsgBar();   

		// 저장여부 확인    (프로젝트번호가 없을 경우에는 신규 수주생성)
		var save_gbn = decode(length(F_VBELN), 0 , "저장된 임시 데이터를 삭제 합니다. 수주정보"); 
		if (!gfn_showConfirm("CI00014", save_gbn))  return false;  

		// 저장항목 값설정   
		if (fn_param_set("tmpdelete") == false)  return false;

		fn_createWait("",this);     // 처리중입니다. 메시지 처리 

		// 서비스 호출 후 사업계획번호 GET
		ds_error.ClearData();   	// 결과값 

		var in_ds_list =  " ds_cond=ds_cond" ;
		var out_ds_list = " ds_error=ds_error";

		F_PROCESSING = true; // 2013.03.21 처리중 플래그 설정

		nsfRequest("update"						        	// srvId
					, "sso0060N/tmpDeleteEstiMateCreate"	// url
					, in_ds_list				        	// inDsList
					, out_ds_list				        	// outDsList
					, ""						        	// strParam
					, "fn_afterSave"			        	// CallBackFunc
					);
    }
}

/********************************************************************************
* 기      능   :  닫기
********************************************************************************/
function fn_close()
{ 
	if (fn_valid_chk("close") == false) return false;

	close();
}


//=========================================================================================
// [ PART 4]
// Callback 함수 정의 : callback 함수명은 메인 함수의 fn_xxxx() 에서 xxxx 부분은 fn_afterXXXX()
// 형태가 되도록 작성 
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(svcId, errCode, errMsg)
{
    var Techreq = "";			//2016.12.12 기술검토 요청 여부 추가 (""-정상, "N"  - 요청서 미존재)by mj.Shin 
    var sLifnrchk = "";
    var sAuart = "";

    fn_query_chk = false; //중복조회 방지용 flag
	fn_destroyWait();

	// 데이타셋 이벤트 활성화
	ds_dtl.FireEvent          = true;
	ds_list_partner.Fireevent = true; 
	ds_list_item.Fireevent 	  = true; 
	ds_list_bill.Fireevent 	  = true; 
	ds_list_txt.Fireevent 	  = true;

	if(ds_error.RowCount() > 0) 	// 조회실패
	{

		// 환율정보가 없습니다.
		if (ds_error.GetColumn(0, "ERRMSG") == "CE10007")
		{
			gfn_showAlert(ds_error.GetColumn(0, "ERRMSG"));
			gfn_showMsgBar(ds_error.GetColumn(0,"ERRMSG"), ds_error.GetColumn(0,"ERRCODE"));
			return false;
		}

		fn_recad_da_exist(false);

		// 폼 파라메터가 존재하고 오류코드가 CW10013 이면 화면을 close한다.
		// "CW10013", "${}는 입찰완료정보가 존재하지 않는 견적번호입니다."); 
		if (length(F_PARENT_QTNUM) > 0 )
		{
			// 조회 후 화면이 Close상태가 필요한지 확인 후 처리.....
			//gfn_showAlert("CE00001");
			gfn_showMsgBar(ds_error.GetColumn(0,"ERRMSG"), ds_error.GetColumn(0,"ERRCODE"));
			gfn_openMsgWin(ds_error.GetColumn(0,"ERRMSG"), 'W','A');			
			fn_close();
		}
		else
		{
			gfn_showMsgBar(ds_error.GetColumn(0,"ERRMSG"), ds_error.GetColumn(0,"ERRCODE"));
			gfn_openMsgWin(ds_error.GetColumn(0,"ERRMSG"), 'W','A');
			return false;
		}
	}
	else
	{
		// 조회된 자료가 있을 경우 그리드 첫번째 행에 포커스 위치시킨다.
		if (ds_dtl.RowCount() > 0) 
		{
		  //영업기술부 요청 >> 기술검토요청 없이 수주 생성 불가 처리 20160310
		  if (( left(ds_dtl.GetColumn(0,"AUART"), 2) != "ZE" && left(ds_dtl.GetColumn(0,"AUART"), 2) != "ZC" && left(ds_dtl.GetColumn(0,"AUART"), 2) != "ZT" )
		  		&& left(ds_dtl.GetColumn(0,"AUART"), 2) != "ZJ" 
		       && (ds_dtl.GetColumn(0,"VBELN") == "" || ds_dtl.GetColumn(0,"VBELN") == null) && (ds_dtl.GetColumn(0,"CNT") == "" || ds_dtl.GetColumn(0,"CNT") == null)) {
		       	//sp 만 품목으로 있는 경우 기술검토요청 불필요	20160627       	
		       	Destroy("ds_list_item_chk");
				Create("dataset", "ds_list_item_chk");			
				ds_list_item_chk.Copy("ds_list_item");

				//ds_list_item_chk.Filter("MATNR == 'Y-1000'");			"2017.08.02 Like 검색으로 변경 by mj.Shin  
				ds_list_item_chk.Filter("pos(MATNR," + "\"" + replace("Y",'"','\\"') + "\") >= 0"); 		

				// if (ds_list_item.rowcount != ds_list_item_chk.rowcount) {
					// fn_recad_da_exist(false);
					// gfn_showAlert("CW10025", "기술검토요청 미진행시 수주등록 불가");
				// }

//-----< 2016.12.12 기술검토 요청 여부 추가 (""-정상, "N"  - 요청서 미존재) 보완 by mj.Shin 	Start 						
				if (ds_list_item.rowcount != ds_list_item_chk.rowcount) {
					Techreq = "N";
					fn_recad_da_exist(false);
					gfn_showAlert("CW10025", "기술검토요청 미진행시 수주등록 불가");
				} else {
					Techreq = "";
				}													
		  } 
		  // else {	

		  if ( Techreq = "" ) {
//-----> 2016.12.12 기술검토 요청 여부 추가 (""-정상, "N"  - 요청서 미존재) 보완 by mj.Shin 	End 

			// 거래선명칭 표시
			if(ds_list_partner.rowcount != 0 )
			{ 
				ed_guresun.Text = ds_list_partner.getColumn(1, "PARTNER_NM"); 
			}

			ds_dtl.SetColumn(0,"KVGR1",""); //계산서 구분 디폴트 : 일반 
			F_QTNUM		= ds_dtl.GetColumn(0, "QTNUM");		// 조회된 견적번호
			F_QTSER		= ds_dtl.GetColumn(0, "QTSER");		// 조회된 견적차수
			F_VBELN		= ds_dtl.GetColumn(0, "VBELN");		// 조회된 프로젝트번호
			F_RECAD_DA 	= ds_dtl.GetColumn(0, "RECAD_DA");	// 조회된 수주승인일
			// 2012.10.19 견적진행상태 추가 반영
			F_ZPRGFLG   = ds_dtl.GetColumn(0, "ZPRGFLG");	// 조회된 수주승인일
			sLifnrchk	= ds_dtl.GetColumn(0, "LIFNRCHK");	// 조회된 계약형태 
			sAuart 		= ds_dtl.GetColumn(0, "AUART");		// 조회된 오더유형 

			// 금액, 원가, 시행율 산출하여 Display
			med_netwr.text = ds_list_item.sum("NETWR");		// 금액
			med_wavwr.text = ds_list_item.sum("WAVWR");		// 원가

			//med_rate.text  = ((parseInt(med_wavwr.Value) / parseInt(med_netwr.Value)) * 100,1) ;	// 시행율
			med_rate.text  = round((tonumber(med_wavwr.Value) / tonumber(med_netwr.Value)) * 100,1) ;	// 시행율

			// 건설공사대장 통보대상 여부 2021.03.05 mj.Shin 
			if ( length(F_VBELN) == 0 && ( left(sAuart, 2) == "ZS" || left(sAuart, 2) == "ZN" || left(sAuart, 2) == "ZJ" ) ) {
				if ( ( sLifnrchk == "1" && toNumber(med_netwr.Value) >= 90909091 ) ||
				     ( sLifnrchk != "1" && toNumber(med_netwr.Value) >= 36363636 ) ) {
				     ds_dtl.SetColumn(0, "HEL_REG"	, "1");
				} else {
					ds_dtl.SetColumn(0, "HEL_REG"	, "2");
				}
			}

			var strCode = ds_dtl.getColumn(0, "HEL_REG");
			hel_reg_onChanged(strCode, strCode, "","","");			

			// 건설공사대장통보 대상여부
			var sHel_reg = ds_dtl.GetColumn(0, "HEL_REG");
			if ( length(sHel_reg) == 0 ) {
				tab_hdel.head.cb_hel_reg.Enable   = true;	
			} else {
				tab_hdel.head.cb_hel_reg.Enable   = false;
			}

			// 청구구분별 총합계
			fn_tot_bill_sum();

			// 수주미생성 견적의 경우 청구구분을 헤더청구로 기본설정처리
			if (length(F_VBELN) == 0)
			{
				chk_bill_gbn.Value = "H";	// default = H (헤더청구)
			} 

			// 빌링필터조건 SET   
			cb_bill_gbn.Value = ds_dtl.GetColumn(0, "BILL_GBN");  
			fn_bill_filter(); 

			// 문서유형명 SET
			ed_auart_nm.text = ds_auart_cd.GetColumn(ds_auart_cd.FindRow("CODE", ds_dtl.GetColumn(0, "AUART"))
													, "CODE_NAME");

			// 제품군명 SET
			ed_spart_nm.text = gfn_GetListCodeName(F_FORM_ID, GC_CDGB_SPART, ds_dtl.GetColumn(0, "SPART"));

			// 수주승인일이 존재할 경우
			if (length(F_RECAD_DA) > 0 && F_RECAD_DA > '00000000')
			{
				fn_recad_da_exist(false);

				ed_status.Text = "수주승인완료";
			}
			// 수주승인일이 존재하지 않을 경우
			else
			{
				// 프로젝트번호가 존재할 경우
				if (length(F_VBELN) > 0)
				{
					fn_recad_da_exist(false);

					ed_status.Text = "수주생성완료";
				}
				else
				{
					// 견적진행상태가 수주의 경우만 처리
					if ( F_ZPRGFLG == "61")
					{
						// 2012.10.19  입력 항목 할성
						fn_recad_da_exist(true);

						ed_status.Text = "견적낙찰성공";

						// 신규 수주건인 경우
						// 품목 그리드의 체크박스를 수정불가능하게 처리하여 
						// 모든 품목이 다 수주생성되도록 처리
						tab_hdel.item.grid_item.SetCellProp("body",1,"Edit","none"); 
						ds_list_item.FireEvent = false;
						for (var i = 0; i < ds_list_item.RowCount(); i++)
						{
							ds_list_item.SetColumn(i, "CHK"	, 1);
							ds_list_item.SetColumn(i, "FLAG", "I");	// 수주생성을 위해 모두 입력(I)으로 처리
						}
						ds_list_item.FireEvent = true;
					}
					else
					{
						fn_recad_da_exist(false);
					}
					if ( tab_hdel.head.chk_shchk.Value != "X" && ( left(ds_dtl.GetColumn(0,"AUART"), 2) == "ZS" || left(ds_dtl.GetColumn(0,"AUART"), 2) == "ZN" ) ) {
						Alert("상해검수 프로젝트의 경우 상해검수여부 체크확인 요망");
					  }
				}

			//해외경우 현장명 수정 가능하도록 
			if ( ds_dtl.GetColumn(0, "VBELN") == '' || ds_dtl.GetColumn(0, "VBELN") == null)
			//if (length(F_VBELN) == 0)
			{
			  if ( left(ds_dtl.GetColumn(0,"AUART"), 2) == "ZE" || 
			       left(ds_dtl.GetColumn(0,"AUART"), 2) == "ZC" ||
			       left(ds_dtl.GetColumn(0,"AUART"), 2) == "ZT" )
			  {
			    // 2013.04.02 청구계획 일괄복사 기능 활성화
			    tab_hdel.item.btn_plan_copy.Enable = true;

			    ed_bstkd.Enable = true;

			    //해외 부품수출(ZC), 중계무역(ZT)인 경우 납기일 변경 -> 2013.03.08 신미정 
			    if ( left(ds_dtl.GetColumn(0,"AUART"), 2) == "ZC" || 
			         left(ds_dtl.GetColumn(0,"AUART"), 2) == "ZT" )
				{ 
					cal_vdatu.Enable = true;
				}
				else
				{
					cal_vdatu.Enable = false;
				}
			  }
			  else	{
				// 2013.04.02 청구계획 일괄복사 기능 비활성화
			    tab_hdel.item.btn_plan_copy.Enable = false;
			  }
			}
			else
			{
			  ed_bstkd.Enable = false;
			  cal_vdatu.Enable = false;
			  // 2013.04.02 청구계획 일괄복사 기능 비활성화
			  tab_hdel.item.btn_plan_copy.Enable = false;
			}			

			// 조회 변경적용완료
			ds_dtl.ApplyChange();
			ds_list_partner.ApplyChange();  
			ds_list_item.ApplyChange();  
			ds_list_bill.ApplyChange(); 
			ds_list_txt.ApplyChange(); 

			// 임시 삭제 Btn 제어 
			if( F_TMPSEARCH == "N" ) {			
				btn_tmp_delete.Enable  = false;
			} else {
				btn_tmp_delete.Enable  = true;
			}

			// CI00001: "조회를 완료했습니다."
			gfn_showMsg("CI00001"); 
		  }

		} else {
			fn_recad_da_exist(false);

			//  trace("fn_Query SUCCESS!!! 건수 없음");
			// CW00010 : "데이터가 없습니다."
			gfn_showMsg("CW00010");  

		}
		//zsdt0215 현장개설일자 연동 지시자 조회. 2020.04.kjlee
		nsfRequest("query"
					, "sso0060N/findSopind"
					, "ds_dtl=ds_dtl ds_sopind=ds_sopind"
					, "ds_sopind=ds_sopind ds_error=ds_error"
					, ""
					, "fn_afterQuery_sopind"
				  );

		//건물용도대분류 세팅 : 2020.12 jss
		if( length(trim(ds_dtl.GetColumnNF(0, "WWBLD"))) > 0 ) {
			var wwbld = trim(ds_dtl.GetColumnNF(0, "WWBLD"));
			var rowIdx = ds_list_build.FindRowNF("BUILDUSE", wwbld);
			if(length(trim(ds_list_build.GetColumnNF(rowIdx, "BUILDUSE_L"))) > 0 ){
			  	var rowIdxL = ds_list_build_l.FindRowNF("BUILDUSE_L", trim(ds_list_build.GetColumnNF(rowIdx, "BUILDUSE_L")));
				tab_hdel.head.cb_wwbld_l.index = rowIdxL;
			}
		 }

		 ds_list_lifnrchk.Filter("CODE != '2'"); //42164 영업(SD) 계약형태 시 공동수급 미표기 할수 있도로 Filter 조건 추가 요청 : 2021.03.18 jss

		}
	}

	if (length(trim(ed_vbeln.Value)) > 0)		// 수주생성 후 프로젝트가 존재하는 경우
	{
		tab_hdel.head.cb_achtp.Enable     = false;	// 건축허가유형
		tab_hdel.head.cal_achdt.Enable    = false;	// 건축허가일
		tab_hdel.head.ed_achno.Enable     = false;	// 건축허가번호	
		tab_hdel.head.cb_soptp.Enable     = false;	// 현장개설 여부
		tab_hdel.head.cal_sopdt.Enable    = false;	// 현장개설 예정일자 
	} else {
		tab_hdel.head.cb_achtp.Enable     = true;	// 건축허가유형	

		if ( tab_hdel.head.cb_achtp.Value != "X" ) {
			tab_hdel.head.cal_achdt.Enable    = false;	// 건축허가일
			tab_hdel.head.ed_achno.Enable     = false;	// 건축허가번호		
		} else {
			tab_hdel.head.cal_achdt.Enable    = true;	// 건축허가일
			tab_hdel.head.ed_achno.Enable     = true;	// 건축허가번호		
		}

		tab_hdel.head.cb_soptp.Enable     = true;	// 현장개설 여부
		tab_hdel.head.cal_sopdt.Enable    = true;	// 현장개설 예정일자 

		if ( tab_hdel.head.cb_achtp.Value != "X" ) {
		 tab_hdel.head.cb_achtp.Value 	  = "";
		}
		if ( tab_hdel.head.cb_soptp.Value != "X" ) {
		  tab_hdel.head.cb_soptp.Value 	  = "";		//현장개설여부
		}
	}

	// 해외현장 by pass 
	if ( left(ds_dtl.GetColumn(0,"AUART"), 2) == "ZE" || 
		 left(ds_dtl.GetColumn(0,"AUART"), 2) == "ZC" || 
		 left(ds_dtl.GetColumn(0,"AUART"), 2) == "ZT") {
		if (length(trim(tab_hdel.head.cal_sopdt.Value)) = 0) {
			tab_hdel.head.cb_soptp.Value      = "X";								// 현장개설 여부
			tab_hdel.head.cal_sopdt.Value     = substr(gfn_getCurrDate(),0,8);		// 현장개설 예정일자 : 현재일자
		}
		tab_hdel.head.cb_soptp.Enable     = false;
		tab_hdel.head.cal_sopdt.Enable    = false;
	} 

	ds_sto.DeleteAll();
	ds_sto.AppendRow();

	ds_sto.setColumn(0,'ordno',ds_dtl.GetColumn(0, "QTNUM"));
	ds_sto.setColumn(0,'ordseq',ds_dtl.GetColumn(0, "QTSER"));
	ds_sto.setColumn(0,'vbeln',ds_dtl.GetColumn(0, "VBELN"));
	nsfRequest("querySTO"
	   , "sso0060N/isStoOrder"
	   , "ds_sto=ds_sto"
	   , "ds_error=ds_error"
	   , "isStoOrder="+quote(false)
	   , ""
	   );
} 


/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_afterSave(svcId, errCode, errMsg)
{   
	F_PROCESSING = false; // 2013.03.21 처리중 플래그 해제

	// 처리중 메세지바 종료처리
	fn_destroyWait();  

	if(ds_error.rowcount() > 0) {    
		gfn_showMsgBar(ds_error.GetColumn(0,"ERRMSG"), ds_error.GetColumn(0,"ERRCODE"));  
		gfn_openMsgWin(ds_error.GetColumn(0,'ERRMSG'), 'W','A');
	} else {  

		gfn_showAlert("CI00005");  // CI00005: "처리를 완료했습니다.

		// 저장 변경적용완료
		ds_dtl.ApplyChange();
		ds_list_partner.ApplyChange();  
		ds_list_item.ApplyChange();  
		ds_list_bill.ApplyChange(); 
		ds_list_txt.ApplyChange(); 

		//저장 완료 후 '무상보수 개월수','공사용사용 개월수','납기일' 수정 못하도록 상태 변경
		tab_hdel.item.ed_repmo.Enable = false;
		tab_hdel.item.ed_wormo.Enable = false;
		tab_hdel.item.cal_edatu.Enable = false;

		// 저장완료 후 재조회
		fn_query();
	}
} 



/********************************************************************************
* 기      능   : 오더유형코드목록 조회 CALL BACK
********************************************************************************/
function fn_afterQuery_auart(errCode, errMsg) 
{
	return;
}  

/********************************************************************************
* 기      능   : 건물용도 코드목록 조회 CALL BACK
********************************************************************************/
function fn_afterQuery_bdtyp(svcId, errCode, errMsg) 
{
	gfn_InsertRowCombo(ds_list_build, 0, "N", "Y", "BUILDUSE", "STEXT"); // 선택 행 추가
	tab_hdel.head.cb_wwbld.index = 0;
	tab_hdel.item.cb_wwbld.index = 0; 
}   

/********************************************************************************
* 기      능   : 지급조건 코드목록 조회
********************************************************************************/
function fn_query_zterm() 
{
	nsfRequest("query"												// srvId
				, "combo/comboZterm"								// url
				, "ds_list_zterm=ds_list_zterm"						// inDsList
				, "ds_list_zterm=ds_list_zterm ds_error=ds_error"	// outDsList
				, ""												// strParam
				, "fn_afterQuery_zterm"								// CallBackFunc
				); 
}   

/********************************************************************************
* 기      능   : 지급조건 코드목록 조회 CALL BACK
********************************************************************************/
function fn_afterQuery_zterm(svcId, errCode, errMsg) 
{
	// HEAD의 지급조건은 현금(0001), 어음(0002) 만 보이도록 Filtering
	gfn_copyDs(ds_list_zterm, ds_list_zterm_head, "CODE=='0001' || CODE=='0002'"||
	                                              "CODE=='LC01' || CODE=='LL01'"||
	                                              "CODE=='LU01' || CODE=='LU02'"||
	                                              "CODE=='LU03' || CODE=='LU04'"||
	                                              "CODE=='PA01' || CODE=='TB01'"||
	                                              "CODE=='TF01' || CODE=='W001'");

	gfn_InsertRowCombo(ds_list_zterm	 , 0, "N", "Y", "CODE", "CODE_NAME"); // 선택 행 추가 
	gfn_InsertRowCombo(ds_list_zterm_head, 0, "N", "Y", "CODE", "CODE_NAME"); // 선택 행 추가 
	cb_zterm.index = 0;
}   

