}

// 청구구분 선택 변경 시
function fn_bill_gbn_onclick(pValidChkYN)
{
	if (ds_dtl.RowCount() == 0) return;

	// 행추가 조건 항목 오류검증
	if (pValidChkYN == "Y")
	{
		if (fn_valid_chk("bill_gbn") == false)  return;  
	} 

	// 청구구분별 합계 재산출 
	fn_tot_bill_sum();

	// 필터구분 재설정 및 필터
	cb_bill_gbn.Value = chk_bill_gbn.Value;  
	fn_bill_filter();
}

