/********************************************************************************
* 기      능   : 조회 1
********************************************************************************/
function fn_query() {

	ds_template.ClearData();	// 조회 후 기존 정보 제거  // 2012.10.14

	ds_cond.ClearData();
	ds_cond.AddRow();

	ds_cond.SetColumn(0, "MANDT", G_MANDT         );
	ds_cond.SetColumn(0, "QTNUM", ds_arg.getColumn(0, "QTNUM") );	// 견적번호
	ds_cond.SetColumn(0, "QTSER", ds_arg.getColumn(0, "QTSER") );	// 견적차수

	//if (F_GUBUN = "T")	{	// 2013.02.28 기술검토요청에서 호출 시 처리
		ds_cond.SetColumn(0, "QTSEQ", ds_arg.getColumn(0, "QTSEQ") );	// 일련번호
	//}

	ds_cond.SetColumn(0, "MATNR", ds_arg.getColumn(0, "MATNR") );	// 자재번호
	F_MATNR = ds_arg.getColumn(0, "MATNR");

	if (length(trim(F_MATNR)) == 0)		{
		gfn_showAlert("CW10025","품목의 자재번호를 확인하십시요.");
		fn_close();
	}

	ds_cond.setColumn(0, "SPRAS", G_LANG);
	ds_cond.SetColumn(0, "VBELN", ds_arg.getColumn(0, "VBELN") );	// 프로젝트번호
	ds_cond.SetColumn(0, "POSNR", ds_arg.getColumn(0, "POSNR") );	// 판매문서품목
	ds_cond.SetColumn(0, "HOGI",  ds_arg.getColumn(0, "HOGI") );	// 호기

	ds_error.Clear();

	cnt = 0;

	fn_createWait("",this);     // 처리중입니다. 메시지 처리

	if (F_GUBUN == "T")	{	// 2013.02.28 기술검토 요청에서 호출 시 반영.
		if ( trim(ds_arg.getColumn(0, "GUBUN")) == "SO")	// 수주생성 정보의 사양
		{
			nsfRequest("queryTemplate"
					 , "sso0062/sofind"
					 , "ds_cond=ds_cond ds_temp=ds_template ds_detail=ds_detail"
					 , "ds_detail=ds_detail ds_template=ds_temp ds_error=ds_error"
					 , ""
					 , "fn_afterQuery");
		}
		else
		{
			nsfRequest("queryTemplate"
					 , "sso0062/qtfind_tech"
					 , "ds_cond=ds_cond ds_temp=ds_template ds_detail=ds_detail"
					 , "ds_detail=ds_detail ds_template=ds_temp ds_error=ds_error"
					 , ""
					 , "fn_afterQuery");
		}
	}else	{
		if ( trim(ds_arg.getColumn(0, "GUBUN")) == "SO")	// 수주생성 정보의 사양
		{

			nsfRequest("queryTemplate"
					 , "sso0062/sofind"
					 , "ds_cond=ds_cond ds_temp=ds_template ds_detail=ds_detail"
					 , "ds_detail=ds_detail ds_template=ds_temp ds_error=ds_error"
					 , ""
					 , "fn_afterQuery");
		}
		else
		{
			nsfRequest("queryTemplate"
					 , "sso0062/qtfind"
					 , "ds_cond=ds_cond ds_temp=ds_template ds_detail=ds_detail"
					 , "ds_detail=ds_detail ds_template=ds_temp ds_error=ds_error"
					 , ""
					 , "fn_afterQuery");
		}
	}
}


//=========================================================================================
// [ PART 4]
// Callback 함수 정의 : callback 함수명은 메인 함수의 fn_xxxx() 에서 xxxx 부분은 fn_afterXXXX()
// 형태가 되도록 작성 
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(svcId, errCode, errMsg) {

	fn_destroyWait();

	if(ds_error.GetRowCount() > 0) {
		if (ds_error.GetColumn(0, "ERRCODE") == "CW10025")	{
			gfn_showMsg("CW10025", ds_error.GetColumn(0, "ERRMSG"));
		}else	{
			gfn_showMsg(ds_error.GetColumn(0, "ERRCODE"));
		}
		return false;
	}else	{
		gfn_showMsg("CI00002", ds_template.RowCount() + "");
	}

    if (svcId == "queryTemplate") {
		fn_template("", "ds_template");
	}
}

/********************************************************************************
* 기      능   : fn_template 화면에 표시
********************************************************************************/
function fn_template(popupYn, ds) {

	var mandt     = "";
	var qtnum     = "";
	var qtseq     = "";
	var qtser     = "";
	var prseq     = "";
	var atkla     = "";
	//var ds_flag   = "";
	var prd       = "";
	var user_prd  = "";
	var prh       = "";
	var h         = "head";
	var t         = "Text";
	var searchRow = 0;
	var user_cnt  = 0;
	var zprdgbn   = ds_detail.GetColumn(0, "ZPRDGBN");	// 제품구분

	var obj_template;
	var obj_user;

	var obj0 = div_template.grd_template0;
	var obj1 = div_template.grd_template1;
	var obj2 = div_template.grd_template2;
	var obj3 = div_template.grd_template3;
	var obj4 = div_template.grd_template4;
	var obj5 = div_template.grd_template5;
	var obj6 = div_template.grd_template6;
	var obj7 = div_template;

	switch (ds) {
		case "ds_template"	    : obj_template = object(ds_template.ID);      break;
		case "ds_template_duty"	: obj_template = object(ds_template_duty.ID); break;
		default                 : obj_template = object(ds_template.ID);      break;
	}

	for (var i = 0; i < obj_template.GetRowCount(); i++) {
		mandt   = obj_template.GetColumn(i, "MANDT");
		qtnum   = obj_template.GetColumn(i, "QTNUM");
		qtseq   = obj_template.GetColumn(i, "QTSEQ");
		qtser   = obj_template.GetColumn(i, "QTSER");
		prseq   = obj_template.GetColumn(i, "PRSEQ");
		//ds_flag = obj_template.GetColumn(i, "FLAG" );

		if (mandt == null)
		{
			obj_template.SetColumn(i, "MANDT", G_MANDT);
		}

		//if (i==0) {
		//	if (ds_flag=="I") spec_flag = "N";
		//	else              spec_flag = "";
		//}
		//if (spec_flag == "N") 
		//	obj_template.SetColumn(i, "FLAG" , "I");
	}

	ds_template0.ClearData(); ds_template0.copy(obj_template);
	ds_template1.ClearData(); ds_template1.copy(obj_template);
	ds_template2.ClearData(); ds_template2.copy(obj_template);
	ds_template3.ClearData(); ds_template3.copy(obj_template);
	ds_template4.ClearData(); ds_template4.copy(obj_template);
	ds_template5.ClearData(); ds_template5.copy(obj_template);
	ds_template6.ClearData(); ds_template6.copy(obj_template);

	if (zprdgbn == "ELV_01" || zprdgbn == "ELV_02" || zprdgbn == "SHN_01" ) {

		atkla = "EL_BASIC"  ; ds_template1.Filter("ATKLA == " + quote(atkla)); obj1.Visible = true; obj1.SetCellProp(h, 1, t, "기본의장 특성");
		atkla = "EL_INSIDE" ; ds_template2.Filter("ATKLA == " + quote(atkla)); obj2.Visible = true; obj2.SetCellProp(h, 1, t, "카내의장 특성");
		atkla = "EL_OUTSIDE"; ds_template3.Filter("ATKLA == " + quote(atkla)); obj3.Visible = true; obj3.SetCellProp(h, 1, t, "카외의장 특성");
		atkla = "EL_OPTION" ; ds_template4.Filter("ATKLA == " + quote(atkla)); obj4.Visible = true; obj4.SetCellProp(h, 1, t, "OPTION 특성");
		atkla = "EL_LAYOUT" ; ds_template5.Filter("ATKLA == " + quote(atkla)); obj5.Visible = true; obj5.SetCellProp(h, 1, t, "LAYOUT 특성");
		// 2020 브랜드
		atkla = "EL_OTHERS" ; ds_template6.Filter("ATKLA == " + quote(atkla)); obj6.Visible = true; obj6.SetCellProp(h, 1, t, "OTHER 특성");
		//atkla = " ";          ds_template0.Filter("ATKLA == " + quote(atkla) + " && Substr(PRHNAME, 0, 6) !=" + quote("REMARK")); obj0.SetCellProp(h, 1, t, "COMMON");
		ds_template0.Filter(" Substr(PRH, 0, 3) =" + quote("CO_")); obj0.SetCellProp(h, 1, t, "COMMON");
		obj0.Left = 2708;
		obj7.Scroll = true;

	} else if (zprdgbn == "ESC_01" || zprdgbn == "SHN_02") {

		atkla = "ES_BASIC" ; ds_template1.Filter("ATKLA == " + quote(atkla)); obj1.Visible = true; obj1.SetCellProp(h, 1, t, "BASIC 특성");
		atkla = "ES_OPTION"; ds_template2.Filter("ATKLA == " + quote(atkla)); obj2.Visible = true; obj2.SetCellProp(h, 1, t, "OPTION 특성");
		atkla = "ES_SAFETY"; ds_template3.Filter("ATKLA == " + quote(atkla)); obj3.Visible = true; obj3.SetCellProp(h, 1, t, "SAFETY 특성");
		atkla = "ES_EXTER" ; ds_template4.Filter("ATKLA == " + quote(atkla)); obj4.Visible = true; obj4.SetCellProp(h, 1, t, "EXTERIOR 특성");
		obj5.Visible = false; ds_template5.ClearData();
		ds_template0.Filter(" Substr(PRH, 0, 3) =" + quote("CO_")); obj0.SetCellProp(h, 1, t, "COMMON");
		obj0.Left = 1808;
		obj7.Scroll  = true;

	} else if (zprdgbn == "MW_01") {

		atkla = "MW_BASIC" ; ds_template1.Filter("ATKLA == " + quote(atkla)); obj1.Visible = true; obj1.SetCellProp(h, 1, t, "BASIC 특성");
		atkla = "MW_OPTION"; ds_template2.Filter("ATKLA == " + quote(atkla)); obj2.Visible = true; obj2.SetCellProp(h, 1, t, "OPTION 특성");
		atkla = "MW_SAFETY"; ds_template3.Filter("ATKLA == " + quote(atkla)); obj3.Visible = true; obj3.SetCellProp(h, 1, t, "SAFETY 특성");
		atkla = "MW_EXTER" ; ds_template4.Filter("ATKLA == " + quote(atkla)); obj4.Visible = true; obj4.SetCellProp(h, 1, t, "EXTERIOR 특성");
		obj5.Visible = false; ds_template5.ClearData();
		ds_template0.Filter(" Substr(PRH, 0, 3) =" + quote("CO_")); obj0.SetCellProp(h, 1, t, "COMMON");
		obj0.Left = 1808;
		obj7.Scroll  = true;

	} else if (zprdgbn == "AP_01") {

		atkla = "AP_BASIC"; ds_template1.Filter("ATKLA == " + quote(atkla)); obj1.Visible = true; obj1.SetCellProp(h, 1, t, "BASIC 특성");
		obj2.Visible = false; ds_template2.ClearData();
		obj3.Visible = false; ds_template3.ClearData();
		obj4.Visible = false; ds_template4.ClearData();
		obj5.Visible = false; ds_template5.ClearData();
		obj6.Visible = false; ds_template6.ClearData();
		obj0.Visible=false;
		obj7.Scroll  = false;
		obj7.ResizeScroll();

	} else if (zprdgbn == "PSD_01") {

		atkla = "PSD_BASIC"; ds_template1.Filter("ATKLA == " + quote(atkla)); obj1.Visible = true; obj1.SetCellProp(h, 1, t, "BASIC 특성");
		atkla = "PSD_SASH" ; ds_template2.Filter("ATKLA == " + quote(atkla)); obj2.Visible = true; obj2.SetCellProp(h, 1, t, "SASH 특성");
		atkla = "PSD_STEEL"; ds_template3.Filter("ATKLA == " + quote(atkla)); obj3.Visible = true; obj3.SetCellProp(h, 1, t, "STEEL 특성");
		atkla = "PSD_ELEC" ; ds_template4.Filter("ATKLA == " + quote(atkla)); obj4.Visible = true; obj4.SetCellProp(h, 1, t, "ELEC 특성");
		obj5.Visible = false; ds_template5.ClearData();
		obj6.Visible = false; ds_template6.ClearData();
		obj0.Visible = false;
		obj7.Scroll  = true;      obj7.ResizeScroll();

	} else if (zprdgbn == "SP_01" || zprdgbn == "SV_01") {

		obj1.Visible = true; obj1.SetCellProp(h,2,t,"값");
		obj2.Visible = false;
		obj3.Visible = false;
		obj4.Visible = false;
		obj5.Visible = false;
		obj6.Visible = false;
		obj0.Left    = 455;
		if (zprdgbn == "SP_01")	{
			obj0.Visible = false;
		}
		obj7.Scroll  = false;
	}
	obj7.resizeScroll();

	if (cnt == 0) {
		ds_user1.ClearData(); ds_user1.copy(ds_template1);
		ds_user2.ClearData(); ds_user2.copy(ds_template2);
		ds_user3.ClearData(); ds_user3.copy(ds_template3);
		ds_user4.ClearData(); ds_user4.copy(ds_template4);
		ds_user5.ClearData(); ds_user5.copy(ds_template5);
		ds_user6.ClearData(); ds_user6.copy(ds_template6);
		cnt++;
	}

	if (popupYn == "Y") {

		for (var a=1; a<=6; a++) {

			switch (a) {
				case 1	: obj_template = object(ds_template1.ID); obj_user = object(ds_user1.ID); break;
				case 2	: obj_template = object(ds_template2.ID); obj_user = object(ds_user2.ID); break;
				case 3	: obj_template = object(ds_template3.ID); obj_user = object(ds_user3.ID); break;
				case 4	: obj_template = object(ds_template4.ID); obj_user = object(ds_user4.ID); break;
				case 5	: obj_template = object(ds_template5.ID); obj_user = object(ds_user5.ID); break;
				case 6	: obj_template = object(ds_template6.ID); obj_user = object(ds_user6.ID); break;
				default : break;
			}

			for (var i=0; i<obj_template.GetRowCount(); i++) {
				prd       = obj_template.GetColumn(i, "PRD");
				atkla     = obj_template.GetColumn(i, "ATKLA");
				prh       = obj_template.GetColumn(i, "PRH"  );
				searchRow = obj_user.searchRow("ATKLA ==" + quote(atkla) + " && PRH ==" + quote(prh) );
				user_prd  = Trim(obj_user.GetColumn(searchRow, "PRD"));
				user_cnt  = Trim(obj_user.GetColumn(searchRow, "CNT"));
				//ds_flag   = obj_template.GetColumn(i, "FLAG");

				obj_template.SetColumn(i, "CNT", user_cnt);

				if (prd != null && prd != "" ) {
					if (prd != user_prd) {
						obj_template.SetColumn(i, "PRD", prd);
						//if (ds_flag == "I") obj_template.SetColumn(i, "FLAG", "I");
						//else                obj_template.SetColumn(i, "FLAG", "U");
					}
				} else {
					if (user_prd != null && user_prd != "") {
						obj_template.SetColumn(i, "PRD" , user_prd);
						//if (ds_flag == "I") obj_template.SetColumn(i, "FLAG", "I");
						//else                obj_template.SetColumn(i, "FLAG", "U");
					}
				}
			}
		}
	}
}


