function fn_resize() {
	this.onsize = "";

	//this.window.showwindow("Maximize");
	window.top    = GetPlatformInfo("WorkArea","top");
	window.left   = GetPlatformInfo("WorkArea","left");
	window.width  = GetPlatformInfo("WorkArea","width");
	window.height = GetPlatformInfo("WorkArea","height");

	div_template.width  = window.width  - 20;
	div_template.height = window.height - 80;

	//tab.main.div_header.Width       = tab.width-4;
	//div_template.Width = tab.width-2;

	//tab.main.div_header.height      = tab.height-25;
	//tab.template.div_template.height= tab.height-30;

	//tab.main.div_header.grd_detail.height = tab.height-305;
	//tab.main.div_header.grd_detail.width  = tab.width-10;

	div_template.grd_template0.height = div_template.height-25;
	div_template.grd_template1.height = div_template.height-25;
	div_template.grd_template2.height = div_template.height-25;
	div_template.grd_template3.height = div_template.height-25;
	div_template.grd_template4.height = div_template.height-25;
	div_template.grd_template5.height = div_template.height-25;
	// 2020 브랜드
	div_template.grd_template6.height = div_template.height-25;
}
