function fn_OnLoadCompleted(obj) {
	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm(true);

	fn_init_form(); 	// 폼 초기화

}

/********************************************************************************
* 기      능   : form UnloadCompleted 
********************************************************************************/
function fn_OnUnloadCompleted(obj) {
	return gfn_isFormUnload(obj);
}

//=========================================================================================
// [ PART 3 ]
// Main Process Event 정의(조회,입력,삭제,저장,인쇄,form닫기)
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 1
********************************************************************************/
function fn_query() {

	if (fn_valid_chk("query") == false)  return;

	cmdFlag = "DISP";

	var cdKtokd  = trim(cboKtokd.Value);								//고객구분					
	var cdKunnr = div_Ktokd.ed_buyr_cd.text;							//고객번호
	var cdStcd2 = txtStcd2.Text;
	var cdStcd1 = txtStcd1.Text;
	var name1 = txtName1.Text;
	var cmd = cmdFlag;	

	ds_cond.ClearData();
	ds_cond.AddRow();

	ds_cond.SetColumn(0, "KTOKD", cdKtokd);
	ds_cond.SetColumn(0, "KUNNR", cdKunnr);
	ds_cond.SetColumn(0, "STCD2", cdStcd2);
	ds_cond.SetColumn(0, "STCD1", cdStcd1);	
	ds_cond.SetColumn(0, "NAME1", name1);
	ds_cond.SetColumn(0, "CMD", cmd);	

	// 서비스 호출
	nsfRequest("query"									// srvId
				, "Sso0100/find"						// url
				, "ds_cond=ds_cond"						// inDsList
				, "ds_list=ds_list ds_error=ds_error"	// outDsList
				, ""									// strParam
				, "fn_afterQuery"						// CallBackFunc
				);

    fn_createWait("",this);     // 처리중입니다. 메시지 처리
}

/********************************************************************************
* 기      능   : 입력
********************************************************************************/
function fn_input() 
{
	cboKtokd.SetFocus();
	// 입력 모드 
	ufn_setGroup(false);

	if(ds_list.rowcount == 0) 
	{
		ds_list.AddRow();
	}

	cmdFlag = "INST";
	ed_Ktokd.Value = "Z001";
	ed_waers = "KRW";
	ed_taxkd.Value = "1";

	ed_Ktokd_Value_Change();

	//ufn_setUpdateMode(false);
	//ufn_enableBtn("save", true);
}

/********************************************************************************
* 기      능   : 폼 초기화
********************************************************************************/
function fn_init_form() {

	div_vbvg.Border = "none";

	div_Ktokd.SetFocus();
} 

/********************************************************************************
* 기      능   : 닫기  
********************************************************************************/
function fn_close() {
	close();
}

/********************************************************************************
* 기      능   : 액셀  
