function fn_excel() {
}

//=========================================================================================
// [ PART 4]
// Callback 함수 정의 : callback 함수명은 메인 함수의 fn_xxxx() 에서 xxxx 부분은 fn_afterXXXX()
// 형태가 되도록 작성 
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(svcId, errCode, errMsg) { 

	trace("ds_result.SaveXML()");
    trace(ds_result.SaveXML());

	fn_destroyWait();

	if(ds_list.rowcount == 0) 
	{
		ds_list.AddRow();
	}

	trace(ds_list.SaveXML());
	trace("svcId = " + svcId);

	trace("fn_Save FAILED!!! "
		   + "[e_type : " 			+ ds_result.GetColumn(0,"E_TYPE") + "] "
		   + "[c_smsg return value : "+ ds_result.GetColumn(0,"C_SMSG") + "]"
		   );		       



	if(ds_error.rowcount() > 0) {  
		trace("fn_Query FAILED!!! "
		       + "[idx : " 			+ ds_error.GetColumn(0,"IDX") + "] "
		       + "[error message : "+ ds_error.GetColumn(0,"ERRMSG") + "]"
		       );		       
		gfn_showMsg("CE00001");
		// error 정보 표시 
		gfn_openWSErrorWin(grd_list, ds_list, ds_error, "FLAG");
	} else {

		if(svcId == "query") {

			trace("query trace List");
		}

		if(svcId == "update") {
			ed_kunnr.Text = ds_result.GetColumn(0,"C_SMSG");
			fn_query();

			trace("update trace List");
		}

		if(svcId == "insert") {
			ed_kunnr.Text = ds_result.GetColumn(0,"C_SMSG");
			fn_query();

			trace("insert trace List");
		}

	}

	trace("fn_afterQuery 호출 end");
}


// 항목 오류 검증
// pProcGb : 처리구분 (query:조회,excel:엑셀)
function fn_valid_chk(pProcGb)
{   
 	switch(pProcGb)
	{
		case "query" :   
			if (length(div_Ktokd.ed_buyr_cd.text) == 0)
			{
				if (length(txtName1.text) == 0)
				{
					if (length(txtStcd2.text) == 0)
					{
						alert("고객번호을 선택하시기 바랍니다.");
						gfn_SetFocus(div_Ktokd.ed_buyr_cd.text,"Y"); 
						return false;
					}
				}
			} 

			if (length(cboKtokd.Text) == 0)
			{
				alert("고객유형을 선택하시기 바랍니다.");
				gfn_SetFocus(div_Ktokd.ed_buyr_cd.text,"Y"); 
				return false;
			} 

			break;  

		case "excel" :   // 엑셀

			if (ds_list.RowCount() <=0)
			{
				alert("조회된 자료가 없습니다.");
				//gfn_showMsg("CI00002", ds_list.RowCount() + "");
				return false;
			}
			break;
		default: 
			break; 
	}   

	return true;
}

