			gfn_SetFocus(Div0.ed_pstlz,"Y"); 
			return false;
		}
		if (length(Div0.ed_land1.Value) == 0)
		{
			alert("국가는 필수입력 입니다.");
			gfn_SetFocus(Div0.ed_land1,"Y"); 
			return false;
		}
		if (length(ed_regio.Value) == 0)
		{
			alert("지역은 필수입력 입니다.");
			gfn_SetFocus(ed_regio,"Y"); 
			return false;
		}
		if (length(Div0.ed_lzone.Value) == 0)
		{
			alert("운송지역은 필수입력 입니다.");
			gfn_SetFocus(Div0.ed_lzone,"Y"); 
			return false;
		}
		if (length(Div0.ed_telf1.Value) == 0)
		{
			alert("전화번호는 필수입력 입니다.");
			gfn_SetFocus(Div0.ed_telf1,"Y"); 
			return false;
		}
		if (length(div_vbvg.cb_frvb.Value) == 0)
		{
			alert("부서코드는 필수입력 입니다.");
			gfn_SetFocus(div_vbvg.cb_frvb,"Y"); 
			return false;
		}
		if (length(ed_waers.Value) == 0)
		{
			alert("통화는 필수입력 입니다.");
			gfn_SetFocus(ed_waers,"Y"); 
			return false;
		}
		if (length(ed_taxkd.Value) == 0)
		{
			alert("세금분류는 필수입력 입니다.");
			gfn_SetFocus(ed_taxkd,"Y"); 
			return false;
		}
	}

	return true;
}

