/********************************************************************************
* 기      능   : 저장 
********************************************************************************/
function vbvgDataBinding() 
{
	ds_list.SetColumn(0, "VKBUR", div_vbvg.cb_frvb.value);
	ds_list.SetColumn(0, "VKGRP", div_vbvg.cb_frvg.value); 
}
/********************************************************************************
* 기      능   : 저장 
********************************************************************************/
function fn_save() 
{
	if(ds_list.GetUpdate() == false) {
		 alert("변경 된사항 이 없습니다." ); 

		 return false; 
    } 

    if(fn_save_data_chk() == false) {
		return false; 
    }

	vbvgDataBinding();

	if(!Confirm("저장하시겠습니까?")){
	   return;
	}

	if (cmdFlag <> "INST"){
		cmdFlag= "UPDT";	
	}

	var cdKtokd  = ds_ktokd.GetColCount(cboKtokd.Index, "CODE");		//고객구분							
	var cdKunnr = div_Ktokd.ed_buyr_cd.text;							//고객번호
	var cdStcd2 = txtStcd2.Text;
	var cdStcd1 = txtStcd1.Text;
	var name1 = txtName1.Text;
	var cmd = cmdFlag;	

	ds_param.ClearData();
	ds_param.AddRow();

	ds_param.SetColumn(0, "KTOKD", cdKtokd);
	ds_param.SetColumn(0, "CMD", cmd);	

	ds_param.SetColumn(0, "KTOKD", cdKtokd);
	ds_param.SetColumn(0, "KUNNR", cdKunnr);
	ds_param.SetColumn(0, "STCD2", cdStcd2);
	ds_param.SetColumn(0, "STCD1", cdStcd1);	
	ds_param.SetColumn(0, "NAME1", name1);
	ds_param.SetColumn(0, "CMD", cmd);	

	// 서비스 호출
	nsfRequest("update"									// srvId
				, "Sso0100/update"						// url
				, "ds_param = ds_param ds_list=ds_list ds_result=ds_result"						// inDsList
				, "ds_result=ds_result ds_error=ds_error"	// outDsList
				, ""									// strParam
				, "fn_afterQuery"						// CallBackFunc
				);

    fn_createWait("",this);     // 처리중입니다. 메시지 처리

}

/********************************************************************************
* 기      능   : 저장 
********************************************************************************/
function fn_insert() 
{
	if(ds_list.GetUpdate() == false) {
		 alert("변경 된사항 이 없습니다." ); 

		 return false; 
    } 

	if(fn_save_data_chk() == false) {
		return false; 
    }

	vbvgDataBinding();

	if(!Confirm("저장하시겠습니까?")){
	   return;
	}

	ds_param.SetColumn(0, "KTOKD", cdKtokd);
	ds_param.SetColumn(0, "CMD", cmd);	

	var cdKtokd  = ds_ktokd.GetColCount(cboKtokd.Index, "CODE");		//고객구분					
	var cdKunnr = div_Ktokd.ed_buyr_cd.text;							//고객번호
	var cdStcd2 = txtStcd2.Text;
	var cdStcd1 = txtStcd1.Text;
	var name1 = txtName1.Text;
	var cmd = "INST";	

	ds_param.ClearData();
	ds_param.AddRow();

	ds_param.SetColumn(0, "KTOKD", cdKtokd);
	ds_param.SetColumn(0, "CMD", cmd);	

	ds_param.SetColumn(0, "KTOKD", cdKtokd);
	ds_param.SetColumn(0, "KUNNR", cdKunnr);
	ds_param.SetColumn(0, "STCD2", cdStcd2);
	ds_param.SetColumn(0, "STCD1", cdStcd1);	
	ds_param.SetColumn(0, "NAME1", name1);
	ds_param.SetColumn(0, "CMD", cmd);	

	// 서비스 호출
	nsfRequest("update"									// srvId
				, "Sso0100/update"						// url
				, "ds_param = ds_param ds_list=ds_list ds_result=ds_result"						// inDsList
				, "ds_result=ds_result ds_error=ds_error"	// outDsList
				, ""									// strParam
				, "fn_afterQuery"						// CallBackFunc
				);

    fn_createWait("",this);     // 처리중입니다. 메시지 처리
}

