		ed_locco.Enable 			= false;				//발행구분
		div_vbvg.cb_frvb.Enable 	= true;					//부서코드
		div_vbvg.cb_frvg.Enable 	= false;				//팀코드
		ed_waers.Enable 			= true;					//통화
		ed_taxkd.Enable 			= true;					//세금분류
		Div2.ed_vtweg.Enable 		= false;				//유통경로
		//Div0.ed_spart.Enable 		= false; 				//제품군
		//ed_zkunnr.Enable 			= false; 	 		 	//영업사원번호
		//ed_zname1.Enable 			= false; 	 		 	//사원이름
		//ed_zstras.Enable 			= false; 	 		 	//사원전화번호
		Div0.ed_dname.Enable 		= false; 				//담당자
		Div0.ed_namev.Enable 		= false; 				//직위
		//Div0.ed_telno.Enable 		= false; 				//전화번호
		ed_nam1.Enable 				= false;		 		//전자세금계산서 담당자
		ed_dpm1.Enable 				= false;		 		//전자세금계산서 부서
		ed_hdp1.Enable 				= false;		 		//담당자 핸드폰
		ed_eml1.Enable 				= false;		 		//담당자 이메일
		ed_nam2.Enable 				= false;		 		//전자세금계산서 담당자
		ed_dpm2.Enable 				= false;		 		//전자세금계산서 부서
		ed_hdp2.Enable 				= false;		 		//담당자 핸드폰
		ed_eml2.Enable 				= false;		 		//담당자 이메일
	}

	//기본값 설정
	if (ed_ktokd.Value == "Z001") {
		ed_waers = "KRW";
		ed_taxkd.Value = "1";
	} else if (ed_ktokd.Value == "Z002") {
		ed_waers = "KRW";
		ed_taxkd.Value = "2";
	} else if (ed_ktokd.Value == "Z003") {
		ed_waers = "USD";
		ed_taxkd.Value = "0";
	} else if (ed_ktokd.Value == "Z004") {
		ed_waers = "KRW";
		ed_taxkd.Value = "1";
	}

	Div2.ed_vtweg.Value = "10";
}

// 국가
function btn_Land1_popup(obj)
{
	var param = Div0.ed_land1.Text;
	var arg  = "popFlag="+"nation"; // pop구분값 ( nation:국가 / area:운송지역 )
		arg += " pCdNm="+quote(param); // 넘길 값

	var arr = gfn_openPopupUrl("COM::Com0060.xml", true, arg);

	if (arr != null && arr != 0)
	{
		Div0.ed_land1.Text = arr[0];
		Div0.ed_land1.SetFocus();

	}
}

