function fn_OnLoadCompleted(obj) {
	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm(true);

	fn_init_form(); 	// 폼 초기화

}

/********************************************************************************
* 기      능   : form UnloadCompleted 
********************************************************************************/
function fn_OnUnloadCompleted(obj) {
	return gfn_isFormUnload(obj);
}

/********************************************************************************
* 기      능   : 폼 초기화
********************************************************************************/
function fn_init_form() {

	cal_zrqdat_fm.Text = gfn_getCurrDate();	// 현재일자 10일전의 일자부터
	//cal_zrqdat_fm.Text = addDate(gfn_getCurrDate(), -10);	// 현재일자 10일전의 일자부터
	//cal_zrqdat_to.Text = gfn_getCurrDate();

}

//=========================================================================================
// [ PART 3 ]
// Main Process Event 정의(조회,입력,삭제,저장,인쇄,form닫기)
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 1
********************************************************************************/
var fn_query_chk = false; //중복조회 방지용 flag

function fn_query() {

	if (!fn_query_validation())	{
		return;
	}

	if(!fn_query_chk) {
		fn_query_chk = true;
	} else {
		gfn_showAlert("CW10025", "조회중입니다..");
		return;
	}

	fn_createWait("",this);     // 처리중입니다. 메시지 처리

	ds_error.ClearData();
	ds_cond.DeleteAll();
	ds_cond.AddRow();

	ds_cond.SetColumn(0, "FR_ZRQDAT", cal_zrqdat_fm.Value);
	ds_cond.SetColumn(0, "TO_ZRQDAT", cal_zrqdat_to.Value);
	ds_cond.SetColumn(0, "ZKUNNR_Z3", ed_sman.Text);

	nsfRequest(  "query"
			   , "sso0210/findList"
			   , "ds_cond=ds_cond ds_list=ds_list"
			   , "ds_list=ds_list ds_error=ds_error"
			   , ""
			   , "fn_afterQuery"
			  );
}

/********************************************************************************
* 기      능   : 조회조건 필수 체크
********************************************************************************/
function fn_query_validation()
{
	var vMSG = "요청기간";

	// 필수조회조건 체크
	if(length(trim(cal_zrqdat_fm.Value)) != 8)	{
		gfn_showAlert("CW00001", vMSG);
		cal_zrqdat_fm.SetFocus();
		return false;
	}
/*
	if(length(trim(cal_zrqdat_to.Value)) != 8)	{
		gfn_showAlert("CW00001", vMSG);
		cal_zrqdat_to.SetFocus();
		return false;
	}
*/
	return true;
}

/********************************************************************************
* 기      능   : 닫기  
********************************************************************************/
function fn_close() {
	close();
}

//=========================================================================================
// [ PART 4]
// Callback 함수 정의 : callback 함수명은 메인 함수의 fn_xxxx() 에서 xxxx 부분은 fn_afterXXXX()
// 형태가 되도록 작성 
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(svcId, errCode, errMsg) {

	fn_query_chk = false; //중복조회 방지용 flag
	fn_destroyWait();

	if(ds_error.rowcount() > 0) 	{
		gfn_showAlert("CE00001");
		return;
	}

	gfn_showMsg("CI00002", ds_list.RowCount() + "");
}

/********************************************************************************
* 기      능   : 공통코드가 조회된 후의 Callback 함수 
********************************************************************************/
function fn_completeInit(sCode, sDsObj, sCodeType) {
	return;
}

