/********************************************************************************
* 기      능   : 액셀  
********************************************************************************/
function fn_excel() {
	if ( ds_list.RowCount() > 0 )
		gfn_exportExcel(grd_list, Sso0210.Title);
	else
		gfn_showAlert("CW00010");
}




function grd_list_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
		var arg  = "fa_pspid=" + quote(ds_list.GetColumn(nRow, "PSPID"));
			arg += " fa_seq=" + quote(ds_list.GetColumn(nRow, "SEQ"));
			arg += " fa_hogi=" + quote(ds_list.GetColumn(nRow, "HOGI"));
		var url = "";

		url = "Sso::Sso0211.xml";

		gfn_openPopUpUrlHdel(url, true, arg);
}
