Window.Script:
/*
 *****************************************************************************************
 * 기      능   :
 * 작  성  자   :
 * 작성  일자   :
 * ----------------------------------------------------------------------------------------
 * HISTORY    :
 ******************************************************************************************
 */
#include "LIB::commons.js";	// 공통 스크립트

//=========================================================================================
// [ PART 1 ]
// Form에서 사용될 전역변수를 선언
// FORM에서 사용되는 전역변수는 대문자로 작성하며, F_XXX 의 형태를 갖도록 한다.
//=========================================================================================
var userGroup = "";

//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj)
{

	// Form 로드 이벤트 맨 첫줄에 기술
	gfn_initForm(true);

	userGroup = gfn_getUserGroup();

	if(userGroup == '10' || userGroup == '20')
	{
		cb_zzpmnum.Enable = true;

	}
	else if( userGroup == '30' )
	{
		cb_zzpmnum.Enable = true;

		tit_clearActionInfo();
		tit_addSearchActionInfo("ps02:PS0201001A_S32");
		tit_callService(
		""
		, ""
		, "ds_cond=ds_cond"
		, "ds_zzpmnum_cd=ds_zzpmnum_cd"
		, ""
		, ""   // callback function
		, ""
		, ""
		, true);
	}
	else if( userGroup == '40' )
	{
		cb_zzpmnum.Enable = true;
	}

	ed_inpday_f.value = gfn_getBeforeDate(gfn_getCurrDate(), 60);
	ed_inpday_t.value = gfn_getCurrDate();

	ds_cond.SetColumn(0,"USER_GRUP",userGroup);
	if (userGroup == "10")
	{
		ds_cond.SetColumn(0,"USER_CODE",G_VEND_CODE_E);
	}
	else if (userGroup == "20")
	{
		ds_cond.SetColumn(0,"USER_CODE",G_USER_ID);
	}
	else if (userGroup == "30")
	{
		ds_cond.SetColumn(0,"USER_CODE",G_AREA_CODE_E);
	}

	var codeArr = ["PS003"];
    var dsObjArr = [ds_zzactss];
    var addTypeArr = [G_CODE_ALL];
    gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);

	tit_clearActionInfo();
	tit_addSearchActionInfo("ps02:PS0201001A_S34");
	tit_addSearchActionInfo("wb01:WB0108001A_S1");

	tit_callService(
		""
		, ""
		, "ds_cond=ds_cond"
		, "ds_zzlifnr=ds_zzlifnr_cd ds_clss=ds_clss"
		, ""
		, "fn_afterQueryC"
		, ""
		, ""
		, true);

	gfn_showMsg("CI00007");
}

/********************************************************************************
* 기      능   :  협력사 조회 Callback
********************************************************************************/
function fn_afterQueryC(errCode, errMsg) {
	if ( userGroup == "10" )
	{
		ds_cond.fireevent = false;
		ds_cond.SetColumn(0, "ZZLIFNR", G_VEND_CODE_E);
		ds_cond.SetColumn(0, "ZZLIFNR_NM", G_VEND_NAME);
		ds_cond.fireevent = true;
	}
	else if ( userGroup == "30" || userGroup == "40")
	{
	}
}


/********************************************************************************
* 기      능   : 공통코드가 조회된 후의 Callback 함수
********************************************************************************/
function fn_completeInit(sCode, sDsObj, sCodeType)
{
	if(userGroup == "40")
	{
		cb_zzactss.Index = 0;
		cb_zzactss.Enable = true;
	}
	else if(userGroup == "30")
	{
		cb_zzactss.Index = 0;
		cb_zzactss.Enable = true;
	}
	else if(userGroup == "10")
	{
		cb_zzactss.Index = 0;
		cb_zzactss.Enable = false;
	}
}


/********************************************************************************
* 기      능   : form UnloadCompleted
********************************************************************************/
function fn_OnUnloadCompleted(obj)
{
	return gfn_isFormUnload(obj);
}


/********************************************************************************
* 기      능   : 조회
********************************************************************************/
function fn_query()
{
	ApplyData();

    if ( !gfn_checkFromToObj(ed_inpday_f, ed_inpday_t, true, st_date.value, 0, true) )
    {
		return;
	}

	tit_clearActionInfo();
	tit_addSearchActionInfo("wb01:WB0108001A_S2");
	tit_callService(
          ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list1=ds_list1"
        , ""
        , "fn_afterQuery"
        , ""
        , ""
        , true);

}


function ds_list1_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{

}


/********************************************************************************
* 기      능   : 닫기
********************************************************************************/
function fn_close()
{
	close();
}

//=========================================================================================
// [ PART 4]
// Callback 함수 정의 : callback 함수명은 메인 함수의 fn_xxxx() 에서 xxxx 부분은 fn_afterXXXX()
// 형태가 되도록 작성
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 Callback
********************************************************************************/
function fn_afterQuery(errCode, errMsg)
{
	gfn_showSearchMsg(grd_list1, errCode, errMsg);

}


/********************************************************************************
* 기      능   : 액셀
********************************************************************************/
function fn_excel()
{
	if( ds_list1.rowcount > 0 )
	{
		gfn_saveToExcel(grd_list1, true );
		break;
	}
}


/********************************************************************************
* 기      능   : 원프로젝트 팝업
********************************************************************************/
function btn_pjt_OnClick(obj)
{
	var arg = "pjt="+quote(ds_cond.GetColumn(0, 'PSPID'));
	gfn_openPopup("CS9601002A", true, arg);

	fn_query();
}


/********************************************************************************
* 기      능   : 리스트의 타이틀 클릭
********************************************************************************/
function grd_list1_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if ( nCell == 0 ) {
		return;
	}
	else if ( nCell == 1 ) {
		// 그리드 표시할 컬럼 선택
		gfn_selectAllGrid(obj,nCell,nCell,true);
	} else {
		// 그리드 정렬 처리
		gfn_sortGrid(obj,nCell);
	}
}

/********************************************************************************
* 기      능   : 사무소콤보 변경시 설치PM 콤보값 생성
********************************************************************************/
function cb_zzactss_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	ds_cond_cd.SetColumn(0,"ZZACTSS",strCode);
	cb_zzpmnum.value = "";

	tit_clearActionInfo();
	tit_addSearchActionInfo("ps02:PS0201001A_S41");

	tit_callService(
		""
		, ""
		, "ds_cond_cd=ds_cond_cd"
		, "ds_zzpmnum_cd=ds_zzpmnum_cd"
		, ""
		, "fn_afterZzactssChange"
		, ""
		, ""
		, true);

}

/********************************************************************************
* 기      능   : 조회 Callback  - 사무소 변경시
********************************************************************************/
function fn_afterZzactssChange(errCode, errMsg) {
	gfn_insertEmptyRow( ds_zzpmnum_cd, G_CODE_ALL );
	cb_zzpmnum.value = "";
}


function cb_tmcd_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
    ds_cond.SetColumn(0,"TMCD",ds_cond.GetColumn(0,"TMCD"));
}


function ds_cond_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{

}

/********************************************************************************
* 기      능   : 마스터 더블 클릭시
********************************************************************************/
function grd_list1_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if (nCell == 11) {
		var args = '_P_POSID=' + quote(ds_list1.GetColumn(nRow, 'POSID'));
		args += ' _P_ZSITE_NM=' + quote(ds_list1.GetColumn(nRow, 'ZSITE_NM'));
		args += ' _P_GBN_DESC=' + quote(ds_list1.GetColumn(nRow, 'GBN_DESC'));
		args += ' _P_RECV_USER=' + quote(ds_list1.GetColumn(nRow, 'RECV_USER'));
		args += ' _P_PHONE=' + quote(ds_list1.GetColumn(nRow, 'PHONE'));
		args += ' _P_MDATE=' + quote(ds_list1.GetColumn(nRow, 'MDATE'));
		args += ' _P_MESSAGE=' + quote(ds_list1.GetColumn(nRow, 'MESSAGE'));

		gfn_openPopup("WB0108001B", true, args);
	}
}
