/********************************************************************************
* 기      능   : 조회 
********************************************************************************/
function fn_query() 
{
	ds_list.row = -1;

	gfn_clearGridSortMark(grd_list);

	tit_clearActionInfo();

	tit_addSearchActionInfo("wb01:WB0199021A_S1");

	tit_callService(
          ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , true);	
}
/********************************************************************************
* 기      능   : 저장  
********************************************************************************/

var _ENC_PWD = "";

function fn_save() 
{
	// 변경 정보 
	if ( !gfn_isChangeDs(ds_list, true, "FLAG") ) 
	{
		return;
	}

	var f = "";
	for( var i = 0; i < ds_list.rowCount(); i++ ) 
	{
		f = ds_list.getColumn(i, "FLAG");
		if ( f == "I" || f == "U" ) 
		{
			if ( length( ds_list.getColumn(i, "USERNUMB") ) == 0 ) // 사용 ID 
			{	
				gfn_showAlert("CW00001", st_userNumb.value);
                ds_list.row = i;
				return;
			}

			if ( length( ds_list.getColumn(i, "USERNAME") ) == 0 ) // 사용 자 명 
			{	
				gfn_showAlert("CW00001", st_userName.value);
                ds_list.row = i;
				return;
			}

			if( charAt( ds_list.getColumn(i, "USERNUMB"), 0) != "Z" ) 
			{
				if ( length( ds_list.getColumn(i, "USERLANG") ) == 0 ) { // 언어 
					gfn_showAlert("CW00001", st_userLang.value);
					ds_list.row = i;
					return;
				}
				if ( length( ds_list.getColumn(i, "USERCODE") ) == 0 ) { // 업체 
					gfn_showAlert("CW00001", st_userCode.value);
					ds_list.row = i;
					return;
				}
				if ( length( ds_list.getColumn(i, "USERPASS") ) == 0 ) { // 업체 
					gfn_showAlert("CW00001", st_userPass.value);
					ds_list.row = i;
					return;
				}
				if ( length( ds_list.getColumn(i, "USEREXPR") ) == 0 ) { // 업체 
					gfn_showAlert("CW00001", st_userExpr.value);
					ds_list.row = i;
					return;
				}

				// 주민번호 - 더이상 사용하지 않음 2013.02.20 hss
				// if ( length(ds_list.getColumn(i, "USERJUNO")) > 0 && length(ds_list.getColumn(i, "USERJUNO")) != 13 ) 
				// {
					// gfn_showMsg("CW00001", st_userJuno.value);
					// ds_list.row = i;
					// return;
				// } 
				// if ( length(ds_list.getColumn(i, "USERJUNO")) == 13) 
				// {  // 외국인의 경우 111111-1111111로 세팅
					// if(!gfn_isValidJumin( left(ds_list.getColumn(i, "USERJUNO"), 6), right(ds_list.getColumn(i, "USERJUNO"), 7) ) && ds_list.getColumn(i, "USERJUNO") != "1111111111111")  
					// {
						// gfn_showMsg("CW00038", st_userJuno.value);
						// ds_list.row = i;
						// return;
					// }
				// }

				// e-mail 확인 
				if( length(ds_list.getColumn(i, "USERMAIL")) > 0 && !gfn_isValidEmail(ds_list.getColumn(i, "USERMAIL")) ) 
				{
					gfn_showMsg("CW00038", st_userMail.value);
					ds_list.row = i;
					return;
				}

				// 권한 자리수 확인 
				if ( length(ds_list.getColumn(i, "USERAUT1")) > 0 && length(ds_list.getColumn(i, "USERAUT1")) != 3 ) 
				{
					gfn_showMsg("CW00038", st_userAuth1.value + st_auth.value);
					ds_list.row = i;
					return;
				}
				if ( length(ds_list.getColumn(i, "USERAUT2")) > 0 && length(ds_list.getColumn(i, "USERAUT2")) != 3 ) 
				{
					gfn_showMsg("CW00038", st_userAuth2.value+ st_auth.value);
					ds_list.row = i;
					return;
				}
				if ( length(ds_list.getColumn(i, "USERAUT3")) > 0 && length(ds_list.getColumn(i, "USERAUT3")) != 3 ) 
				{
					gfn_showMsg("CW00038", st_userAuth3.value+ st_auth.value);
					ds_list.row = i;
					return;
				}
/*
				if ( length(ds_list.getColumn(i, "USERAUT3")) > 0 && length(ds_list.getColumn(i, "USERTEAM")) == 0 ) 
				{
					gfn_showMsg("CW00038", st_userAuth3.value+ st_auth.value);
					ds_list.row = i;
					return;
				}
*/
				if ( length(ds_list.getColumn(i, "USERAUT4")) > 0 && length(ds_list.getColumn(i, "USERAUT4")) != 3 ) 
				{
					gfn_showMsg("CW00038", st_userAuth4.value+ st_auth.value);
					ds_list.row = i;
					return;
				}
				if ( length(ds_list.getColumn(i, "USERAUT5")) > 0 && length(ds_list.getColumn(i, "USERAUT5")) != 3 ) 
				{
					gfn_showMsg("CW00038", st_userAuth5.value+ st_auth.value);
					ds_list.row = i;
					return;
				}
			}	// end z 
		}	// end flag 
	}

	// 그룹 정보에 입력할 데이터생성
	 ds_grup.clearData();
	var row = 0;
	for ( var i = 0; i < ds_list.rowCount(); i++ ) 
	{
		if ( charat( ds_list.getColumn(i, "USERNUMB") ) == "Z" ) 
		{
			continue;
		}

		if ( ds_list.getColumn(i, "FLAG") == "I") 
		{
			row = ds_grup.addRow();
			ds_grup.setColumn(row, "FLAG", "I");
			ds_grup.setColumn(row, "GNUMB", ds_list.getColumn(i, "USERNUMB"));
			ds_grup.setColumn(row, "GGRUP", ds_list.getColumn(i, "USERNUMB"));
		} 
		else if ( ds_list.getColumn(i, "FLAG") == "D") 
		{
			row = ds_grup.addRow();
			ds_grup.setColumn(row, "FLAG", "D");
			ds_grup.setColumn(row, "MANDT", ds_list.getColumn(i, "MANDT"));
			ds_grup.setColumn(row, "GNUMB", ds_list.getColumn(i, "USERNUMB"));
			ds_grup.setColumn(row, "GGRUP", ds_list.getColumn(i, "USERNUMB"));
		}
	}

	gfn_clearGridSortMark(grd_list);


   // 비밀번호 암호화  적용 2016.09.27. hss
   // 저장시 암호화 

	// if(orgpass != ds_list.GetColumn(0,"USERPASS"))
	// {
		// tit_callService(
		// "COMM_ACT"
		// , "getEncPwd"
		// , ""
		// , ""
		// , " ORG_PWD=" + ToUpper(ds_list.GetColumn(0,"USERPASS"))
		// , ""
		// , false
		// , true
		// , true);


		// ds_list.SetColumn(0,"USERPASS", _ENC_PWD);	
	// }

	/////db 암호화 ////


	tit_clearActionInfo();

	tit_addSaveActionInfo(
		  "wb01:WB0199021A_I1"
		, "wb01:WB0199021A_U1"
		, "wb01:WB0199021A_D1"
		, "FLAG");

	tit_addSaveActionInfo(
		  "wb01:WB0199021C_I1"
		, ""
		, "wb01:WB0199021C_D1"
		, "FLAG");

	tit_addSearchActionInfo("wb01:WB0199021A_S1");

	tit_callService(
          ""
        , ""
        , "ds_list=ds_list:U ds_groupList=ds_grup:A ds_cond=ds_cond"
        , "ds_list=ds_list"
        , ""
        , "fn_afterSave"
        , true);
}
/********************************************************************************
* 기      능   : 입력
********************************************************************************/
function fn_input() 
{
	var nRow = gfn_addGridRow(grd_list, -1, "FLAG");
	ed_userNumb.SetFocus();
	// 입력 모드 
	var isGroup = (charAt(ds_list.getColumn(nRow, "USERNUMB"), 0) == "Z");
	chk_group.value = isGroup;
	ufn_setGroup(false);
	ufn_setUpdateMode(false);
	ufn_enableBtn("save", true);
	if ( length( cal_userExpr.value ) == 0 ) 
	{ 
		cal_userExpr.value = gfn_getAfterDate(gfn_getCurrDate(), 90);
	}

	ed_userPass.value = "INIT1234";
}
/********************************************************************************
* 기      능   : 삭제 
********************************************************************************/
function fn_delete() 
{
	if (!gfn_showConfirm("CI00015","사용자")) 
	{
		return;
	}	
	gfn_deleteGridRow(grd_list, ds_list.row, "FLAG");	
}
/********************************************************************************
* 기      능   : 닫기  
********************************************************************************/
function fn_close() 
{
	close();
}
/********************************************************************************
* 기      능   : 복원 
********************************************************************************/
function fn_reset() 
{
	ds_list.Reset();
}
