/********************************************************************************
* 기      능   : 수정 모드인지 여부 설정   
********************************************************************************/
function ufn_setUpdateMode(isUpdate) 
{
	// 사용자 아이디 수정 불가능 
	ed_userNumb.enable = !isUpdate;
	chk_group.Enable = !isUpdate;
	btn_pSearch.Enable = isUpdate; // 협력사 변경시
	btn_pSearch_R.Enable = isUpdate; // 협력사 변경시
	btn_pSearch_B.Enable = isUpdate; // 협력사 변경시
	btn_pSearch_M.Enable = isUpdate; // 협력사 변경시
	btn_pSearch_E.Enable = isUpdate; // 협력사 변경시

	btn_usePgm.enable = isUpdate;
	btn_pwdInit.enable = ( isUpdate && !chk_group.value );
	btn_retire.enable = ( isUpdate && !chk_group.value );
	btn_useGroup.enable = ( isUpdate && !chk_group.value );

	// 그룹일 경우 삭제 불가 
	if( isUpdate ) 
	{
		ufn_enableBtn("delete",  !chk_group.value);
	//	btn_pSearch.enable = !chk_group.value;
	} 
	else 
	{
		ufn_enableBtn("delete", true);
	//	btn_pSearch.enable = true;
	}
}
/********************************************************************************
* 기      능   :  그룹으로 설정 
********************************************************************************/
function ufn_setGroup(isGroup) 
{
	btn_pSearch.enable = !isGroup;
	btn_pSearch_R.enable = !isGroup;
	btn_pSearch_B.enable = !isGroup;
	btn_pSearch_M.enable = !isGroup;
	btn_pSearch_E.enable = !isGroup;
	btn_pwdInit.enable = !isGroup;

	ed_userTele.enable = !isGroup;
	ed_userMbpn.enable = !isGroup;
	ed_userMail.enable = !isGroup;
	ed_userPass.enable = !isGroup;
	cal_userExpr.enable = !isGroup;
	cb_userLang.enable = !isGroup;
	ed_userAut1.enable = !isGroup;
	ed_userAut2.enable = !isGroup;
	ed_userAut3.enable = !isGroup;
	ed_userAut4.enable = !isGroup;
	ed_userAut5.enable = !isGroup;
	//med_userJuno.enable = !isGroup;

	if ( isGroup ) 
	{
		ed_userTele.value = "";
		ed_userMbpn.value = "";
		ed_userMail.value = "";
		ed_userPass.value = "";
		cal_userExpr.value = "";
		cb_userLang.value = "";
		ed_userAut1.value = "";
		ed_userAut2.value = "";
		ed_userAut3.value = "";
		ed_userAut4.value = "";
		ed_userAut5.value = "";
    	//med_userJuno.value = "";
		cal_userExpr.value = "";
	} 
	else 
	{
		if ( cal_userExpr.value == "" ) 
		{
			cal_userExpr.value = gfn_getAfterDate(gfn_getCurrDate(), 90);
		}	
	}
}
