/********************************************************************************
* 기      능   :  협력업체 찾기 
********************************************************************************/
function btn_pSearch_OnClick(obj)
{
	var arg = "comName=''";
	var rtn = gfn_openPopup("WB0101001C",true,arg);

	var gbn = substr(obj.ID,obj.ID.length -1,1);

	if ( rtn != null ) 
	{
		if (gbn == "h")
		{
			ds_list.setColumn(ds_list.row, "USERCODE", rtn[0]);
			ed_vendName.value = rtn[1];
		} else 
		{
			switch(gbn)
			{ 
				case 'R': ds_list.setColumn(ds_list.row, "USERCODE_R", rtn[0]);
				          ed_vend_name_R.value  = rtn[1];
				          break;
				case 'B': ds_list.setColumn(ds_list.row, "USERCODE_B", rtn[0]);
				          ed_vend_name_B.value  = rtn[1];
				          break;
				case 'M': ds_list.setColumn(ds_list.row, "USERCODE_M", rtn[0]);
				          ed_vend_name_M.value  = rtn[1];
				          break;
				case 'E': ds_list.setColumn(ds_list.row, "USERCODE_E", rtn[0]);
				          ed_vend_name_E.value  = rtn[1];
				          break;
			}

		}

	}
}
/********************************************************************************
* 기      능   :  변경시 마다 
********************************************************************************/
function ds_list_OnRowPosChanged(obj,nOldRow,nRow)
{
	if( nRow == -1 ) 
	{
		return;
	}

	if( ds_list.getColumn(nRow, "FLAG") == "I") 
	{
		ed_userNumb.SetFocus();
		// 입력 모드 
		var isGroup = (charAt(ds_list.getColumn(nRow, "USERNUMB"), 0) == "Z");
		chk_group.value = isGroup;
		ufn_setGroup(false);
		ufn_setUpdateMode(false);
		ufn_enableBtn("save", true);
		if ( length( cal_userExpr.value ) == 0 ) 
		{ 
			cal_userExpr.value = gfn_getAfterDate(gfn_getCurrDate(), 90);
		}	
	} 
	else 
	{
		ds_list.fireevent = false;
		var isGroup = (charAt(ds_list.getColumn(nRow, "USERNUMB"), 0) == "Z");
		chk_group.value = isGroup;
		ufn_setGroup(isGroup);
		ufn_setUpdateMode(true);
		ufn_enableBtn("save", true);
		ds_list.fireevent = true;
	}
}
/********************************************************************************
* 기      능   :  데이터 변경시 처리 
********************************************************************************/
function ds_list_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	gfn_changeFlag( obj, strColumnID, nRow, "FLAG" );
}
/********************************************************************************
* 기      능   :  데이터 row 변경 시 
********************************************************************************/
function ds_list_CanRowPosChange(obj,nOldRow,nRow)
{
	if ( !chk_group.Enable ) 
	{
		return true;
	}
	// Z 로 시작하면 그룹 정보 
	if ( chk_group.value ) 
	{
		// 그룹 초기화 시킴.  
		if( charAt(ed_userNumb.value, 0) != "Z" ) 
		{
			gfn_showMsg("CW00038", st_userNumb.value);
			ed_userNumb.setFocus();
			return false;
		}
	} 
	else 
	{
		//그룹이 아닐경우 Z 로 시작 불가 
		if( charAt(ed_userNumb.value, 0) == "Z" ) 
		{
			gfn_showMsg("CW00038", st_userNumb.value);
			ed_userNumb.setFocus();
			return false;
		}
	}
	return true;
}
/********************************************************************************
* 기      능   :  지우기 
********************************************************************************/
function btn_trash_OnClick(obj)
{
	ds_cond.setColumn(0, "USERCODE", "");
	ed_userCode_s.value = "";
}
/********************************************************************************
* 기      능   :  협력업체 찾기 
********************************************************************************/
function btn_pSearch2_OnClick(obj)
{
	var arg = "comName=''";
	var rtn = gfn_openPopup("WB0101001C",true,arg);

	if ( rtn != null ) 
	{
		ds_cond.setColumn(0, "USERCODE", rtn[0]);
		ed_userCode_s.value = rtn[1];
	}
}
/********************************************************************************
* 기      능   :  그룹인지 여부 
********************************************************************************/
function ed_userNumb_OnChanged(obj,strText)
{
	if ( length(obj.value) > 0 && charat(obj.value, 0 ) == "Z" ) 
	{
		chk_group.value = "1";
	} 
	else 
	{
		chk_group.value = "0";
	}

	chk_group_OnClick(chk_group,chk_group.value);
}

/********************************************************************************
* 기      능   : 액셀  
********************************************************************************/
function fn_excel() {
	gfn_saveToExcel(grd_list, true );
}
