var orgpass = "";
function fn_OnLoadCompleted(obj) 
{
	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm();
	// 버튼처리
	//ufn_enableBtn("input",  false);
	//ufn_enableBtn("delete", false);
	ufn_enableBtn("save",   false);
	//ufn_enableBtn("reset",  false);
	if(g_user_id == "2017814" || g_user_id == "2022532" || g_user_id == "2031205") {
		ds_cond.SetColumn(0,"REGDPT","M");		
	}else if(g_user_id == "2030861" || g_user_id == "2030144") {
		ds_cond.SetColumn(0,"REGDPT","B");
	}else if(g_user_id == "2029758" || g_user_id == "2018900") {
		ds_cond.SetColumn(0,"REGDPT","R");
	}else if(g_user_id == "2029847") {
		ds_cond.SetColumn(0,"REGDPT","E");
	}else if(g_user_id == "2018853") {
		ds_cond.SetColumn(0,"REGDPT","P");
	}else if(g_user_id == "2028115") {
		ds_cond.SetColumn(0,"REGDPT","D");
	}else {
		ds_cond.SetColumn(0,"REGDPT","");
	}


	fn_searchCode();
	// Form 로드 이벤트 맨 첫줄에 기술   
	gfn_initForm();
	if ( isExistVar("fa_usernumb", true) ) {
       ed_userNumb_s.Value = fa_usernumb;

	   fn_query();
	} else {     
	// 메세지
	   gfn_showMsg("CI00007");
	} 
	cb_compyn.Index = 0;	// 승인여부 초기화
	cb_dtp.Index = 0;
	//cb_dpt.Enable = false;
}
