function fn_query() 
{
	ds_list.row = -1;

	gfn_clearGridSortMark(grd_list);

	tit_clearActionInfo();

	tit_addSearchActionInfo("wb01:WB0199021K_S1");

	tit_callService(
          ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , true);	
}

function fn_afterGetid() 
{
	ds_list.row = -1;

	gfn_clearGridSortMark(grd_list);

	tit_clearActionInfo();

	tit_addSearchActionInfo("wb01:WB0199021K_S3");

	tit_callService(
          ""
        , ""
        , "ds_list2=ds_list2"
        , "ds_numb=ds_numb"
        , ""
        , "fn_afterSave"
        , true);	
}
