/********************************************************************************
* 기      능   :  협력업체 찾기 
********************************************************************************/
function btn_pSearch_OnClick(obj)
{
	var arg = "comName=''";
	var rtn = gfn_openPopup("WB0101001C",true,arg);

	var gbn = substr(obj.ID,obj.ID.length -1,1);

	if ( rtn != null ) 
	{
		if (gbn == "h")
		{
			ds_list.setColumn(ds_list.row, "USERCODE", rtn[0]);
			ed_vendName.value = rtn[1];
		} else 
		{
			switch(gbn)
			{ 
				case 'R': ds_list.setColumn(ds_list.row, "USERCODE_R", rtn[0]);
				          ed_vend_name_R.value  = rtn[1];
				          break;
				case 'B': ds_list.setColumn(ds_list.row, "USERCODE_B", rtn[0]);
				          ed_vend_name_B.value  = rtn[1];
				          break;
				case 'M': ds_list.setColumn(ds_list.row, "USERCODE_M", rtn[0]);
				          ed_vend_name_M.value  = rtn[1];
				          break;
				case 'E': ds_list.setColumn(ds_list.row, "USERCODE_E", rtn[0]);
				          ed_vend_name_E.value  = rtn[1];
				          break;
				case '': ds_list.setColumn(ds_list.row, "USERCODE", rtn[0]);
				          ed_vendName.value  = rtn[1];
				          break;

			}

		}

	}
}
/********************************************************************************
* 기      능   :  변경시 마다 
********************************************************************************/
function ds_list_OnRowPosChanged(obj,nOldRow,nRow)
{
	if( nRow == -1 ) 
	{
		return;
	}

	if(ds_list.GetColumn(nRow,"REGDPT") == "보수" && ds_list.GetColumn(nRow,"USERGBN") == "협력사")	{
		ds_list.SetColumn(nRow,"USERAUT1","Z10") && ds_list.SetColumn(nRow,"USERAUT2","R10");
	}else if(ds_list.GetColumn(nRow,"REGDPT") == "영업" && ds_list.GetColumn(nRow,"USERGBN") == "협력사") {
		ds_list.SetColumn(nRow,"USERAUT1","Z10") && ds_list.SetColumn(nRow,"USERAUT3","B10");
	}else if(ds_list.GetColumn(nRow,"REGDPT") == "자재" && ds_list.GetColumn(nRow,"USERGBN") == "협력사") {
		ds_list.SetColumn(nRow,"USERAUT1","Z10") && ds_list.SetColumn(nRow,"USERAUT4","M10");
	}else if(ds_list.GetColumn(nRow,"REGDPT") == "설치" && ds_list.GetColumn(nRow,"USERGBN") == "협력사") {
		ds_list.SetColumn(nRow,"USERAUT1","Z10") && ds_list.SetColumn(nRow,"USERAUT5","E10");
	} else {
	}

	if(ds_list.GetColumn(nRow,"COMP_YN") == "승인완료")	{
		btn_retire.Enable = false;
		btn_return.Enable = false;
		btn_useGroup.Enable = true;
		cb_userLang.Enable = true;
		ed_userAut1.Enable = true;
		ed_userAut2.Enable = true;
		ed_userAut3.Enable = true;
		ed_userAut4.Enable = true;
		ed_userAut5.Enable = true;
		cb_vkgrp.Enable = false;

	}else if(ds_list.GetColumn(nRow,"COMP_YN") == "반려"){
		btn_retire.Enable = false;
		btn_return.Enable = false;
		btn_useGroup.Enable = false;
		ds_list.SetColumn(nRow,"USERAUT1","") && ds_list.SetColumn(nRow,"USERAUT2","") && ds_list.SetColumn(nRow,"USERAUT3","") && ds_list.SetColumn(nRow,"USERAUT4","") && ds_list.SetColumn(nRow,"USERAUT5","");
		cb_userLang.Enable = false;
		ed_userAut1.Enable = false;
		ed_userAut2.Enable = false;
		ed_userAut3.Enable = false;
		ed_userAut4.Enable = false;
		ed_userAut5.Enable = false;
		cb_vkgrp.Enable = false;
	}else{
		btn_retire.Enable = true;
		btn_return.Enable = true;
		btn_useGroup.Enable = false;
		cb_userLang.Enable = true;
		ed_userAut1.Enable = true;
		ed_userAut2.Enable = true;
		ed_userAut3.Enable = true;
		ed_userAut4.Enable = true;
		ed_userAut5.Enable = true;
		cb_vkgrp.Enable = true;
	}

	// if( ds_list.getColumn(nRow, "FLAG") == "I") 
	// {
		// ed_userNumb.SetFocus();
		// 입력 모드 
		// var isGroup = (charAt(ds_list.getColumn(nRow, "USERNUMB"), 0) == "Z");
		// chk_group.value = isGroup;
		// ufn_setGroup(false);
		// ufn_setUpdateMode(false);
		// ufn_enableBtn("save", true);
		// if ( length( cal_userExpr.value ) == 0 ) 
		// { 
			// cal_userExpr.value = gfn_getAfterDate(gfn_getCurrDate(), 90);
		// }	
	// } 
	// else 
	// {
		// ds_list.fireevent = false;
		// var isGroup = (charAt(ds_list.getColumn(nRow, "USERNUMB"), 0) == "Z");
		// chk_group.value = isGroup;
		// ufn_setGroup(isGroup);
		// ufn_setUpdateMode(true);
		// ufn_enableBtn("save", true);
		// ds_list.fireevent = true;
	// }
}
