/********************************************************************************
* 기      능   :  E-mail 보내는 function
********************************************************************************/
function ufn_send_email(email_to, title, body) {
	tit_clearActionInfo();	
	tit_callService(
		"COMM_ACT"
		, "sendmail"
		, ""
		, ""
		," SEND_USER=ADMIN@HDEL.CO.KR"
			+ " RECE_USER=" + quote(email_to)
			+ " MESGTITL=" + quote( title ) 
			+ " MESGBODY=" + quote( body ) 
		, ""  //"fn_afterQuery"
		, false
		, true);			
}
