/********************************************************************************
* 기      능   : 조회 
********************************************************************************/
function fn_query() {

	if(Length(Trim(ed_pjt.Text)) < 6)
	{
		return;
	}

	ds_list2.Clear();

	tit_clearActionInfo();
	tit_addSearchActionInfo("wb01:WB0199061A_S1");		                         	
	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , ""
        , ""
        , true);



     ed_FileName.value = '';

//alert(ds_list.SaveXML());	                

}

/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
	gfn_showMsg("CI00002",ds_list.RowCount()+"");

	ds_list2.ClearData();
	if(ds_list.RowCount() > 0) fn_query2(0);
}



/********************************************************************************
* 기      능   : grid double click 시 조회 
********************************************************************************/
function fn_query2(nRow) {
	ds_list2.ClearData();

//alert(ds_list.SaveXML());

	ds_cond2.ClearData();
	ds_cond2.AddRow();
	ds_cond2.setColumn(0, "GBN", ds_list.GetColumn(nRow,"GBN"));
	ds_cond2.setColumn(0, "PSPID", ds_list.GetColumn(nRow,"PSPID"));
	ds_cond2.setColumn(0, "POSID", ds_list.GetColumn(nRow,"POSID"));

//alert(ds_cond2.SaveXML());	

	tit_clearActionInfo();
	tit_addSearchActionInfo("wb01:WB0199061A_S2", null, true);

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond2"
        , "ds_list2=ds_list"
        , ""
        , "fn_afterQuery2"
        , ""
        , ""
        , true);

}

/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery2(errCode, errMsg) {
	grd_list2.SetCellProp("head",0,"text",ds_list2.rowcount+" 건");
	//gfn_showMsg("CI00002",ds_list2.RowCount()+"");
}


/********************************************************************************
* 기      능   : 첨부파일 열기 
********************************************************************************/
function btn_openFile_OnClick(obj)
{
	fileDialog.open();

	if ( length(fileDialog.FilePath) == 0 
		|| length(fileDialog.FileName ) == 0 ) {
		return;
	}

	var fileSize = f_tmp.GetLength(fileDialog.FilePath + "\\" + fileDialog.FileName );
	if ( fileSize <= 0 ) {
		gfn_showAlert("CW00048");
		return;
	}

	if ( fileSize > toNumber(F_SIZE)) {
		gfn_showAlert("CW00047", "10 Mbytes" );	
		return;
	}

	/*
	//한글제목 입력금지(한글제목깨짐)
	for( var i = 0; i < length( fileDialog.FileName); i++ ) {
		if ( lengthb(charAt( fileDialog.FileName, i)) == 2 ) {
			gfn_showMsg("CW00213");
			return;
		}
	}
	*/

	// 그리드 저장 대상 로우
	if(ds_list.FindRow("CHECK", 1)==-1)
	{
		gfn_showMsg('CW00046');
		return;
	}

	ed_FileName.value = fileDialog.FileName;

	for(var i=0; i<ds_list.GetRowCount(); i++)
	{
		if(ds_list.GetColumn(i, "CHECK")==1)
		{
			Trace(fileDialog.FileName);
			// 파일정보
			//if(length(ed_FileName.value) > 0 )
			//{
				ds_list.setColumn(i, "FNAME", fileDialog.FileName);
				ds_list.setColumn(i, "FSIZE", fileSize);
				ds_list.setColumn(i, "FPATH", fileDialog.FilePath);

				ds_list.setColumn(i, "GBN", cb_gbn.Value);
			//} 

		}
	}

//alert(ds_list.SaveXML());	

//Alert("FNAME : " + ds_list.GetColumn(0, "FNAME"));
//Alert("FSIZE : " + ds_list.GetColumn(0, "FSIZE"));
//Alert("FPATH : " + ds_list.GetColumn(0, "FPATH"));
//Alert("FNAME : " + fileDialog.FileName);
//Alert("FSIZE : " + fileSize);
//Alert("ds_list : " + ds_list.SaveXML());
//alert(ds_cond.SaveXML());	 


}



/********************************************************************************
* 기      능   : 저장 
********************************************************************************/
function fn_save() {

	if(gfn_isNull(ed_FileName, true, st_insuName.Value)) return false; // 첨부파일



	// 그리드 저장 대상 로우
	if(ds_list.FindRow("CHECK", 1)==-1)
	{
		gfn_showMsg('CW00046');
		return;
	}


	ds_list.Filter("CHECK==1");
	ds_list_tmp.copyF(ds_list);
	ds_list.unFilter();	

	if(fn_checkSave()){

//Alert("httpFile : " + httpFile );	
//alert("FPATH : " + ds_list_tmp.getColumn(0, "FPATH"));	
//alert("FNAME : " + ds_list_tmp.getColumn(0, "FNAME"));	
//alert("fileName : " + fileName);


		var fileName = "";
		//fileName = left(GetDate(),8)+"_"+right(GetDate(),6)+"_"+ds_list_tmp.getColumn(0, "FNAME");

		var thumbext = ds_list_tmp.GetColumn(0, "FNAME"); //파일을 추가한 input 값
		thumbext = substr(thumbext, ufn_lastIndexOf(thumbext)); //확정자값 가져오기 
		thumbext = ToLower(thumbext); //소문자로 변경

		fileName = left(GetDate(),8)+"_"+right(GetDate(),6)+"_"+ds_list_tmp.getColumn(0, "GBN")+"_"+ds_list_tmp.getColumn(0, "PSPID")+"."+thumbext;


		var arr = tit_fileWrite(httpFile
						, "QM"
						, ds_list_tmp.getColumn(0, "FPATH")
						, ds_list_tmp.getColumn(0, "FNAME")
						, fileName// 실제 db에 저장될 파일 이름
						, "U"	  // time stamp를 붙이지 않을 경우 U 사용 
						, "");    // ds_list_tmp.getColumn(i, "CS108_QAD") );//삭제할 이전 파일명 

//alert("xx");						
//alert("arr[0] : " + arr[0]);	
//alert("arr[1] : " + arr[1]);


//alert(ds_list_tmp.SaveXML());			
		if ( arr[0] == "S" ) 
		{
			for(var i=0; i<ds_list_tmp.GetRowCount(); i++)
			{
				ds_list_tmp.setColumn(i, "FNAME_APP", arr[2]);

				//=======update/insert (마스터)구분=================
				ds_cond3.ClearData();
				ds_cond3.AddRow();
				ds_cond3.setColumn(0, "GBN",   ds_list_tmp.GetColumn(i, "GBN"));
				ds_cond3.setColumn(0, "PSPID", ds_list_tmp.GetColumn(i, "PSPID"));
				ds_cond3.setColumn(0, "POSID", ds_list_tmp.GetColumn(i, "POSID"));
				ds_cond3.setColumn(0, "FNAME", ds_list_tmp.GetColumn(i, "FNAME"));

				tit_clearActionInfo();
				tit_addSelectExActionInfo("wb01:WB0199061A_S4", "wb01:WB0199061A_I1", "wb01:WB0199061A_U1");				                           
				tit_callService(
					""
					, ""
					, "ds_cond3=ds_cond3"
					, ""
					, ""
					, ""
					, ""
					, ""
					, true  // ""

				);

			}

			//디테일 INSERT
			tit_clearActionInfo();
			tit_addMultiActionInfo("wb01:WB0199061A_I2");		                       
			tit_callService(
			  ""
			, ""
			, "ds_list_tmp=ds_list_tmp"
			, ""
			, ""
			, "fn_afterSave" 
			, ""
			, ""
			, true);

		}
		else if ( arr[0] != "S" )
		{
			gfn_showMsg("CW00035"); 
			return;
		}



	}



}

/********************************************************************************
* 기      능   : 저장전에 자료 검증 
********************************************************************************/
function fn_checkSave()
{

	for(var i=0; i<ds_list_tmp.GetRowCount(); i++)
	{
		ds_cond3.ClearData();
		ds_cond3.AddRow();
		ds_cond3.setColumn(0, "GBN", ds_list_tmp.GetColumn(i, "GBN"));
		ds_cond3.setColumn(0, "PSPID", ds_list_tmp.GetColumn(i, "PSPID"));
		ds_cond3.setColumn(0, "POSID", ds_list_tmp.GetColumn(i, "POSID"));
		ds_cond3.setColumn(0, "FNAME", ds_list_tmp.GetColumn(i, "FNAME"));

		tit_clearActionInfo();
		tit_addSearchActionInfo("wb01:WB0199061A_S3");		                         	
		tit_callService(
			""
			, ""
			, "ds_cond3=ds_cond3"
			, "ds_list3=ds_list3"
			, ""
			, ""
			, ""
			, ""
			, true);

//Alert("CNT : " + ds_list_tmp.GetColumn(0, "CNT") );

		if( ds_list3.GetColumn(0, "CNT") > 0 )
		{
			global.FRM_BOTTOM.img_msgType.imageId = "bottom_icon_caution";
			global.FRM_BOTTOM.st_msg.color = "yellow";       
			global.FRM_BOTTOM.st_msg.value = "같은 호기에 동일한 파일이 존재합니다.";
			return false;
		}

	}



	return true;
}



