/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_afterSave(errCode, errMsg) {
	if ( errCode!= 0 ) 
	{		
		gfn_showMsg("CE00001");		
	} 
	else 
	{
		gfn_showMsg("CI00003");
		ds_list_tmp.DeleteAll();
		fn_query();
	}
}



