/********************************************************************************
* 기      능   : 헤더클릭
********************************************************************************/
function grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{

	if ( nCell == 0 ) {
		return;
	}
	else if ( nCell == 1 ) {
		// 그리드 표시할 컬럼 선택 
		gfn_selectAllGrid(obj,nCell,nCell,true);
	} else {
		// 그리드 정렬 처리 
		gfn_sortGrid(obj,nCell);
	}	


}



/********************************************************************************
* 기      능   : 헤더클릭
********************************************************************************/
function grd_list2_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if ( nCell == 0 ) {
		return;
	}
	else if ( nCell == 1 ) {
		// 그리드 표시할 컬럼 선택 
		gfn_selectAllGrid(obj,nCell,nCell,true);
	} else {
		// 그리드 정렬 처리 
		gfn_sortGrid(obj,nCell);
	}	
}


function grd_list2_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if ( nCell == 4 )
	{
		if ( length(ds_list2.getColumn(nRow, "FNAME")) > 0) {
			// 첨부 파일 처리 

//alert(	ds_list2.getColumn(nRow, "FNAME") );

			saveDialog.FileName = ds_list2.getColumn(nRow, "FNAME");//tit_getLocalFileName(ds_list2.getColumn(nRow, "FNAME"));

			if (saveDialog.open())
			{
				tit_fileRead(
					httpFile
					, "QM"
					, ds_list2.getColumn(nRow, "FNAME_APP")
					, saveDialog.FilePath
					, saveDialog.FileName);
			}
		}
	}
	else if(nCell == 5)
	{
		if( length(ds_list2.getColumn(nRow, "FNAME")) > 0) {
		   // 첨부 파일 처리 
		   fn_createWait("", this);

			var FilePath = "c:\\Temp" ;		
			var FileExec = "c:\\Temp\\" + ds_list2.getColumn(nRow, "FNAME");

			saveDialog.FileName = ds_list2.getColumn(nRow, "FNAME");

			var rMsg = tit_fileRead(
										httpFile
										, "QM"  	  // 저장 디렉토리
										, ds_list2.getColumn(nRow, "FNAME_APP")	// 서버에서 전송받을 이름
										, FilePath    // 로컬 경로
										, ds_list2.getColumn(nRow, "FNAME")    // 로컬 파일명
									   );

				fn_destroyWait();
				if ( rMsg[0] == "E" ) {
					if ( length(trim(rMsg[1])) > 0 ) {
					  //  ExecShell(FileExec);
						gfn_showAlert("CW10025", rMsg[1]);
					} else {

						gfn_showAlert("CW10025", "파일 다운로드에 실패하였습니다.");
					}
				} else {	
					ExecShell(FileExec);		
				}	


			 }
	}

}







