function fn_OnLoadCompleted(obj) {
	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm(true);

	ed_psno.setFocus();
	gfn_showMsg("CI00007");

	tit_clearActionInfo();
	tit_addSearchActionInfo("wb02:WB0202005A_S1");
	tit_addSearchActionInfo("wb02:WB0202005A_S2");

	tit_callService(
        ""
        , ""
        , ""
        , "ds_dpt=ds_dpt ds_rank=ds_rank"
        , ""
        , "fn_afterInit"
        , true);
}
/********************************************************************************
* 기      능   : 공통코드가 조회된 후의 Callback 함수 
********************************************************************************/
function fn_completeInit(sCode, sDsObj, sCodeType) {
}
/********************************************************************************
* 기      능   : 부서조회 후 콜백
********************************************************************************/
function fn_afterInit()
{
	gfn_insertEmptyRow( ds_dpt, G_CODE_ALL );
	cb_dpt.value = "";
	gfn_insertEmptyRow( ds_rank, G_CODE_ALL );
	cb_rank.value = "";
}
/********************************************************************************
* 기      능   : form UnloadCompleted 
********************************************************************************/
function fn_OnUnloadCompleted(obj) {
	return gfn_isFormUnload(obj);
}
